INSERT INTO `articles` VALUES (14, '<h2 style=\"text-align: start;\">‰ª£Á†Å</h2><p style=\"text-align: start;\">‰∏ÄÔºöÁè≠Èïø</p><pre><code class=\"language-java\">import java.util.*;\n\npublic class Main {\n\n    static int[] fa;\n\n    public static void main(String[] args) {\n        Scanner sc = new Scanner(System.in);\n        int n = sc.nextInt();\n        int m = sc.nextInt();\n        fa = new int[n + 1];\n        for (int i = 1; i &lt;= n; i++) {\n            fa[i] = i;\n        }\n        for (int i = 0; i &lt; m; i++) {\n            int type = sc.nextInt();\n            if (type == 1) {\n                merge(sc.nextInt(), sc.nextInt());\n            } else if (type == 2) {\n                int a = sc.nextInt();\n                int root = find(a);\n                fa[a] = a;\n                fa[root] = a;\n            } else {\n                System.out.println(find(sc.nextInt()));\n            }\n        }\n    }\n\n    public static void merge(int x, int y) {\n        fa[find(x)] = find(y);\n    }\n\n    public static int find(int x) {\n        if (fa[x] != x) {\n            fa[x] = find(fa[x]);\n        }\n        return fa[x];\n    }\n}</code></pre><p style=\"text-align: start;\">‰∫åÔºöÁ¥¢ÈÅì</p><pre><code class=\"language-java\">import java.util.*;\n\npublic class Main {\n\n    public static void main(String[] args) {\n        Scanner sc = new Scanner(System.in);\n        int n = sc.nextInt();\n        int k = sc.nextInt() - 1;\n        int[] arr = new int[n];\n        for (int i = 0; i &lt; n; i++) {\n            arr[i] = sc.nextInt();\n        }\n        int[] dp = new int[n];\n        int max1 = 0;\n        if (arr[0] &lt; arr[k]) {\n            dp[0] = 1;\n            max1 = 1;\n        }\n        for (int i = 1; i &lt; k; i++) {\n            if (arr[i] &lt; arr[k]) {\n                dp[i] = 1;\n                for (int j = 0; j &lt; i; j++) {\n                    if (arr[i] &gt; arr[j]) {\n                        dp[i] = Math.max(dp[i], dp[j] + 1);\n                    }\n                }\n            }\n            max1 = Math.max(max1, dp[i]);\n        }\n        int max2 = 0;\n        if (k + 1 &lt; n && arr[k + 1] &lt; arr[k]) {\n            dp[k + 1] = 1;\n            max2 = 1;\n        }\n        for (int i = k + 2; i &lt; n; i++) {\n            if (arr[i] &lt; arr[k]) {\n                dp[i] = 1;\n                for (int j = k + 1; j &lt; i; j++) {\n                    if (arr[j] &gt; arr[i]) {\n                        dp[i] = Math.max(dp[i], dp[j] + 1);\n                    }\n                }\n            }\n            max2 = Math.max(max2, dp[i]);\n        }\n        System.out.println(max1 + max2 + 1);\n    }\n\n}</code></pre><p><br></p>', 1, '2023-08-01 23:07:34', 'Á¨¨ÂõõÊ¨°ÈöèÊú∫Ëµõ', '', NULL, '', '‰ª£Á†Å\n‰∏ÄÔºöÁè≠Èïø\nimport java.util.*;\r\n\r\npublic class Main {\r\n\r\n    static int[] fa;\r\n\r\n    public static void main(String[] args) {\r\n        Scanner sc = new Scanner(System.in);\r\n        int n = sc.nextInt();\r\n        int m = sc.nextInt();\r\n        fa = new int[n + 1];\r\n        for (int i = 1; i <= n; i++) {\r\n            fa[i] = i;\r\n        }\r\n        for (int i = 0; i < m; i++) {\r\n            int type = sc.nextInt();\r\n            if (type == 1) {\r\n                merge(sc.nextInt(), sc.nextInt());\r\n            } else if (type == 2) {\r\n                int a = sc.nextInt();\r\n                int root = find(a);\r\n                fa[a] = a;\r\n                fa[root] = a;\r\n            } else {\r\n                System.out.println(find(sc.nextInt()));\r\n            }\r\n        }\r\n    }\r\n\r\n    public static void merge(int x, int y) {\r\n        fa[find(x)] = find(y);\r\n    }\r\n\r\n    public static int find(int x) {\r\n        if (fa[x] != x) {\r\n            fa[x] = find(fa[x]);\r\n        }\r\n        return fa[x];\r\n    }\r\n}\n‰∫åÔºöÁ¥¢ÈÅì\nimport java.util.*;\r\n\r\npublic class Main {\r\n\r\n    public static void main(String[] args) {\r\n        Scanner sc = new Scanner(System.in);\r\n        int n = sc.nextInt();\r\n        int k = sc.nextInt() - 1;\r\n        int[] arr = new int[n];\r\n        for (int i = 0; i < n; i++) {\r\n            arr[i] = sc.nextInt();\r\n        }\r\n        int[] dp = new int[n];\r\n        int max1 = 0;\r\n        if (arr[0] < arr[k]) {\r\n            dp[0] = 1;\r\n            max1 = 1;\r\n        }\r\n        for (int i = 1; i < k; i++) {\r\n            if (arr[i] < arr[k]) {\r\n                dp[i] = 1;\r\n                for (int j = 0; j < i; j++) {\r\n                    if (arr[i] > arr[j]) {\r\n                        dp[i] = Math.max(dp[i], dp[j] + 1);\r\n                    }\r\n                }\r\n            }\r\n            max1 = Math.max(max1, dp[i]);\r\n        }\r\n        int max2 = 0;\r\n        if (k + 1 < n && arr[k + 1] < arr[k]) {\r\n            dp[k + 1] = 1;\r\n            max2 = 1;\r\n        }\r\n        for (int i = k + 2; i < n; i++) {\r\n            if (arr[i] < arr[k]) {\r\n                dp[i] = 1;\r\n                for (int j = k + 1; j < i; j++) {\r\n                    if (arr[j] > arr[i]) {\r\n                        dp[i] = Math.max(dp[i], dp[j] + 1);\r\n                    }\r\n                }\r\n            }\r\n            max2 = Math.max(max2, dp[i]);\r\n        }\r\n        System.out.println(max1 + max2 + 1);\r\n    }\r\n\r\n}\n', 'richtext', '0');
INSERT INTO `articles` VALUES (15, '<h1>ËÆ∞ÂΩï‰∏Ä‰∫õMySQLÂ∏∏Áî®Êü•ËØ¢Êìç‰Ωú</h1><h2><strong>Êü•ËØ¢ÈÉ®Èó®Ëñ™ËµÑÂâçNÊàñËÄÖÂâçÂá†ÁöÑÂëòÂ∑•</strong></h2><h3>1„ÄÅÁ™óÂè£ÂáΩÊï∞Ëß£Ê≥ï</h3><pre><code class=\"language-sql\">ËæìÂÖ•Ôºö\nEmployee Ë°®:\n+----+-------+--------+--------------+\n| id | name  | salary | departmentId |\n+----+-------+--------+--------------+\n| 1  | Joe   | 70000  | 1            |\n| 2  | Jim   | 90000  | 1            |\n| 3  | Henry | 80000  | 2            |\n| 4  | Sam   | 60000  | 2            |\n| 5  | Max   | 90000  | 1            |\n+----+-------+--------+--------------+\nDepartment Ë°®:\n+----+-------+\n| id | name  |\n+----+-------+\n| 1  | IT    |\n| 2  | Sales |\n+----+-------+\nËæìÂá∫Ôºö\n+------------+----------+--------+\n| Department | Employee | Salary |\n+------------+----------+--------+\n| IT         | Jim      | 90000  |\n| Sales      | Henry    | 80000  |\n| IT         | Max      | 90000  |\n+------------+----------+--------+\nËß£ÈáäÔºöMax Âíå Jim Âú® IT ÈÉ®Èó®ÁöÑÂ∑•ËµÑÈÉΩÊòØÊúÄÈ´òÁöÑÔºåHenry Âú®ÈîÄÂîÆÈÉ®ÁöÑÂ∑•ËµÑÊúÄÈ´ò„ÄÇ\n\nselect Department,Employee,Salary\nfrom (\n    select d.name Department,e.name Employee,e.Salary,\n    dense_rank() over(\n        partition by e.departmentId \n        order by e.salary desc\n    ) r_rank\n    from Employee e,Department d\n    where e.departmentId = d.id\n) t\nwhere t.r_rank = 1</code></pre><h3>2„ÄÅÊôÆÈÄöËß£Ê≥ï</h3><pre><code class=\"language-sql\">select d.name Department,e.name Employee,e.Salary\nfrom Employee e\nleft join Department d on e.departmentId = d.id\nwhere (departmentId,Salary) in (\n    select departmentId,max(salary) from Employee\n    group by departmentId\n)</code></pre><h2><strong>Âà†Èô§Ë°®‰∏≠ÈáçÂ§çÂÖÉÁ¥†</strong></h2><h3>1„ÄÅÁ™óÂè£ÂáΩÊï∞Ëß£Ê≥ï</h3><pre><code class=\"language-sql\">ËæìÂÖ•: \nPerson Ë°®:\n+----+------------------+\n| id | email            |\n+----+------------------+\n| 1  | john@example.com |\n| 2  | bob@example.com  |\n| 3  | john@example.com |\n+----+------------------+\nËæìÂá∫: \n+----+------------------+\n| id | email            |\n+----+------------------+\n| 1  | john@example.com |\n| 2  | bob@example.com  |\n+----+------------------+\nËß£Èáä: john@example.comÈáçÂ§ç‰∏§Ê¨°„ÄÇÊàë‰ª¨‰øùÁïôÊúÄÂ∞èÁöÑId = 1„ÄÇ\n\ndelete p from Person p\ninner join (\n    select id,email,row_number() over(\n        partition by email\n        order by id\n    ) r_number\n    from Person\n) dups on dups.id = p.id\nwhere dups.r_number &lt;&gt; 1</code></pre><h3>2„ÄÅËá™ËøûÊé•Âà†Èô§</h3><p><br></p><pre><code class=\"language-sql\">delete p1 from Person p1,Person p2\nwhere p1.email = p2.email and p1.id &gt; p2.id</code></pre><h2>LagÂáΩÊï∞</h2><p><strong>Êü•Êâæ‰∏ä‰∏ÄÂπ¥ÁöÑËÆ¢ÂçïÈ¢ù</strong></p><p><br></p><pre><code class=\"language-sql\">+------------------+------------+-------------+-----------------------+\n| productline      | order_year | order_value | prev_year_order_value |\n+------------------+------------+-------------+-----------------------+\n| Classic Cars     |       2013 |     1374832 |                  NULL |\n| Classic Cars     |       2014 |     1763137 |               1374832 |\n| Classic Cars     |       2015 |      715954 |               1763137 |\n| Motorcycles      |       2013 |      348909 |                  NULL |\n| Motorcycles      |       2014 |      527244 |                348909 |\n| Motorcycles      |       2015 |      245273 |                527244 |\n| Planes           |       2013 |      309784 |                  NULL |\n| Planes           |       2014 |      471971 |                309784 |\n| Planes           |       2015 |      172882 |                471971 |\n| Ships            |       2013 |      222182 |                  NULL |\n| Ships            |       2014 |      337326 |                222182 |\n| Ships            |       2015 |      104490 |                337326 |\n| Trains           |       2013 |       65822 |                  NULL |\n| Trains           |       2014 |       96286 |                 65822 |\n| Trains           |       2015 |       26425 |                 96286 |\n| Trucks and Buses |       2013 |      376657 |                  NULL |\n| Trucks and Buses |       2014 |      465390 |                376657 |\n| Trucks and Buses |       2015 |      182066 |                465390 |\n| Vintage Cars     |       2013 |      619161 |                  NULL |\n| Vintage Cars     |       2014 |      854552 |                619161 |\n| Vintage Cars     |       2015 |      323846 |                854552 |\n+------------------+------------+-------------+-----------------------+\n\nWITH productline_sales AS (\n    SELECT productline,\n           YEAR(orderDate) order_year,\n           ROUND(SUM(quantityOrdered * priceEach),0) order_value\n    FROM orders\n    INNER JOIN orderdetails USING (orderNumber)\n    INNER JOIN products USING (productCode)\n    GROUP BY productline, order_year\n)\nSELECT\n    productline, \n    order_year, \n    order_value,\n    LAG(order_value, 1) OVER (\n        PARTITION BY productLine\n        ORDER BY order_year\n    ) prev_year_order_value\nFROM \n    productline_sales; </code></pre><h2>Êó•ÊúüÊúâÂÖ≥ÁöÑÂáΩÊï∞</h2><ol><li><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-size: 16px;\">ËøîÂõû‰ªé0000Âπ¥Âà∞Áé∞Âú®ÁöÑÂ§©Êï∞</span></li></ol><pre><code class=\"language-sql\">select to_days(\"2023-02-07\")</code></pre><p><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-size: 16px;\">2.Â∞ÜÊó∂Èó¥/Êó•ÊúüÈó¥ÈöîÊ∑ªÂä†Âà∞Êó•Êúü</span></p><pre><code class=\"language-sql\">select adddate(\"2023-01-03\",INTERVAL 1 day) #2023-01-04</code></pre><p><br></p>', 1, '2023-08-01 23:18:42', 'Êï∞ÊçÆÂ∫ìËØ≠Âè•ËÆ∞ÂΩï', '', NULL, '', 'ËÆ∞ÂΩï‰∏Ä‰∫õMySQLÂ∏∏Áî®Êü•ËØ¢Êìç‰Ωú\nÊü•ËØ¢ÈÉ®Èó®Ëñ™ËµÑÂâçNÊàñËÄÖÂâçÂá†ÁöÑÂëòÂ∑•\n1„ÄÅÁ™óÂè£ÂáΩÊï∞Ëß£Ê≥ï\nËæìÂÖ•Ôºö\r\nEmployee Ë°®:\r\n+----+-------+--------+--------------+\r\n| id | name  | salary | departmentId |\r\n+----+-------+--------+--------------+\r\n| 1 