INSERT INTO `articles` VALUES (18, '<h1 style=\"text-align: start;\">ËÆ°ÁÆóxÁöÑnÊ¨°Êñπ</h1><p style=\"text-align: start;\">‰ΩøÁî®Ë¥πÈ©¨Â∞èÂÆöÁêÜÁöÑÊó∂ÂÄôÂèØ‰ª•Áî®Âà∞</p><p style=\"text-align: start;\">Ë¥πÈ©¨Â∞èÂÆöÁêÜÔºö(a / b) % mod = a * pow(b,mod - 2) % mod</p><pre style=\"text-align: left;\"><code>public double pow(double x,long n){ &nbsp; &nbsp;double res = 1; &nbsp; &nbsp;while(n &gt; 0){ &nbsp; &nbsp; &nbsp; &nbsp;if(n % 2 == 1) res *= x; &nbsp; &nbsp; &nbsp; &nbsp;x *= x; &nbsp; &nbsp; &nbsp; &nbsp;n /= 2; &nbsp;  } &nbsp; &nbsp;return res;}</code></pre><pre style=\"text-align: left;\"><code>public long pow(long x, int n) { &nbsp; &nbsp;long res = 1L; &nbsp; &nbsp;while (n &gt; 0) { &nbsp; &nbsp; &nbsp; &nbsp;if (n % 2 == 1) res = res * x % MOD; &nbsp; &nbsp; &nbsp; &nbsp;x = x * x % MOD; &nbsp; &nbsp; &nbsp; &nbsp;n /= 2; &nbsp;  } &nbsp; &nbsp;return res;}</code></pre><p style=\"text-align: start;\"><br></p><h1 style=\"text-align: start;\">Ê±Ç‰∏Ä‰∏™Êï∞ÁöÑÊâÄÊúâÂõ†Â≠ê</h1><pre style=\"text-align: left;\"><code>public static Set&lt;Integer&gt; f(int a) { &nbsp; &nbsp;Set&lt;Integer&gt; set = new HashSet&lt;&gt;(); &nbsp; &nbsp;for (int i = 1; i * i &lt;= a; i++) { &nbsp; &nbsp; &nbsp; &nbsp;if (a % i == 0) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;set.add(i); &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;if (i != a / i) &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;set.add(a / i); &nbsp; &nbsp; &nbsp;  } &nbsp;  } &nbsp; &nbsp;return set;}</code></pre><p style=\"text-align: start;\"><br></p><h1 style=\"text-align: start;\">ÊúÄÂ∞èÂÖ¨ÂÄçÊï∞ &amp; ÊúÄÂ§ßÂÖ¨Á∫¶Êï∞</h1><pre style=\"text-align: left;\"><code>//ÊúÄÂ∞èÂÖ¨ÂÖ¨ÂÄçÊï∞public int lcm(int a, int b) { &nbsp; &nbsp;return a * b / gcd(a, b);}‚Äã//ÊúÄÂ§ßÂÖ¨Á∫¶Êï∞public int gcd(int a, int b) { &nbsp; &nbsp;while (b &gt; 0) { &nbsp; &nbsp; &nbsp; &nbsp;int tmp = a % b; &nbsp; &nbsp; &nbsp; &nbsp;a = b; &nbsp; &nbsp; &nbsp; &nbsp;b = tmp; &nbsp;  } &nbsp; &nbsp;return a;}</code></pre><p style=\"text-align: start;\"><br></p><h1 style=\"text-align: start;\">Ë£¥ËúÄÂÆöÁêÜ</h1><p style=\"text-align: start;\">‰∏Ä‰∏™Êï∞ÁªÑarr</p><p style=\"text-align: start;\">ÈïøÂ∫¶‰∏∫nÔºåÂÅáËÆæÈöîÁùÄk‰∏™Áõ∏Á≠âÔºåarr[i] = arr[i + k]</p><p style=\"text-align: start;\">Êúân Âíå k Âë®Êúü ÈÇ£‰πàÂøÖÁÑ∂Êúâgcd(n,k)‰∏™Âë®ÊúüÁõ∏Á≠â,arr[i] = arr[i + gcd]</p><p style=\"text-align: start;\"><br></p><h1 style=\"text-align: start;\">ÂÜÖÂêëÂü∫ÁéØÊ†ëÊâæÊúÄÂ§ßÁéØ</h1><p style=\"text-align: start;\"><em>ÊåáÁöÑÊòØn‰∏™ËäÇÁÇπÁöÑÊúâÂêëÂõæÔºåÊØè‰∏™ÁªìÁÇπÊúÄÂ§öÂè™Êúâ‰∏ÄÊù°Âá∫Ëæπ</em></p><p style=\"text-align: start;\">ÂèØ‰ª•Âà©Áî®Êó∂Èó¥Êà≥ÊäÄÂ∑ßüëá</p><p style=\"text-align: start;\"><strong>Ê≥®ÊÑèÁÇπÔºö</strong>Âà§Êñ≠ÂΩìÂâçÁªèËøáÁöÑÁÇπÊòØ‰πãÂâçËÆøÈóÆËøáÁöÑÁÇπÔºåËøòÊòØÁéØ</p><ol><li style=\"text-align: start;\">ËÆ∞ÂΩïÂºÄÂßãÊó∂Èó¥startTime</li><li style=\"text-align: start;\">Â¶ÇÊûútime[cur] &gt;= startTime Âç≥ÊòØÁéØ</li><li style=\"text-align: start;\">Â¶ÇÊûútime[cur] &lt; startTime ËØ¥ÊòéÊòØ‰πãÂâçÂ∑≤ÁªèËµ∞ËøáÁöÑÁªìÁÇπÔºå‰∏çÊòØÁéØ„ÄÇ</li></ol><pre style=\"text-align: left;\"><code>/**    ÂäõÊâ£2360    https://leetcode.cn/problems/longest-cycle-in-a-graph/description/**/public int longestCycle(int[] edges) { &nbsp; &nbsp;int n = edges.length; &nbsp; &nbsp;int[] time = new int[n]; &nbsp; &nbsp;int locate = 1; &nbsp; &nbsp;int ans = -1; &nbsp; &nbsp;for (int i = 0; i &lt; n; i++) { &nbsp; &nbsp; &nbsp; &nbsp;if (time[i] == 0) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;int cur = i; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;int startTime = locate; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;while (cur != -1) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;if (time[cur] != 0) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;if (time[cur] &gt;= startTime) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;ans = Math.max(ans, locate - time[cur]); &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  } &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;break; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  } &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;time[cur] = locate++; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;cur = edges[cur]; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  } &nbsp; &nbsp; &nbsp;  } &nbsp;  } &nbsp; &nbsp;return ans;}</code></pre><p style=\"text-align: start;\"><br></p><h1 style=\"text-align: start;\">Á¥†Êï∞È¢ÑÂ§ÑÁêÜÔºàÊ¨ßÊãâÁ≠õÊ≥ïÔºâ</h1><pre style=\"text-align: left;\"><code>//È¢ÑÂ§ÑÁêÜ1e6ËåÉÂõ¥ÂÜÖÁöÑÂÖ®ÈÉ®Á¥†Êï∞//Ê†áËÆ∞staticÈò≤Ê≠¢ÊØèÊ¨°ÊâßË°åÈÉΩÂ§ÑÁêÜo(n)static Set&lt;Integer&gt; set = new HashSet&lt;&gt;() {{ &nbsp; &nbsp;boolean[] f = new boolean[(int) 1e6 + 1]; &nbsp; &nbsp;for (int i = 2; i &lt; f.length; i++) { &nbsp; &nbsp; &nbsp; &nbsp;if (!f[i]) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;add(i); &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;for (int j = i; j &lt; f.length; j += i) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;f[j] = true; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  } &nbsp; &nbsp; &nbsp;  } &nbsp;  }}};</code></pre><p style=\"text-align: start;\"><br></p><h1 style=\"text-align: start;\">Ë¥®Âõ†Êï∞ÂàÜËß£</h1><p style=\"text-align: start;\">‰æãÔºöËÆ°ÁÆóÂ§ö‰∏™Êï∞Â≠ó‰πòÁßØÁöÑÊüê‰Ωç0‰∏™Êï∞ -&gt; ÊâÄÊúâÊï∞Â≠óÁöÑÂõ†Â≠ê2ÂíåÂõ†Â≠ê5ÁöÑ‰∏™Êï∞ÔºåÂèñÊúÄÂ∞èÂÄº</p><p style=\"text-align: start;\">8 ---&gt; 2 * 2 * 2 * 1</p><pre style=\"text-align: left;\"><code>public String f(int x) { &nbsp; &nbsp;StringBuilder sb = new StringBuilder(); &nbsp; &nbsp;for (int i = 2; i * i &lt;= x; i++) { &nbsp; &nbsp; &nbsp; &nbsp;if (x % i == 0) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;while (x % i == 0) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;sb.append(i).append(\"*\"); &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;x /= i; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  } &nbsp; &nbsp; &nbsp;  } &nbsp;  } &nbsp; &nbsp;sb.append(x); &nbsp; &nbsp;return sb.toString();}</code></pre><p style=\"text-align: start;\"><br></p><h1 style=\"text-align: start;\">Á∫øÊÆµÊ†ëÊ®°Êùø</h1><pre style=\"text-align: left;\"><code>static class Tree { &nbsp; &nbsp;Tree left = null; &nbsp; &nbsp;Tree right = null; &nbsp; &nbsp;int start; &nbsp; &nbsp;int end; &nbsp; &nbsp;int count = 0;‚Äã &nbsp; &nbsp;public Tree(int start, int end) { &nbsp; &nbsp; &nbsp; &nbsp;this.start = start; &nbsp; &nbsp; &nbsp; &nbsp;this.end = end; &nbsp;  }}‚Äãpublic void update(Tree root, int l, int r, int idx) { &nbsp; &nbsp; &nbsp; &nbsp;if (l == r) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;root.count++; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;return; &nbsp; &nbsp; &nbsp;  } &nbsp; &nbsp; &nbsp; &nbsp;int m = (r - l) / 2 + l; &nbsp; &nbsp; &nbsp; &nbsp;if (root.left == null) root.left = new Tree(l, m); &nbsp; &nbsp; &nbsp; &nbsp;if (root.right == null) root.right = new Tree(m + 1,r); &nbsp; &nbsp; &nbsp; &nbsp;if (m &gt;= idx) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;update(root.left, l, m, idx); &nbsp; &nbsp; &nbsp;  } else { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;update(root.right, m + 1, r, idx); &nbsp; &nbsp; &nbsp;  } &nbsp; &nbsp; &nbsp; &nbsp;root.count = root.left.count + root.right.count; &nbsp;  }‚Äã &nbsp; &nbsp;public int query(Tree root, int l, int r, int L, int R) { &nbsp; &nbsp; &nbsp; &nbsp;if (root == null) return 0; &nbsp; &nbsp; &nbsp; &nbsp;if (l &gt;= L &amp;&amp; r &lt;= R) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;return root.count; &nbsp; &nbsp; &nbsp;  } &nbsp; &nbsp; &nbsp; &nbsp;int m = (r - l) / 2 + l; &nbsp; &nbsp; &nbsp; &nbsp;int ans = 0; &nbsp; &nbsp; &nbsp; &nbsp;if (m &gt;= L) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;ans += query(root.left, l, m, L, R); &nbsp; &nbsp; &nbsp;  } &nbsp; &nbsp; &nbsp; &nbsp;if (m &lt; R) { &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;ans += query(root.right, m + 1, r, L, R); &nbsp; &nbsp; &nbsp;  } &nbsp; &nbsp; &nbsp; &nbsp;return ans; &nbsp;  }</code></pre><p style=\"text-align: start;\"><br></p><p style=\"text-align: start;\"><strong>ÁêÜËß£lazyÊ†áËÆ∞Ê≥ïÔºå‰∏∫‰ªÄ‰πàÂú®Ê±ÇmaxÔºåÊ±ÇsumÊó∂ÂÜ