INSERT INTO `articles` VALUES (224, 'È¢òÁõÆÔºöÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢òÔºàDijkstraÁÆóÊ≥ïÔºâÁöÑ‰ºòÂåñÁâàÊú¨\n\nÈ¢òÁõÆÊèèËø∞Ôºö\nÁªôÂÆö‰∏Ä‰∏™Â∏¶ÊùÉÊúâÂêëÂõæÔºåÂÖ∂‰∏≠ËäÇÁÇπË°®Á§∫‰∏∫Êï¥Êï∞ÔºåËæπË°®Á§∫‰∏∫‰∏ÄÂØπÊï¥Êï∞ÔºàËµ∑ÁÇπÔºåÁªàÁÇπÔºåÊùÉÈáçÔºâÔºåÁõÆÊ†áÊòØÊâæÂà∞‰ªéËµ∑ÁÇπÂà∞ÊâÄÊúâÂÖ∂‰ªñËäÇÁÇπÁöÑÊúÄÁü≠Ë∑ØÂæÑ„ÄÇ\n\nË¶ÅÊ±ÇÔºö\n\n* ÁÆóÊ≥ïÁöÑÊó∂Èó¥Â§çÊùÇÂ∫¶‰∏∫O(V^2)ÔºåÂÖ∂‰∏≠VÊòØËäÇÁÇπÊï∞Èáè„ÄÇ\n* ÁÆóÊ≥ïÂ∫îËØ•‰ΩøÁî®‰ºòÂÖàÈòüÂàóÊï∞ÊçÆÁªìÊûÑÊù•‰ºòÂåñDijkstraÁÆóÊ≥ïÔºå‰ΩøÂæóÊó∂Èó¥Â§çÊùÇÂ∫¶Èôç‰ΩéÂà∞O(VlogV)„ÄÇ\n\nPython‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```python\nimport heapq\n\ndef dijkstra_priority_queue(graph, start):\n    # ÂàùÂßãÂåñË∑ùÁ¶ªÂ≠óÂÖ∏ÔºåÂ∞ÜÊâÄÊúâËäÇÁÇπÂàùÂßãÂåñ‰∏∫Êó†Á©∑Â§ß\n    distances = {node: float(\'infinity\') for node in graph}\n    # Â∞ÜËµ∑ÁÇπË∑ùÁ¶ªËÆæÁΩÆ‰∏∫0\n    distances[start] = 0\n    # ‰ºòÂÖàÈòüÂàóÔºåÂåÖÂê´ÂæÖËÆøÈóÆËäÇÁÇπÂíåÂØπÂ∫îÁöÑË∑ùÁ¶ª\n    priority_queue = [(0, start)]\n    while priority_queue:\n        # ÂèñÂá∫ÂΩìÂâçË∑ùÁ¶ªÊúÄÂ∞èÁöÑËäÇÁÇπ\n        current_distance, current_node = heapq.heappop(priority_queue)\n        # Â¶ÇÊûúÂΩìÂâçËäÇÁÇπÂ∑≤ÁªèË¢´ËÆøÈóÆËøáÔºåË∑≥Ëøá\n        if current_distance > distances[current_node]:\n            continue\n        # ÈÅçÂéÜÂΩìÂâçËäÇÁÇπÁöÑÈÇªÂ±ÖËäÇÁÇπ\n        for neighbor, weight in graph[current_node].items():\n            # ËÆ°ÁÆó‰ªéÂΩìÂâçËäÇÁÇπÂà∞ÈÇªÂ±ÖËäÇÁÇπÁöÑË∑ùÁ¶ªÔºåÂπ∂Âä†‰∏äÊùÉÂÄº\n            distance = current_distance + weight\n            # Â¶ÇÊûúÊñ∞ÁöÑË∑ùÁ¶ªÂ∞è‰∫éÂ∑≤Áü•ÁöÑË∑ùÁ¶ªÔºåÊõ¥Êñ∞Ë∑ùÁ¶ªÂ≠óÂÖ∏Âíå‰ºòÂÖàÈòüÂàó\n            if distance < distances[neighbor]:\n                distances[neighbor] = distance\n                heapq.heappush(priority_queue, (distance, neighbor))\n    return distances\n```\nJava‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```java\nimport java.util.*;\nimport java.util.stream.*;\nimport java.util.function.*;\nimport java.util.Arrays.*;;\nimport java.util.PriorityQueue.*;;\n\npublic class DijkstraPriorityQueue {\n    public Map<Integer, Integer> dijkstra(int[][] graph, int start) {\n        // ÂàùÂßãÂåñË∑ùÁ¶ªÂ≠óÂÖ∏ÔºåÂ∞ÜÊâÄÊúâËäÇÁÇπÂàùÂßãÂåñ‰∏∫Êó†Á©∑Â§ß\n        Map<Integer, Integer> distances = new HashMap<>();\n        for (int i : graph) { distances.put(i[0], Integer.MAX_VALUE); }\n        distances.put(start, 0); // Â∞ÜËµ∑ÁÇπË∑ùÁ¶ªËÆæÁΩÆ‰∏∫0\n        PriorityQueue<int[]> priorityQueue = new PriorityQueue<>((a, b) -> a[1] - b[1]); // ‰ºòÂÖàÈòüÂàóÔºåÂåÖÂê´ÂæÖËÆøÈóÆËäÇÁÇπÂíåÂØπÂ∫îÁöÑË∑ùÁ¶ª\n        priorityQueue.offer(new int[]{distances.get(start), start}); // Â∞ÜËµ∑ÁÇπÂä†ÂÖ•‰ºòÂÖàÈòüÂàó\n        while (!priorityQueue.isEmpty()) {\n            int[] current = priorityQueue.poll(); // ÂèñÂá∫ÂΩìÂâçË∑ùÁ¶ªÊúÄÂ∞èÁöÑËäÇÁÇπÂíåÂØπÂ∫îÁöÑË∑ùÁ¶ªÂÄºÂíåËäÇÁÇπÂÄºÁªÑÊàêÁöÑÊï∞ÁªÑ\n            int distance = current[0]; // ‰ªéËµ∑ÁÇπÂà∞ÂΩìÂâçËäÇÁÇπÁöÑË∑ùÁ¶ªÂÄº\n            int node = current[1]; // ‰ªéËµ∑ÁÇπÂà∞ÂΩìÂâçËäÇÁÇπÁöÑËäÇÁÇπÂÄºÔºàÂç≥ËäÇÁÇπÁöÑÁºñÂè∑Ôºâ\n            if (distance > distances.get(node)) { // Â¶ÇÊûúÂΩìÂâçËäÇÁÇπÂ∑≤ÁªèË¢´ËÆøÈóÆËøáÔºåË∑≥Ëøá\n                continue;\n            }\n            for (int[] neighbor : Arrays.stream(graph).filter(i -> i[0] == node).map(i -> i[1]).toArray(int[][]::new)) { // ÈÅçÂéÜÂΩìÂâçËäÇÁÇπÁöÑÈÇªÂ±ÖËäÇÁÇπ\n                int weight = neighbor[1]; // ÂΩìÂâçÈÇªÂ±ÖËäÇÁÇπÁöÑÊùÉÈáçÂÄºÔºàÂç≥Ëæπ‰∏äÁöÑÊùÉÂÄºÔºâ\n                int newDistance = distance + weight; // ‰ªéÂΩìÂâçËäÇÁÇπÂà∞ÈÇªÂ±ÖËäÇÁÇπÁöÑÊñ∞ÁöÑË∑ùÁ¶ªÂÄºÔºàÂç≥ÊùÉÂÄº‰πãÂíåÔºâ\n                if (newDistance < distances.get(neighbor)) { // Â¶ÇÊûúÊñ∞ÁöÑË∑ùÁ¶ªÂ∞è‰∫éÂ∑≤Áü•ÁöÑË∑ùÁ¶ªÔºåÊõ¥Êñ∞Ë∑ùÁ¶ªÂ≠óÂÖ∏Âíå‰ºòÂÖàÈòüÂàó‰∏≠ÁöÑËäÇÁÇπÂÄºÂíåÂØπÂ∫îÁöÑË∑ùÁ¶ªÂÄºÁªÑÊàêÁöÑÊï∞ÁªÑÔºàÂç≥Âä†ÂÖ•‰ºòÂÖàÈòüÂàóÔºâ\n                    distances.put(neighbor, newDistance); // Â∞ÜÊñ∞ÁöÑË∑ùÁ¶ªÂÄºÊîæÂÖ•Ë∑ùÁ¶ªÂ≠óÂÖ∏‰∏≠ÂØπÂ∫îÁöÑÈÇªÂ±ÖËäÇÁÇπÁöÑ‰ΩçÁΩÆ‰∏äÔºàÂ¶ÇÊûúÂ≠òÂú®ÁöÑËØùÔºâ\n                    priorityQueue.offer(new int[]{newDistance, neighbor[0]}); // Â∞ÜÊñ∞ÁöÑËäÇÁÇπÂä†ÂÖ•‰ºòÂÖàÈòüÂàó‰∏≠ÔºàÂ¶ÇÊûúÂ≠òÂú®ÁöÑËØùÔºâ\n                } else if (newDistance == distances.get(neighbor)) { // Â¶ÇÊûúÊñ∞ÁöÑË∑ùÁ¶ªÁ≠â‰∫éÂ∑≤Áü•ÁöÑË∑ùÁ¶ªÔºåÊõ¥Êñ∞‰ºòÂÖàÈòüÂàó‰∏≠ÁöÑËäÇÁÇπÂÄºÂíåÂØπÂ∫îÁöÑÊùÉÈáçÂÄºÁªÑÊàêÁöÑÊï∞ÁªÑÔºàÂç≥Âä†ÂÖ•‰ºòÂÖàÈòüÂàó‰∏≠Ôºâ\n                    priorityQueue.offer(new int[]{distances.get(neighbor), neighbor[0]}); // Â∞ÜËäÇÁÇπÂä†ÂÖ•‰ºòÂÖàÈòüÂàó‰∏≠ÔºàÂ¶ÇÊûúÂ≠òÂú®ÁöÑËØùÔºâÂπ∂‰∏î‰øùÊåÅÈòüÂàó‰∏≠ËäÇÁÇπÁöÑÈ°∫Â∫è‰∏çÂèòÔºàÂõ†‰∏∫Êàë‰ª¨ÊòØ', 21, '2024-03-06 00:09:14', 'È¢òÁõÆÔºöÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢òÔºàDijkstraÁÆóÊ≥ïÔºâÁöÑ‰ºòÂåñÁâàÊú¨\n\nÈ¢òÁõÆÊèèËø∞Ôºö\nÁªôÂÆö‰∏Ä‰∏™Â∏¶ÊùÉÊúâÂêëÂõæÔºåÂÖ∂‰∏≠ËäÇÁÇπË°®Á§∫‰∏∫Êï¥Êï∞ÔºåËæπË°®Á§∫‰∏∫‰∏ÄÂØπÊï¥Êï∞ÔºàËµ∑ÁÇπÔºåÁªàÁÇπÔºåÊùÉÈáçÔºâÔºåÁõÆÊ†áÊòØÊâæÂà∞‰ªéËµ∑ÁÇπÂà∞ÊâÄÊúâÂÖ∂‰ªñËäÇÁÇπÁöÑÊúÄÁü≠Ë∑ØÂæÑ', NULL, NULL, NULL, NULL, 'markdown', '0');
INSERT INTO `articles` VALUES (225, 'È¢òÁõÆÔºöÂü∫‰∫éÂä®ÊÄÅËßÑÂàíÁöÑÊúÄÈïøÂõûÊñáÂ≠ê‰∏≤ÈóÆÈ¢ò\n\nÁªôÂÆö‰∏Ä‰∏™Â≠óÁ¨¶‰∏≤sÂíå‰∏Ä‰∏™Êï¥Êï∞kÔºåÊâæÂá∫ÈïøÂ∫¶‰∏∫kÁöÑÊúÄÈïøÂõûÊñáÂ≠ê‰∏≤„ÄÇ\n\nÁÆóÊ≥ïË¶ÅÊ±ÇÔºö\n\n1. ‰ΩøÁî®Âä®ÊÄÅËßÑÂàíÁÆóÊ≥ïËß£ÂÜ≥ËØ•ÈóÆÈ¢ò„ÄÇ\n2. Â∞ΩÂèØËÉΩÂáèÂ∞ëÈáçÂ§çËÆ°ÁÆóÔºåÊèêÈ´òÁÆóÊ≥ïÊïàÁéá„ÄÇ\n\nPython‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```python\ndef longest_palindrome(s, k):\n    \"\"\"\n    :type s: str\n    :type k: int\n    :rtype: str\n    \"\"\"\n    # ÊûÑÂª∫Âä®ÊÄÅËßÑÂàíË°®Ê†ºÔºåËÆ∞ÂΩï‰ª•ÂΩìÂâçÂ≠óÁ¨¶ÁªìÂ∞æÁöÑÊúÄÈïøÂõûÊñáÂ≠ê‰∏≤ÈïøÂ∫¶\n    dp = [[0] * (k + 1) for _ in range(len(s) + 1)]\n    max_len = 0\n    start = 0\n    for i in range(1, len(s) + 1):\n        if s[i - 1] == s[i]:\n            # ÂΩìÂâçÂ≠óÁ¨¶ÂíåÂâç‰∏Ä‰∏™Â≠óÁ¨¶Áõ∏Á≠âÔºåËÄÉËôëÁªßÁª≠‰∏é‰πãÂâçÊûÑÂª∫ÁöÑÂ≠ê‰∏≤Áõ∏Ëøû\n            if i - start > 2:\n                dp[i][k] = max(dp[i - 2][k - 2], dp[i - 1][k - 1] + 2)\n            else:\n                dp[i][k] = dp[i - 1][k] + 1\n            max_len = max(max_len, dp[i][k])\n            start = i\n        else:\n            # ÂΩìÂâçÂ≠óÁ¨¶Âíå‰πãÂâçÊûÑÂª∫ÁöÑÂ≠ê‰∏≤‰∏çÁõ∏Á≠âÔºåÈáçÊñ∞ÂºÄÂßãÊûÑÂª∫Êñ∞ÁöÑÂ≠ê‰∏≤\n            start = i - 1\n    return s[start - k + 1:start + k] if max_len == k else None\n```\nJava‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```java\npublic class Main {\n    public static String longestPalindrome(String s, int k) {\n        // ÊûÑÂª∫Âä®ÊÄÅËßÑÂàíË°®Ê†ºÔºåËÆ∞ÂΩï‰ª•ÂΩìÂâçÂ≠óÁ¨¶ÁªìÂ∞æÁöÑÊúÄÈïøÂõûÊñáÂ≠ê‰∏≤ÈïøÂ∫¶\n        int n = s.length() + 1;\n        int[][] dp = new int[n][k + 1];\n        int maxLen = 0;\n        int start = 0;\n        for (int i = 1; i <= n; i++) {\n            char c = s.charAt(i - 1);\n            if (Character.toLowerCase(c) == Character.toLowerCase(s.charAt(i - 2))) {\n                // ÂΩìÂâçÂ≠óÁ¨¶ÂíåÂâç‰∏Ä‰∏™Â≠óÁ¨¶Áõ∏Á≠âÔºåËÄÉËôëÁªßÁª≠‰∏é‰πãÂâçÊûÑÂª∫ÁöÑÂ≠ê‰∏≤Áõ∏Ëøû\n                if (i - start > 2) {\n                    dp[i][k] = Math.max(dp[i - 2][k - 2], dp[i - 1][k - 1] + 2);\n                } else {\n                    dp[i][k] = dp[i - 1][k]; // dp[i-1][k]Ë°®Á§∫ÂΩìÂâçÂ≠óÁ¨¶ÁªìÂ∞æÁöÑÊúÄÈïøÂõûÊñáÂ≠ê‰∏≤ÈïøÂ∫¶Âä†‰∏ÄÁöÑÊÉÖÂÜµÔºåÂç≥Âä†‰∏äÂΩìÂâçÂ≠óÁ¨¶Êú¨Ë∫´ÈïøÂ∫¶ÁöÑÊÉÖÂÜµ\n                }\n                maxLen = Math.max(maxLen, dp[i][k]); // Êõ¥Êñ∞ÊúÄÈïøÂõûÊñáÂ≠ê‰∏≤ÈïøÂ∫¶\n                start = i; // ÈáçÁΩÆstart‰∏∫ÂΩìÂâç‰ΩçÁΩÆÔºåÈáçÊñ∞ÂºÄÂßãÊûÑÂª∫Êñ∞ÁöÑÂ≠ê‰∏≤\n            } else { // ÂΩìÂâçÂ≠óÁ¨¶Âíå‰πãÂâçÊûÑÂª∫ÁöÑÂ≠ê‰∏≤‰∏çÁõ∏Á≠âÔºåÈáçÊñ∞ÂºÄÂßãÊûÑÂª∫Êñ∞ÁöÑÂ≠ê‰∏≤\n                start = i - 1; // Â∞ÜstartÁßªÂà∞‰∏ã‰∏Ä‰∏™‰ΩçÁΩÆÔºåÈáçÊñ∞ÂºÄÂßãÊûÑÂª∫Êñ∞ÁöÑÂ≠ê‰∏≤\n            }\n        }\n        return s.substring(start - k + 1, start + k); // Â¶ÇÊûúÊúÄÈïøÂõûÊñáÂ≠ê‰∏≤ÈïøÂ∫¶Á≠â‰∫ékÔºåËøîÂõûÁªìÊûúÔºõÂê¶ÂàôËøîÂõûÁ©∫Â≠óÁ¨¶‰∏≤\"null\"ÊàñËÄÖnull„ÄÇÂ¶ÇÊûúÂ≠óÁ¨¶‰∏≤ÈïøÂ∫¶‰∏∫Â•áÊï∞ÔºåÂàôÈúÄË¶ÅÂà§Êñ≠start-k+1Âíåstart‰πãÈó¥ÊòØÂê¶ÊúâÂ≠óÁ¨¶‰∏≤ËøîÂõûÔºåËøôÈáåÈúÄË¶ÅÊâãÂä®Â§ÑÁêÜ‰∏Ä‰∏ã„ÄÇÂê¶Âàô‰ΩøÁî®substring()ÊñπÊ≥ïËøîÂõû‰ªéstart-k+1Âà∞start‰πãÈó¥ÁöÑÂ≠óÁ¨¶‰∏≤Âç≥ÂèØ„ÄÇËøîÂõûÁ©∫Â≠óÁ¨¶‰∏≤\"null\"ÊàñËÄÖnull‰πüÂèØ‰ª•Áõ¥Êé•ËøîÂõûÂ≠óÁ¨¶‰∏≤Êú¨Ë∫´Âç≥ÂèØ„ÄÇÂè¶Â§ñÈúÄË¶ÅÊ≥®ÊÑèÂú®Âä®ÊÄÅËßÑÂàíÁöÑËøáÁ®ã‰∏≠Ë¶ÅÊ≥®ÊÑèÂØπÊï∞ÁªÑÁöÑË∂äÁïåÈóÆÈ¢òËøõË°åÊ£ÄÊü•„ÄÇÂØπ‰∫éËæìÂÖ•ÂèÇÊï∞‰∏≠ÁöÑn„ÄÅkÁöÑÂ§ßÂ∞èÔºåËøòÈúÄË¶ÅËøõË°å‰∏ÄÂÆöÁöÑÊ£ÄÊü•„ÄÇÂê¶ÂàôÂèØËÉΩ‰ºöÂØºËá¥Êï∞ÁªÑË∂äÁïåÁ≠âÈóÆÈ¢ò„ÄÇÊØîÂ¶ÇËæìÂÖ•ÂèÇÊï∞Â∞è‰∫éÁ≠â‰∫éÈõ∂„ÄÅË¥üÊï∞Á≠âÊÉÖÂÜµÈúÄË¶ÅÊ£ÄÊü•ÂíåÂ§ÑÁêÜ„ÄÇËøòÊúâÂä®ÊÄÅËßÑÂàíÁöÑËøáÁ®ã‰∏≠ÂèØËÉΩ‰ºö‰ΩøÁî®Âà∞‰∏≠Èó¥ÁªìÊûúÂíåÊúÄÂ§ßÂÄº„ÄÅÊúÄÂ∞èÂÄºÁ≠âÊÉÖÂÜµÔºåËøòÈúÄË¶ÅËÄÉËôë‰∏≠Èó¥ÁªìÊûú„ÄÅÊúÄÂ§ßÂÄº„ÄÅÊúÄÂ∞èÂÄºÁöÑÊúâÊïàÊÄßÂíåÂêàÊ≥ïÊÄßÁ≠âÈóÆÈ¢ò„ÄÇËøô‰∫õÈÉΩÊòØÈúÄË¶ÅÊ≥®ÊÑèÁöÑÈóÆÈ¢ò„ÄÇÂΩìÁÑ∂ÂÖ∑‰ΩìÂÆûÁé∞ËøòÈúÄË¶ÅÊ†πÊçÆÂÖ∑‰ΩìÊÉÖÂÜµÊù•ËÄÉËôëÂíåË∞ÉÊï¥„ÄÇ‰ΩÜÊòØÊó†ËÆ∫Â¶Ç‰ΩïÈÉΩÈúÄË¶ÅËÄÉËôë‰∏Ä‰∫õÂü∫Êú¨ÁöÑÊ≥®ÊÑè‰∫ãÈ°πÂíåÈóÆÈ¢òÁöÑÂ§ÑÁêÜÊñπÂºè„ÄÇËøô‰∫õÊòØÂÆûÁé∞ÁÆóÊ≥ïÊó∂ÈúÄË¶ÅËÄÉËôëÁöÑÂü∫Êú¨ÈóÆÈ¢ò„ÄÇËøòÈúÄË¶ÅÊ≥®ÊÑèÊµãËØïÊï∞ÊçÆÁöÑÂêàÁêÜÊÄßÂíåÊúâÊïàÊÄßÔºå‰ª•ÂèäÂØπÁªìÊûúÁöÑËæìÂá∫ÊñπÂºè„ÄÅËæìÂá∫Ê†ºÂºèÁ≠âÈóÆÈ¢òËøõË°åÂêàÁêÜÁöÑÂ§ÑÁêÜÂíåÂÆâÊéí„ÄÇÊÄª‰πãÔºåÂÆûÁé∞ÁÆóÊ≥ïÊó∂ÈúÄË¶ÅËÄÉËôëÁöÑÈóÆÈ¢òËøòÊòØÊØîËæÉ', 21, '2024-03-06 00:09:28', 'È¢òÁõÆÔºöÂü∫‰∫éÂä®ÊÄÅËßÑÂàíÁöÑÊú