INSERT INTO `articles` VALUES (292, '```java\nimport java.util.ArrayDeque;\nimport java.util.Deque;\nimport java.util.Scanner;\n\npublic class Main {\n\n    static final int N = 2 * (int) 1e5 + 1;\n    static int[][] tr = new int[N][26];\n    static int[] e = new int[N];\n    static int[] fail = new int[N];\n    static int tot = 0;\n    static int[] cnt = new int[N];\n    static int[] val = new int[N];\n    static int[] deg = new int[N];\n    static int[] rev = new int[N];\n\n    static void insert(String s, int idx) {\n        int u = 0;\n        for (int i = 0; i < s.length(); i++) {\n            char c = s.charAt(i);\n            if (tr[u][c - \'a\'] == 0) {\n                tr[u][c - \'a\'] = ++tot;\n            }\n            u = tr[u][c - \'a\'];\n        }\n        if (e[u] == 0) {\n            e[u] = idx;\n        }\n        rev[idx] = e[u];\n    }\n\n    static void build() {\n        Deque<Integer> deque = new ArrayDeque<>();\n        for (int i = 0; i < 26; i++) {\n            if (tr[0][i] != 0) {\n                deque.offer(tr[0][i]);\n            }\n        }\n        while (!deque.isEmpty()) {\n            int u = deque.poll();\n            for (int i = 0; i < 26; i++) {\n                if (tr[u][i] != 0) {\n                    fail[tr[u][i]] = tr[fail[u]][i];\n                    deque.offer(tr[u][i]);\n                    deg[tr[fail[u]][i]] += 1;\n                } else {\n                    tr[u][i] = tr[fail[u]][i];\n                }\n            }\n        }\n    }\n\n    static void query(String t) {\n        int u = 0;\n        for (int i = 0; i < t.length(); i++) {\n            u = tr[u][t.charAt(i) - \'a\'];\n            val[u]++;\n        }\n    }\n\n    static void topo() {\n        ArrayDeque<Integer> deque = new ArrayDeque<>();\n        for (int i = 1; i <= tot; i++) {\n            if (deg[i] == 0) {\n                deque.offer(i);\n            }\n        }\n        while (!deque.isEmpty()) {\n            Integer fr = deque.poll();\n            cnt[e[fr]] = val[fr];\n            int u = fail[fr];\n            val[u] += val[fr];\n            if (--deg[u] == 0) {\n                deque.offer(u);\n            }\n        }\n    }\n\n    public static void main(String[] args) {\n        Scanner sc = new Scanner(System.in);\n        int n = sc.nextInt();\n        String[] s = new String[n + 1];\n        for (int i = 1; i <= n; i++) {\n            s[i] = sc.next();\n            insert(s[i], i);\n        }\n        build();\n        String t = sc.next();\n        query(t);\n        topo();\n        for (int i = 1; i <= n; i++) {\n            System.out.println(cnt[rev[i]]);\n        }\n    }\n}\n```\n', 1, '2024-07-09 10:34:28', 'ACËá™Âä®Êú∫', '', NULL, '', 'Áï•', 'markdown', '0');
INSERT INTO `articles` VALUES (293, '```\nimport java.io.BufferedReader;\nimport java.io.IOException;\nimport java.io.InputStreamReader;\nimport java.util.StringTokenizer;\n\npublic class Main2 {\n    static final double PI = Math.acos(-1.0);\n\n    static class Complex {\n        double x, y;\n\n        Complex(double _x, double _y) {\n            x = _x;\n            y = _y;\n        }\n\n        Complex() {\n            this(0.0, 0.0);\n        }\n\n        Complex subtract(Complex b) {\n            return new Complex(x - b.x, y - b.y);\n        }\n\n        Complex add(Complex b) {\n            return new Complex(x + b.x, y + b.y);\n        }\n\n        Complex multiply(Complex b) {\n            return new Complex(x * b.x - y * b.y, x * b.y + y * b.x);\n        }\n    }\n\n    static void change(Complex[] y, int len) {\n        int j = len / 2;\n\n        for (int i = 1; i < len - 1; i++) {\n            if (i < j) {\n                Complex temp = y[i];\n                y[i] = y[j];\n                y[j] = temp;\n            }\n\n            int k = len / 2;\n\n            while (j >= k) {\n                j -= k;\n                k /= 2;\n            }\n\n            if (j < k) {\n                j += k;\n            }\n        }\n    }\n\n    static void fft(Complex[] y, int len, int on) {\n        change(y, len);\n\n        for (int h = 2; h <= len; h <<= 1) {\n            Complex wn = new Complex(Math.cos(2 * PI / h), Math.sin(on * 2 * PI / h));\n\n            for (int j = 0; j < len; j += h) {\n                Complex w = new Complex(1, 0);\n\n                for (int k = j; k < j + h / 2; k++) {\n                    Complex u = y[k];\n                    Complex t = w.multiply(y[k + h / 2]);\n                    y[k] = u.add(t);\n                    y[k + h / 2] = u.subtract(t);\n                    w = w.multiply(wn);\n                }\n            }\n        }\n\n        if (on == -1) {\n            for (int i = 0; i < len; i++) {\n                y[i].x /= len;\n            }\n        }\n    }\n\n    public static void main(String[] args) {\n        AReader scanner = new AReader();\n        final int MAXN = 5000020;\n        Complex[] x1 = new Complex[MAXN];\n        Complex[] x2 = new Complex[MAXN];\n        int[] sum = new int[MAXN];\n        int t = scanner.nextInt();\n        for (int p = 0; p < t; p++) {\n            StringBuilder sb = new StringBuilder();\n            String str1 = scanner.next();\n            String str2 = scanner.next();\n            int len1 = str1.length();\n            int len2 = str2.length();\n            int len = 1;\n\n            while (len < len1 * 2 || len < len2 * 2) {\n                len <<= 1;\n            }\n\n            for (int i = 0; i < len1; i++) {\n                x1[i] = new Complex(str1.charAt(len1 - 1 - i) - \'0\', 0);\n            }\n\n            for (int i = len1; i < len; i++) {\n                x1[i] = new Complex(0, 0);\n            }\n\n            for (int i = 0; i < len2; i++) {\n                x2[i] = new Complex(str2.charAt(len2 - 1 - i) - \'0\', 0);\n            }\n\n            for (int i = len2; i < len; i++) {\n                x2[i] = new Complex(0, 0);\n            }\n\n            fft(x1, len, 1);\n            fft(x2, len, 1);\n\n            for (int i = 0; i < len; i++) {\n                x1[i] = x1[i].multiply(x2[i]);\n            }\n\n            fft(x1, len, -1);\n\n            for (int i = 0; i < len; i++) {\n                sum[i] = (int) (x1[i].x + 0.5);\n            }\n\n            for (int i = 0; i < len; i++) {\n                sum[i + 1] += sum[i] / 10;\n                sum[i] %= 10;\n            }\n\n            len = len1 + len2 - 1;\n\n            while (len > 0 && sum[len] == 0) {\n                len--;\n            }\n\n            for (int i = len; i >= 0; i--) {\n                sb.append(sum[i]);\n            }\n            System.out.println(sb);\n        }\n    }\n\n    static\n    class AReader {\n        private BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));\n        private StringTokenizer tokenizer = new StringTokenizer(\"\");\n\n        private String innerNextLine() {\n            try {\n                return reader.readLine();\n            } catch (IOException ex) {\n                return null;\n            }\n        }\n\n        public boolean hasNext() {\n            while (!tokenizer.hasMoreTokens()) {\n                String nextLine = innerNextLine();\n                if (nextLine == null) {\n                    return false;\n                }\n                tokenizer = new StringTokenizer(nextLine);\n            }\n            return true;\n        }\n\n        public String nextLine() {\n            tokenizer = new StringTokenizer(\"\");\n            return innerNextLine();\n        }\n\n        public String next() {\n            hasNext();\n            return tokenizer.nextToken();\n        }\n\n        public int nextInt() {\n            return Integer.parseInt(next());\n        }\n\n        public long nextLong() {\n            return Long.parseLong(next());\n        }\n\n        //        public BigInteger nextBigInt() {\n        //            return new BigInteger(next());\n        //        }\n        // Ëã•ÈúÄË¶ÅnextDoubleÁ≠âÊñπÊ≥ïÔºåËØ∑Ëá™Ë°åË∞ÉÁî®Double.parseDoubleÂåÖË£Ö\n    }\n}\n```\n', 1, '2024-07-24 17:09:11', 'ËÆ∞ÂΩï', '', NULL, '', 'Âø´ÈÄüÂÇÖÈáåÂè∂ÂèòÊç¢', 'markdown', '0');
INSERT INTO `articles` VALUES (294, '```\nimport java.io.*;\nimport java.util.Scanner;\n\npublic class Main {\n    static final int MAXN = (int) 1e5 + 1;\n    static int[] a = new int[MAXN];\n    static Tree[] t = new Tree[MAXN * 4];\n\n    static class Tree {\n        long sum, l, r, ans;\n\n        Tree() {\n            this.sum = this.l = this.r = this.ans = 0L;\n        }\n    }\n\n    static Tree pushup(Tree L, Tree R) {\n        Tree z = new Tree();\n        z.sum = L.sum + R.sum;\n        z.l = Math.max(L.l, L.sum + R.l);\n        z.r = Math.max(R.r, R.sum + L.r);\n        z.ans = Math.max(Math.max(L.ans, R.ans), L.r + R.l);\n        return z;\n    }\n\n    static void build(int l, int r, int num) {\n        if (l == r) {\n            t[num].sum = a[l];\n            t[num].l = a[l];\n            t[num].r = a[l];\n            t[num].ans = a[l];\n            return;\n        }\n        int mid = (l + r) >> 1;\n        build(l, mid, num << 1);\n        build(mid + 1, r, num << 1 | 1);\n        t[num] = pushup(t[num << 1], t[num << 1 | 1]);\n    }\n\n    static void change(int l, int r, int num, int x, int y) {\n        if (l == r) {\n            t[num].sum = y;\n            t[num].l = y;\n            t[num].r = y;\n            t[num].ans = y;\n            return;\n        }\n        int mid = (l + r) >> 1;\n        if (x <= mid) {\n            change(l, mid, num << 1, x, y);\n        } else {\n            change(mid + 1, r, num << 1 | 1, x, y);\n        }\n        t[num] = pushup(t[num << 1], t[num << 1 | 1]);\n    }\n\n    static Tree query(int x, int y, int num, int l, int r) {\n        if (x <= l && r <= y) return t[num];\n        int mid = (l + r) >> 1;\n        if (y <= mid) return query(x, y, num << 1, l, mid);\n        if (mid < x) return query(x, y, num << 1 | 1, mid + 1, r);\n        return pushup(query(x, mid, num << 1, l, mid), query(mid + 1, y, num << 1 | 1, mid + 1, r));\n    }\n\n    public static void main(String[] args) throws IOException {\n        Scanner sc = new Scanner(System.in);\n\n        int n = sc.nextInt();\n        int T = sc.nextInt();\n        for (int i = 1; i <= n; i++) a[i] = 1;\n        for (int i = 0; i < t.length; i++) t[i] = new Tree();\n        build(1, n, 1);\n\n        while (T-- > 0) {\n            int f = sc.nextInt();\n            if (f == 1) {\n                int x = sc.nextInt();\n                if (a[x] == Integer.MIN_VALUE) {\n                    a[x] = 1;\n                } else {\n                    a[x] = Integer.MIN_VALUE;\n                }\n                int y = a[x];\n                change(1, n, 1, x, y);\n            } else {\n                int x = sc.nextInt();\n                int y = sc.nextInt();\n                System.out.println(Math.max(0, query(x, y, 1, 1, n).ans));\n            }\n        }\n    }\n}\n```\n', 1, '2024-07-31 15:48:52', 'Á∫øÊÆµÊ†ëÊúÄÂ§ßÂ≠êÊÆµÂíå', 'https://guetzjb.cn/2024-7-31/bg3.webp', NULL, '', 'Á∫øÊÆµÊ†ëÊúÄÂ§ßÂ≠êÊÆµÂíåËÆ∞ÂΩï‰∏Ä‰∏ã', 'markdown', '0');
