INSERT INTO `xz_dialogue` VALUES (227, '#include \"bits/stdc++.h\"\n\nusing namespace std;\nusing i64 = long long;\n\n#define range(a) begin(a), end(a)\n\ntemplate <class T>\nT max(const vector<T> &a) {\n    return *max_element(a.begin(), a.end());\n}      \n\nint main() {\n    ios::sync_with_stdio(false);\n    cin.tie(nullptr);\n  \n    int n;\n    cin >> n;\n    vector<int> a(n);\n    vector<int> f(3);\n    for (int i = 0; i < n; i++) {\n        string s;\n\n        cin >> s >> a[i];\n        if (s[0] == \'i\') {\n            f[0]++;\n        } else if (s[0] == \'t\') {\n            f[1]++;\n        } else {\n            f[2]++;\n        }\n    }\n\n    int k = min(n / 2, n - max(f));\n    i64 ans = 0;\n    sort(range(a), greater());\n    for (int i = 0; i < n; i++) {\n        if (i < k) {\n            ans += a[i];\n        }\n        ans += a[i];\n    }\n    cout << ans << \'\\n\';\n    \n    return 0;\n}\nÂ∏ÆÊàëÁøªËØëÊàêjava‰ª£Á†Å\n', NULL, 'user', '2023-09-25 20:40:15');
INSERT INTO `xz_dialogue` VALUES (228, '‰ª•‰∏ãÊòØÂØπÂ∫îÁöÑJava‰ª£Á†ÅÔºö\n\n\n```java\nimport java.util.*;\n\npublic class Main {\n    public static void main(String[] args) {\n        Scanner sc = new Scanner(System.in);\n        int n = sc.nextInt();\n        int[] a = new int[n];\n        int[] f = new int[3];\n        for (int i = 0; i < n; i++) {\n            String s = sc.next();\n            int x = sc.nextInt();\n            if (s.charAt(0) == \'i\') {\n                f[0]++;\n            } else if (s.charAt(0) == \'t\') {\n                f[1]++;\n            } else {\n                f[2]++;\n            }\n            a[i] = x;\n        }\n        int k = Math.min(n / 2, n - Math.max(f[0], Math.max(f[1], f[2])));\n        long ans = 0;\n        Arrays.sort(a);\n        for (int i = 0; i < n; i++) {\n            if (i < k) {\n                ans += a[i];\n            }\n            ans += a[n - 1 - i];\n        }\n        System.out.println(ans);\n    }\n}\n```', NULL, 'assistant', '2023-09-25 20:40:15');
