INSERT INTO `xz_dialogue` VALUES (230, '‰ª•‰∏ãÊòØ‰ΩøÁî®ÂéüÁîüJavaScriptÂíåVueÊù•ÂÆûÁé∞ÁÇπÂáªÊåâÈíÆÊ∑ªÂä†‰∏ÄÊù°Êï∞ÊçÆÔºåÂπ∂Â∞ÜÊñ∞Êï∞ÊçÆÊ∏≤ÊüìÂú®È°µÈù¢‰∏äÊèê‰∫§HTMLÊñá‰ª∂ÁöÑÁ§∫‰æãÔºö\n\n**ÂéüÁîüJavaScript**\n\n\n```html\n<!DOCTYPE html>\n<html>\n<head>\n  <title>Add Data with Native JavaScript</title>\n</head>\n<body>\n  <button onclick=\"addData()\">Add Data</button>\n  <div id=\"data-container\"></div>\n\n  <script>\n    function addData() {\n      // ÂàõÂª∫Êñ∞ÁöÑÊï∞ÊçÆÈ°π\n      const newItem = { name: \'New Item\', value: Math.random() };\n\n      // Ëé∑ÂèñÊï∞ÊçÆÂÆπÂô®\n      const container = document.getElementById(\'data-container\');\n\n      // ÂàõÂª∫Êñ∞ÁöÑÊï∞ÊçÆÈ°πÊòæÁ§∫ÂÖÉÁ¥†\n      const itemElement = document.createElement(\'div\');\n      itemElement.textContent = `Name: ${newItem.name}, Value: ${newItem.value}`;\n\n      // Â∞ÜÊñ∞ÁöÑÊï∞ÊçÆÈ°πÊòæÁ§∫ÂÖÉÁ¥†Ê∑ªÂä†Âà∞Êï∞ÊçÆÂÆπÂô®‰∏≠\n      container.appendChild(itemElement);\n    }\n  </script>\n</body>\n</html>\n```\nÂú®Ëøô‰∏™Á§∫‰æã‰∏≠ÔºåÊàë‰ª¨ÂàõÂª∫‰∫Ü‰∏Ä‰∏™ÊåâÈíÆÔºåÂΩìÁÇπÂáªËØ•ÊåâÈíÆÊó∂ÔºåÂ∞±‰ºöË∞ÉÁî® `addData()` ÂáΩÊï∞„ÄÇËøô‰∏™ÂáΩÊï∞‰ºöÂàõÂª∫‰∏Ä‰∏™Êñ∞ÁöÑÊï∞ÊçÆÈ°πÔºåÂπ∂Â∞ÜÂÖ∂ÊòæÁ§∫Âú®È°µÈù¢‰∏äÁöÑ‰∏Ä‰∏™ `div` ÂÖÉÁ¥†‰∏≠„ÄÇ\n\n**Vue**\n\n\n```html\n<template>\n  <div>\n    <button @click=\"addData\">Add Data</button>\n    <div v-for=\"item in items\" :key=\"item.id\">\n      Name: {{ item.name }}, Value: {{ item.value }}\n    </div>\n  </div>\n</template>\n\n<script>\nexport default {\n  data() {\n    return {\n      items: []\n    };\n  },\n  methods: {\n    addData() {\n      // ÂàõÂª∫Êñ∞ÁöÑÊï∞ÊçÆÈ°π\n      const newItem = { id: Date.now(), name: \'New Item\', value: Math.random() };\n\n      // Â∞ÜÊñ∞ÁöÑÊï∞ÊçÆÈ°πÊ∑ªÂä†Âà∞Êï∞ÊçÆÊï∞ÁªÑ‰∏≠\n      this.items.push(newItem);\n    }\n  }\n};\n</script>\n```\nÂú®Ëøô‰∏™VueÁªÑ‰ª∂‰∏≠ÔºåÊàë‰ª¨ÂàõÂª∫‰∫Ü‰∏Ä‰∏™ÊåâÈíÆÔºåÂΩìÁÇπÂáªËØ•ÊåâÈíÆÊó∂ÔºåÂ∞±‰ºöË∞ÉÁî® `addData` ÊñπÊ≥ï„ÄÇËøô‰∏™ÊñπÊ≥ï‰ºöÂàõÂª∫‰∏Ä‰∏™Êñ∞ÁöÑÊï∞ÊçÆÈ°πÔºåÂπ∂Â∞ÜÂÖ∂Ê∑ªÂä†Âà∞ÁªÑ‰ª∂ÁöÑÊï∞ÊçÆÊï∞ÁªÑ `items` ‰∏≠„ÄÇËøô‰∏™Êï∞ÁªÑ‰∏≠ÁöÑÊØè‰∏™È°πÁõÆÈÉΩ‰ºöÂú®È°µÈù¢‰∏äÊòæÁ§∫‰∏∫‰∏Ä‰∏™Êñ∞ÁöÑ `div` ÂÖÉÁ¥†„ÄÇÊ≥®ÊÑèËøôÈáå‰ΩøÁî®‰∫ÜVueÁöÑ `v-for` Êåá‰ª§Êù•Âæ™ÁéØÊ∏≤Êüì `items` Êï∞ÁªÑ‰∏≠ÁöÑÊØè‰∏™È°πÁõÆ„ÄÇ', 54, 'assistant', '2023-09-27 19:54:27');
