INSERT INTO `xz_dialogue` VALUES (310, '#include <bits/stdc++.h>\n#define all(s) s.begin(), s.end()\nusing namespace std;\nusing ll = long long;\nusing ull = unsigned long long;\n\nconst int _N = 1e5 + 5;\n\nint T;\n\nvoid solve() {\n	int n; cin >> n;\n	vector<ll> a(n); ll sum = 0;\n	for (int i = 0; i < n; i++) cin >> a[i], sum += a[i];\n	if (sum % n) return cout << \"No\" << \'\\n\', void();\n	sum /= n;\n	vector<int> bit(31, 0);\n	auto lowbit = [](int x) {\n	    return x & (-x);\n	};\n	for (int i = 0; i < n; i++) {\n		if (a[i] == sum) continue;\n		int d = abs(a[i] - sum);\n		int p = lowbit(d);\n		int e = d + p;\n		if (__builtin_popcount(e) == 1) {\n		    if (a[i] > sum) bit[__lg(e)]++, bit[__lg(p)]--;\n		    else bit[__lg(e)]--, bit[__lg(p)]++;\n		} else {\n		    cout << \"No\" << \'\\n\';\n		    return;\n		}\n	}\n	for (int i = 0; i < 31; i++) {\n		if (bit[i]) {\n			cout << \"No\" << \'\\n\';\n			return;\n		}\n	}\n	cout << \"Yes\" << \'\\n\';\n	return;\n}\n\nint main() {\n	ios::sync_with_stdio(false), cin.tie(0), cout.tie(0);\n	cin >> T;\n	while (T--) {\n		solve();\n	}\n}\nÁøªËØëÊàêjava', 65, 'user', '2023-10-08 21:29:42');
INSERT INTO `xz_dialogue` VALUES (311, '‰ª•‰∏ãÊòØËøôÊÆµC++‰ª£Á†ÅÁöÑJavaÁâàÊú¨Ôºö\n\n\n```java\nimport java.util.*;\n\npublic class Main {\n    public static void main(String[] args) {\n        ios.SyncWithStdio(false);\n        cin.tie(0);\n        cout.tie(0);\n        cin.get(); // Remove this line to solve the problem with Java\n\n        Scanner sc = new Scanner(System.in);\n        int T = sc.nextInt();\n        while (T-- > 0) {\n            solve();\n        }\n    }\n\n    static void solve() {\n        int n; \n        cin >> n;\n        Long[] a = new Long[n];\n        long sum = 0;\n        for (int i = 0; i < n; i++) {\n            cin >> a[i];\n            sum += a[i];\n        }\n        if (sum % n != 0) {\n            cout << \"No\" << \'\\n\';\n            return;\n        }\n        sum /= n;\n        int[] bit = new int[31];\n\n        // Define a helper function to get the lowermost 1 bit of an integer\n        int lowbit = i -> i & (-i);\n\n        for (Long ai : a) {\n            if (ai == sum) continue;\n            long d = Math.abs(ai - sum);\n            int p = lowbit(d);\n            long e = d + p;\n            if ((e & ~p) == 0) { // Checking if e has only one bit other than p as 1\n                if (ai > sum) bit[Integer.getInteger(e)]++, bit[Integer.getInteger(p)]--;\n                else bit[Integer.getInteger(e)]--, bit[Integer.getInteger(p)]++;\n            } else {\n                cout << \"No\" << \'\\n\';\n                return;\n            }\n        }\n        for (int i = 0; i < 31; i++) {\n            if (bit[i] != 0) {\n                cout << \"No\" << \'\\n\';\n                return;\n            }\n        }\n        cout << \"Yes\" << \'\\n\';\n    }\n}\n```\nÊ≥®ÊÑèÔºåÁî±‰∫éJavaÂíåC++ÁöÑ‰∫íÊìç‰ΩúÊÄßÈóÆÈ¢òÔºå‰Ω†ÂèØËÉΩÈúÄË¶ÅÂØºÂÖ•‰∏Ä‰∫õÂú®C++‰∏≠ÂèØËÉΩ‰∏çÈúÄË¶ÅÁöÑÂ∫ìÊàñËÄÖÂåÖ„ÄÇÂè¶Â§ñÔºåJavaÁâàÊú¨Ê≤°Êúâ‰ΩøÁî®`ll`Âíå`ull`Á±ªÂûãÔºåËÄåÊòØ‰ΩøÁî®`long`‰ª£Êõø„ÄÇÂú®Java‰∏≠ÔºåÊâÄÊúâËæìÂÖ•ÂíåËæìÂá∫‰ªçÁÑ∂ÈÄöËøáÊ†áÂáÜËæìÂÖ•ÂíåÊ†áÂáÜËæìÂá∫ÊµÅËøõË°å„ÄÇÂú®Java‰∏≠Ê≤°Êúâ‰ΩøÁî®`using namespace std;`ËØ≠Âè•ÔºåËøôÊòØÂõ†‰∏∫Java‰∏çÊîØÊåÅËøôÁßçËØ≠Ê≥ï„ÄÇÂú®Java‰∏≠ÔºåÊâÄÊúâÁöÑÊ†áÂáÜÂ∫ìÁ±ªÂûãÂíåÂáΩÊï∞ÈÉΩÂú®`java.util`ÂåÖ‰∏≠ÔºåÂõ†Ê≠§Êàë‰ª¨ÈúÄË¶ÅÂú®Êñá‰ª∂È°∂ÈÉ®ÂØºÂÖ•ÂÆÉ„ÄÇÊúÄÂêéÔºåÁî±‰∫éJava‰∏≠ÁöÑÁ±ªÂûãËΩ¨Êç¢‰∏çÂêåÔºåÊàë‰ª¨‰ΩøÁî®`Integer.getInteger()`Êù•Ëé∑ÂèñÊï¥Êï∞ÁöÑ‰∫åËøõÂà∂Ë°®Á§∫ÁöÑ‰ΩçÊï∞„ÄÇ', 65, 'assistant', '2023-10-08 21:29:42');
