INSERT INTO `xz_dialogue` VALUES (313, '‰ª•‰∏ãÊòØÂ∞ÜËøôÊÆµC++‰ª£Á†ÅÁøªËØëÊàêJava‰ª£Á†ÅÁöÑÂ∞ùËØïÔºö\n\n\n```java\nimport java.util.*;\n\npublic class Main {\n    static final int _N = 1e5 + 5;\n    \n    static void solve() {\n        int n; Scanner in = new Scanner(System.in);\n        in.nextInt(); // read T\n        \n        int sum = 0;\n        for (int i = 0; i < n; i++) {\n            long a = in.nextLong();\n            sum += a;\n        }\n        \n        if (sum % n != 0) {\n            System.out.println(\"No\");\n            return;\n        }\n        \n        sum /= n;\n        int[] bit = new int[31];\n        \n        long[] a = new long[n];\n        for (int i = 0; i < n; i++) {\n            a[i] = in.nextLong();\n        }\n        \n        Arrays.sort(a);\n        for (int i = 0; i < n; i++) {\n            if (a[i] == sum) continue;\n            long d = Math.abs(a[i] - sum);\n            int p = (int)d & (-(int)d);\n            long e = d + p;\n            if (((int)e & (e >> 31)) == 1) { // check if e is odd\n                if (a[i] > sum) {\n                    bit[Math.log2(e)]++;\n                    bit[Math.log2(p)]--;\n                } else {\n                    bit[Math.log2(e)]--;\n                    bit[Math.log2(p)]++;\n                }\n            } else {\n                System.out.println(\"No\");\n                return;\n            }\n        }\n        \n        for (int i = 0; i < 31; i++) {\n            if (bit[i] != 0) {\n                System.out.println(\"No\");\n                return;\n            }\n        }\n        System.out.println(\"Yes\");\n    }\n    \n    public static void main(String[] args) {\n        Scanner in = new Scanner(System.in);\n        int T = in.nextInt();\n        while (T-- > 0) {\n            solve();\n        }\n    }\n}\n```\nÊ≥®ÊÑèÔºåJava‰∏çÊîØÊåÅ`<bits/stdc++.h>`Â∫ìÔºåÂõ†Ê≠§ÊàëÂà†Èô§‰∫Ü‰∏éÊ≠§Â∫ìÁõ∏ÂÖ≥ÁöÑ‰ª£Á†Å„ÄÇÊ≠§Â§ñÔºåJava‰∏çÊîØÊåÅ‰ΩøÁî®Êú™ÂàùÂßãÂåñÁöÑÂèòÈáèÔºåÂõ†Ê≠§ÊàëÂàùÂ