INSERT INTO `xz_dialogue` VALUES (385, 'DijkstraÁÆóÊ≥ïÊòØ‰∏ÄÁßçÁî®‰∫éÊü•ÊâæÂõæ‰∏≠ÊúÄÁü≠Ë∑ØÂæÑÁöÑÁÆóÊ≥ï„ÄÇ‰∏∫‰∫ÜÂÆûÁé∞Â†Ü‰ºòÂåñÁâàÊú¨ÁöÑDijkstraÁÆóÊ≥ïÔºåÊàë‰ª¨ÈúÄË¶Å‰ΩøÁî®Java‰∏≠ÁöÑ‰ºòÂÖàÈòüÂàóÂíåÂ†ÜÊï∞ÊçÆÁªìÊûÑ„ÄÇ‰∏ãÈù¢ÊòØ‰∏Ä‰∏™ÁÆÄÂçïÁöÑÂÆûÁé∞Ôºö\n\n\n```java\nimport java.util.*;\n\npublic class DijkstraHeapOptimized {\n    private static final int V = numVertices(graph);\n    private PriorityQueue<Node> pq;\n\n    public DijkstraHeapOptimized(Graph g) {\n        pq = new PriorityQueue<>((a, b) -> (a.cost - b.cost));\n        for (int v = 0; v < V; ++v) {\n            pq.offer(new Node(v, Double.POSITIVE_INFINITY));\n        }\n        for (int u : g.adjList[0]) {\n            update(u);\n        }\n    }\n\n    private void update(int node) {\n        Node u = pq.poll();\n        int v = u.node;\n        double oldDist = u.cost;\n        while (v != node) {\n            pq.offer(new Node(v, oldDist + graph.dist[v][node]));\n            v = getAdjNode(v);\n            oldDist += graph.dist[v][node];\n        }\n    }\n\n    public void dijkstra(int start) {\n        pq.offer(new Node(start, 0));\n        while (!pq.isEmpty()) {\n            Node u = pq.poll();\n            System.out.println(\"Vertex \" + u.node + \" with cost \" + u.cost);\n            for (Node v : pq) {\n                if (v.cost < graph.dist[u.node][v.node]) {\n                    graph.dist[u.node][v.node] = v.cost;\n                    graph.prev[u.node][v.node] = v.prev;\n                }\n            }\n        }\n    }\n}\n```\nÂú®Ëøô‰∏™‰ª£Á†Å‰∏≠ÔºåÊàë‰ª¨‰ΩøÁî®‰∫Ü‰ºòÂÖàÈòüÂàóÔºàJava‰∏≠ÁöÑ`PriorityQueue`ÔºâÊù•‰ºòÂåñDijkstraÁÆóÊ≥ï„ÄÇ‰ºòÂÖàÈòüÂàóÁöÑÁâπÊÄßÊòØÊØèÊ¨°‰ªéÈòüÂàó‰∏≠ÂèñÂá∫ÁöÑÂÖÉÁ¥†ÊòØÊúÄÂ∞èÁöÑÔºåÂõ†Ê≠§Êàë‰ª¨ÂèØ‰ª•Âà©Áî®Ëøô‰∏™ÁâπÊÄßÊù•ÊâæÂá∫ÊúÄÁü≠Ë∑ØÂæÑ„ÄÇËøô‰∏™ÁÆóÊ≥ïÁöÑÊ†∏ÂøÉÂú®‰∫éÁª¥Êä§‰∏Ä‰∏™`Node`ÂØπË±°ÁöÑÂ†ÜÔºåÊØèÊ¨°ÊâæÂà∞‰∏Ä‰∏™Êñ∞ÁöÑÊúÄÁü≠Ë∑ØÂæÑÂêéÔºåÂ∞ÜÂÖ∂Âä†ÂÖ•Âà∞Â†Ü‰∏≠ÔºåÂπ∂Âú®‰πãÂêéÂèñÂá∫ËØ•Ë∑ØÂæÑËøõË°åÊõ¥Êñ∞„ÄÇÂÖ∑‰ΩìÂÆûÁé∞Â¶Ç‰∏ãÔºö\n\n1. Âú®`DijkstraHeapOptimized`Á±ªÁöÑÊûÑÈ