INSERT INTO `xz_dialogue` VALUES (469, '‰ª•‰∏ãÊòØ‰ΩøÁî®JavaÂÆûÁé∞Ê†ëÁöÑ‰∏≠Â∫èÈÅçÂéÜÁöÑÁ§∫‰æã‰ª£Á†ÅÔºö\n\n\n```java\nclass TreeNode {\n    int val;\n    TreeNode left;\n    TreeNode right;\n\n    TreeNode(int val) {\n        this.val = val;\n        this.left = null;\n        this.right = null;\n    }\n}\n\npublic class TreeTraversal {\n    public void inorderTraversal(TreeNode root) {\n        if (root == null) {\n            return;\n        }\n        inorderTraversal(root.left);\n        System.out.println(root.val);\n        inorderTraversal(root.right);\n    }\n\n    public static void main(String[] args) {\n        // ÂàõÂª∫‰∏Ä‰∏™ÁÆÄÂçïÁöÑÊ†ëÁªìÊûÑ\n        TreeNode root = new TreeNode(1);\n        root.left = new TreeNode(2);\n        root.right = new TreeNode(3);\n        root.left.left = new TreeNode(4);\n        root.left.right = new TreeNode(5);\n\n        // ÊâßË°å‰∏≠Â∫èÈÅçÂéÜÂπ∂ËæìÂá∫ÁªìÊûú\n        TreeTraversal tt = new TreeTraversal();\n        tt.inorderTraversal(root);\n    }\n}\n```\nÂú®Ëøô‰∏™Á§∫‰æã‰∏≠ÔºåÊàë‰ª¨È¶ñÂÖàÂÆö‰πâ‰∫Ü‰∏Ä‰∏™ÁÆÄÂçïÁöÑÊ†ëËäÇÁÇπÁ±ª `TreeNode`ÔºåÂÆÉÂåÖÂê´‰∏Ä‰∏™Êï¥Êï∞ÂÄº `val` ÂíåÂ∑¶Âè≥Â≠êËäÇÁÇπ `left` Âíå `right`„ÄÇÊé•‰∏ãÊù•ÔºåÊàë‰ª¨ÂÆûÁé∞‰∫Ü‰∏Ä‰∏™Âêç‰∏∫ `inorderTraversal` ÁöÑÊñπÊ≥ïÔºåÂÆÉÈááÁî®ÈÄíÂΩíÊñπÂºèÈÅçÂéÜÊ†ëÁªìÊûÑÔºåÈ¶ñÂÖàÈÅçÂéÜÂ∑¶Â≠êÊ†ëÔºåÁÑ∂ÂêéËæìÂá∫ÂΩìÂâçËäÇÁÇπÁöÑÂÄºÔºåÊúÄÂêéÈÅçÂéÜÂè≥Â≠êÊ†ë„ÄÇÂú®‰∏ªÊñπÊ≥ï‰∏≠ÔºåÊàë‰ª¨ÂàõÂª∫‰∫Ü‰∏Ä‰∏™ÁÆÄÂçïÁöÑÊ†ëÁªìÊûÑÔºåÂπ∂‰ΩøÁî® `inorderTraversal` ÊñπÊ≥ïÂØπÂÖ∂ËøõË°å‰∏≠Â∫èÈÅçÂéÜ„ÄÇËæìÂá∫ÁªìÊûúÂ∞ÜÊåâÈ°∫Â∫èÊâìÂç∞ËäÇÁÇπÁöÑÂÄº„ÄÇ', 97, 'assistant', '2023-10-09 22:41:22');
