INSERT INTO `xz_dialogue` VALUES (488, 'include <bits/stdc++.h>\nusing namespace std;\nusing i64 = int64_t;\nconstexpr i64 mod = 998244353;\nint main() {\n  cin.tie(nullptr)->sync_with_stdio(false);\n  auto read = [&]() {\n    int n;\n    cin >> n;\n    vector<int> a(n), b(n);\n    for (int &ai : a) { cin >> ai; }\n    for (int &bi : b) { cin >> bi; }\n    map<int, int> f;\n    for (int i = 0; i < n; i += 1) { f[a[i]] = b[i]; }\n    return tuple(n, f);\n  };\n  auto [n, x] = read();\n  auto [m, y] = read();\n  for (auto [ci, di] : y) {\n    if (not x.contains(ci)) { x[ci] = 0; }\n  }\n  i64 ans = 1;\n  for (auto [ai, bi] : x) {\n    int di = y[ai];\n    if (bi < di) {\n      ans = 0;\n    } else if (bi != di) {\n      ans = ans * 2 % mod;\n    }\n  }\n  cout << ans;\n}\nËΩ¨ÂåñÊàêjava‰ª£Á†Å', 99, 'user', '2023-10-10 12:42:51');
