INSERT INTO `xz_dialogue` VALUES (578, '#include <iostream>  \n#include <vector>  \n#include <algorithm>  \nusing namespace std;  \n  \nint main() {  \n    int n;  \n    cin >> n;  \n    vector<int> arr(n);  \n    for (int i = 0; i < n; i++) {  \n        cin >> arr[i];  \n    }  \n    sort(arr.begin(), arr.end());  \n    vector<bool> vis(1e6 + 1, false);  \n    for (int i = 0; i < n; i++) {  \n        for (int j = i + 1; j < n; j++) {  \n            int num = arr[j] - arr[i];  \n            if (vis[num]) continue;  \n            vis[num] = true;  \n            for (int k = 2; k * k <= num; k++) {  \n                if (num % k == 0) {  \n                    vis[k] = true;  \n                    while (num % k == 0) {  \n                        num /= k;  \n                    }  \n                }  \n            }  \n            if (num > 1) {  \n                vis[num] = true;  \n            }  \n        }  \n    }  \n    for (int i = n; i <= 1000000; i++) {  \n        if (!vis[i]) {  \n            cout << i << endl;  \n            break;  \n        }  \n    }  \n    return 0;  \n}\nÂàÜÊûê‰∏Ä‰∏ãÊó∂Èó¥Â§çÊùÇÂ∫¶', 110, 'user', '2023-10-13 16:40:27');
