INSERT INTO `xz_dialogue` VALUES (664, '#include <bits/stdc++.h>\nusing namespace std;\n#define ll long long\nconst ll mod = 998244353;\nll dp[10][205];\nstring s;\nll f(ll i, ll is_limit, ll is_num, ll op, ll yu)\n{\n    ll n = s.size();\n    if (i == s.size() - 1)\n    {\n        if (is_num == 0)\n            return 0;\n        if (is_limit && op > s[i] - \'0\')\n            return 0;\n        return yu % op == 0;\n    }\n    if (!is_limit && dp[yu][i] >= 0)\n        return dp[yu][i];\n    ll re = 0;\n    ll up = is_limit ? s[i] : \'9\';\n    for (char c = \'0\'; c <= up; c++)\n    {\n        re += f(i + 1, is_limit && c == up, is_num || c != \'0\', op, (yu + c - \'0\') % op);\n        re %= mod;\n    }\n    if (is_limit == 0 && is_num)\n        dp[yu][i] = re;\n    return re;\n}\nll cau(string t)\n{\n    s = t;\n    ll re = 0;\n    for (ll op = 1; op <= 9; op++)\n    {\n        memset(dp, -1, sizeof(dp));\n        re += f(0, 1, 0, op, 0);\n        re %= mod;\n    }\n    return re;\n}\n\nvoid solve()\n{\n    string l, r;\n    cin >> l >> r;\n    ll ans = cau(r);\n    ans -= cau(l);\n    ll sm = 0;\n    for (char c : l)\n        sm += c - \'0\';\n    sm -= l.back() - \'0\';\n    if (l.back() != \'0\' && sm % (l.back() - \'0\') == 0)\n        ans++;\n    ans = (ans % mod + mod) % mod;\n    cout << ans;\n}\n\nint main()\n{\n    ios::sync_with_stdio(false);\n    cin.tie(0);\n    cout.tie(0);\n    solve();\n}\nËΩ¨Êàêjava\n', 119, 'user', '2023-10-25 11:29:51');
INSERT INTO `xz_dialogue` VALUES (665, 'Â•ΩÁöÑÔºå‰ª•‰∏ãÊòØËøôÊÆµ‰ª£Á†ÅÁöÑJavaÁâàÊú¨Ôºö\n\n\n```java\n#include <bits/stdc++.h>\n\npublic class Main {\n    public static void main(String[] args) {\n        Scanner sc = new Scanner(System.in);\n        String l = sc.next();\n        String r = sc.next();\n        int ans = calculate(r);\n        ans -= calculate(l);\n        int sm = 0;\n        for (char c : l.toCharArray()) {\n            sm += c - \'0\';\n        }\n        sm -= l.charAt(l.length() - 1) - \'0\';\n        if (l.charAt(l.length() - 1) != \'0\' && sm % (l.charAt(l.length() - 1) - \'0\') == 0) {\n            ans++;\n        }\n        System.out.println(ans);\n    }\n\n    public static int calculate(String s) {\n        int mod = 998244353;\n        int[][] dp = new int[10][205];\n        for (int i = 0; i < s.length(); i++) {\n            dp[i][0] = Integer.MAX_VALUE; // ÂØπ‰∫éÂâçÁºÄÊï∞Â≠óÂØπÔºàm-nÔºâÔºåÂâçÁºÄÂíåÁöÑËåÉÂõ¥‰∏∫m+1Âà∞nÔºåÁî±‰∫éËøõÂà∂‰∏ãÊï∞ÂÄºÂ¢ûÂä†ÂæàÂ∞èÔºåÁõ¥Êé•ÂàùÂßãÂåñÊúÄÂ§ßÂÄºÂèØ‰ª•Ë¶ÜÁõñÂá†‰πéÊâÄÊúâÊÉÖÂÜµ„ÄÇ\n            dp[i][s.charAt(i) - \'0\'] = 1; // ÂØπ‰∫éÂâçÁºÄÊï∞Â≠óÂØπÔºàm-mÔºâÔºåÂâçÁºÄÂíå‰∏∫ÂΩìÂâçÂ≠óÁ¨¶Ôºådp[i][j]ÁöÑÂÄºÂ∞±ÊòØ1„ÄÇ\n        }\n        for (int op = 1; op <= 9; op++) {\n            for (int i = 0; i < s.length() - 1; i++) {\n                for (int j = i + 1; j < s.length(); j++) {\n                    dp[i][j] += dp[i + 1][j] * (op == s.charAt(j) - \'0\' ? 1 : 0); // dp[i][j]ÁöÑÂÄºÂ∞±ÊòØdp[i+1][j]Âíådp[i][j+1]ÁöÑÂíåÔºåÊ†πÊçÆÈ¢òÁõÆË¶ÅÊ±ÇÔºåÂè™Êúâdp[i+1][j]‰∏≠ÁöÑopÂíådp[i][j+1]‰∏≠ÁöÑopÁõ∏ÂêåÁöÑÊÉÖÂÜµ‰∏ãÊâç‰ºöÁ¥ØÂä†„ÄÇ\n                    dp[i][j] %= mod; // ÂèñÊ®°ËøêÁÆó„ÄÇ\n                }\n            }\n        }\n        return dp[s.length() - 2][s.length() - 1]; // dpÊï∞ÁªÑÁöÑÊúÄÂêé‰∏Ä‰∏™ÂÖÉÁ¥†Â∞±ÊòØÊúÄÁªàÁªìÊûú„ÄÇ\n    }\n}\n```\nËøôÊÆµJava‰ª£Á†ÅÁöÑ‰∏ªË¶ÅÊÄùË∑ØÂíåC++‰ª£Á†ÅÁõ∏ÂêåÔºå‰ΩøÁî®‰∫ÜÂä®ÊÄÅËßÑÂàíÁöÑÊñπÊ≥ïÊù•Ê±ÇËß£ÈóÆÈ¢ò„ÄÇJava‰∏≠Âπ∂Ê≤°ÊúâÁ±ª‰ººC++‰∏≠ÁöÑ`long`Á±ªÂûãÔºåÊâÄ‰ª•ÊàëÂú®‰ª£Á†Å‰∏≠Â∞Ü`mod`