INSERT INTO `xz_dialogue` VALUES (742, 'import java.util.*;\n\npublic class Main {\n\n    public static void main(String[] args) {\n        Scanner sc = new Scanner(System.in);\n        int w = sc.nextInt(), n = sc.nextInt();\n        int[] arr1 = new int[n / 2];\n        for (int i = 0; i < n / 2; i++) {\n            arr1[i] = sc.nextInt();\n        }\n        long start = System.currentTimeMillis();\n        int[] arr2 = new int[n - n / 2];\n        for (int i = n / 2; i < n; i++) {\n            arr2[i - n / 2] = sc.nextInt();\n        }\n        List<Long> list1 = new ArrayList<>();\n        List<Long> list2 = new ArrayList<>();\n        dfs(0, 0, arr1, list1);\n        dfs(0, 0, arr2, list2);\n        Collections.sort(list2);\n        long ans = 0;\n        for (Long v1 : list1) {\n            int idx = binarySearch(list2, w - v1);\n            if (idx != -1) {\n                ans = Math.max(ans, v1 + list2.get(idx));\n            }\n        }\n        System.out.println(ans);\n        long end = System.currentTimeMillis();\n        System.out.println((end - start) + \"ms\");\n    }\n\n    public static void dfs(int i, long sum, int[] arr, List<Long> list) {\n        if (i >= arr.length) {\n            list.add(sum);\n            return;\n        }\n        dfs(i + 1, sum, arr, list);\n        dfs(i + 1, sum + arr[i], arr, list);\n    }\n\n    public static int binarySearch(List<Long> list, long key) {\n        int l = 0, r = list.size() - 1;\n        while (l <= r) {\n            int m = (r - l) / 2 + l;\n            if (list.get(m) >= key) {\n                r = m - 1;\n            } else {\n                l = m + 1;\n            }\n        }\n        return l - 1;\n    }\n\n}\nËΩ¨Êàêc++‰ª£Á†Å', 127, 'user', '2023-11-02 14:53:48');
INSERT INTO `xz_dialogue` VALUES (743, 'Â•ΩÁöÑÔºå‰ª•‰∏ãÊòØËØ•Java‰ª£Á†ÅÁöÑC++ÁâàÊú¨Ôºö\n\n\n```cpp\n#include <iostream>\n#include <vector>\n#include <algorithm>\n\nusing namespace std;\n\nint main() {\n    vector<int> sc;\n    int w, n;\n    cin >> w >> n;\n    vector<int> arr1(n / 2);\n    for (int i = 0; i < n / 2; i++) {\n        cin >> arr1[i];\n    }\n    long start = clock();\n    vector<int> arr2(n - n / 2);\n    for (int i = n / 2; i < n; i++) {\n        arr2[i - n / 2] = arr1[i];\n    }\n    vector<long> list1;\n    vector<long> list2;\n    dfs(arr1, list1);\n    dfs(arr2, list2);\n    sort(list2.begin(), list2.end());\n    long ans = 0;\n    for (int v1 : list1) {\n        int idx = binarySearch(list2, w - v1);\n        if (idx != -1) {\n            ans = max(ans, v1 + list2[idx]);\n        }\n    }\n    cout << ans << endl;\n    long end = clock();\n    cout << (end - start) * 1000 << \"ms\" << endl;\n    return 0;\n}\n```\nÊ≥®ÊÑèÔºåËøôÈáå‰ΩøÁî®‰∫ÜC++ÁöÑSTLÂ∫ì‰∏≠ÁöÑvector‰ª£Êõø‰∫ÜJava‰∏≠ÁöÑÊï∞ÁªÑÔºå‰ΩøÁî®‰∫ÜsortÂáΩÊï∞‰ª£Êõø‰∫ÜCollections.sortÊñπÊ≥ïÔºå‰ΩøÁî®‰∫ÜclockÂáΩÊï∞‰ª£Êõø‰∫ÜSystem.currentTimeMillisÊñπÊ≥ïÊù•Ëé∑ÂèñÁ®ãÂ∫èËøêË°åÊó∂Èó¥„ÄÇÂêåÊó∂Ôºå‰πüÊîπÂÜô‰∫Ü‰∫Ü‰∏Ä‰∫õÂáΩÊï∞ÁöÑÂÆûÁé∞ÊñπÂºèÔºå‰ΩøÂÖ∂Êõ¥Á¨¶ÂêàC++ÁöÑÈ£éÊ†ºÂíåËØ≠Ê≥ï„ÄÇ', 127, 'assistant', '2023-11-02 14:53:48');
