INSERT INTO `xz_dialogue` VALUES (1090, 'È¢òÁõÆÔºöÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢òÔºàDijkstraÁÆóÊ≥ïÔºâ\n\nÁªôÂÆö‰∏Ä‰∏™Â∏¶ÊùÉÊúâÂêëÂõæÔºåÂÖ∂‰∏≠ËäÇÁÇπË°®Á§∫‰∏∫Êï¥Êï∞ÔºåËæπË°®Á§∫‰∏∫‰∏ÄÂØπËäÇÁÇπÂíåÊùÉÈáçÁöÑÂÖÉÁªÑ„ÄÇÁªôÂÆöËµ∑ÁÇπÂíåÁªàÁÇπÔºåÊ±Ç‰ªéËµ∑ÁÇπÂà∞ÁªàÁÇπÁöÑÊúÄÁü≠Ë∑ØÂæÑ„ÄÇ\n\nÁÆóÊ≥ïÊÄùË∑ØÔºö\nDijkstraÁÆóÊ≥ïÊòØ‰∏ÄÁßçÁî®‰∫éËß£ÂÜ≥ÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢òÁöÑÁÆóÊ≥ïÔºåÈÄÇÁî®‰∫éÁ®ÄÁñèÂõæ„ÄÇÂÆÉÈÄöËøá‰∏çÊñ≠Êõ¥Êñ∞Ë∑ùÁ¶ªÂÄºÔºåÈÄêÊ≠•ÊâæÂà∞ÊúÄÁü≠Ë∑ØÂæÑ„ÄÇÁÆóÊ≥ï‰ΩøÁî®‰ºòÂÖàÈòüÂàóÔºàÂ¶ÇÊúÄÂ∞èÂ†ÜÔºâÊù•Áª¥Êä§Êú™ËÆøÈóÆËäÇÁÇπ‰∏≠Ë∑ùÁ¶ªËµ∑ÁÇπÊúÄËøëÁöÑËäÇÁÇπ„ÄÇ\n\nPython‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```python\nimport heapq\n\ndef dijkstra(graph, start, end):\n    # ÂàùÂßãÂåñË∑ùÁ¶ªÂ≠óÂÖ∏ÔºåÂ∞ÜËµ∑ÁÇπÂà∞ÊâÄÊúâËäÇÁÇπÁöÑË∑ùÁ¶ªÂàùÂßãÂåñ‰∏∫Êó†Á©∑Â§ßÔºåÂ∞ÜËµ∑ÁÇπÂà∞Ëá™Ë∫´ÁöÑË∑ùÁ¶ªÂàùÂßãÂåñ‰∏∫0\n    distances = {node: float(\'infinity\') for node in graph}\n    distances[start] = 0\n\n    # ÂàùÂßãÂåñÊú™ËÆøÈóÆËäÇÁÇπÈõÜÂêà\n    unvisited = set(graph) - set([start])\n\n    # ‰ΩøÁî®‰ºòÂÖàÈòüÂàóÁª¥Êä§Êú™ËÆøÈóÆËäÇÁÇπ‰∏≠Ë∑ùÁ¶ªËµ∑ÁÇπÊúÄËøëÁöÑËäÇÁÇπ\n    heap = [(0, start)]  # (distance, node)ÁöÑÂÖÉÁªÑ\n    while heap:\n        # ÂèñÂá∫ÂΩìÂâçË∑ùÁ¶ªËµ∑ÁÇπÊúÄËøëÁöÑËäÇÁÇπÔºåÂπ∂Êõ¥Êñ∞ÂÖ∂Áõ∏ÈÇªËäÇÁÇπÁöÑË∑ùÁ¶ª\n        (distance, node) = heapq.heappop(heap)\n        unvisited.remove(node)\n        for neighbor, weight in graph[node]:\n            old_distance = distances[neighbor]\n            new_distance = distance + weight\n            if new_distance < old_distance:  # Êõ¥Êñ∞Áõ∏ÈÇªËäÇÁÇπÁöÑË∑ùÁ¶ª\n                distances[neighbor] = new_distance\n                heapq.heappush(heap, (new_distance, neighbor))  # Â∞ÜÊõ¥Êñ∞ÂêéÁöÑËäÇÁÇπÂä†ÂÖ•‰ºòÂÖàÈòüÂàó‰∏≠\n\n    # ËøîÂõûÁªàÁÇπÂà∞ÊâÄÊúâÂÖ∂‰ªñËäÇÁÇπÁöÑÊúÄÁü≠Ë∑ùÁ¶ªÔºà‰∏çÂåÖÊã¨Ëµ∑ÁÇπÔºâ\n    return {end: distances[end], node: distances[node] for node in distances if node != start}\n```\n‰ΩøÁî®Á§∫‰æãÔºö\n\n\n```python\ngraph = {\n    \'A\': [((\'B\', 3), 1), ((\'C\', 2), 2)],\n    \'B\': [((\'A\', 3), 1)],\n    \'C\': [((\'A\', 2), 3)],\n    \'D\': [((\'E\', 6), 2), ((\'F\', 8), 3)],\n    \'E\': [((\'D\', 6), 2)],\n    \'F\': [((\'D\', 8), 1)],\n}\nprint(dijkstra(graph, \'A\', \'D\'))  # {\'A\': 0, \'B\': 1, \'C\': 2, \'D\': 2}\n```', 141, 'assistant', '2024-03-05 23:52:13');
