INSERT INTO `xz_dialogue` VALUES (1094, 'È¢òÁõÆÔºöÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢òÔºàDijkstraÁÆóÊ≥ïÔºâ\n\nÁªôÂÆö‰∏Ä‰∏™Â∏¶ÊùÉÊúâÂêëÂõæÔºåÂÖ∂‰∏≠ËäÇÁÇπË°®Á§∫‰∏∫Êï¥Êï∞ÔºåËæπË°®Á§∫‰∏∫‰∏ÄÂØπËäÇÁÇπÂíåÊùÉÈáçÁöÑÂÖÉÁªÑ„ÄÇË¶ÅÊ±ÇÊâæÂà∞‰ªéËµ∑ÁÇπÂà∞ÊâÄÊúâÂÖ∂‰ªñËäÇÁÇπÁöÑÊúÄÁü≠Ë∑ØÂæÑ„ÄÇ\n\nPython‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```python\nimport heapq\n\ndef shortest_path(graph, start):\n    # ÂàùÂßãÂåñË∑ùÁ¶ªÂ≠óÂÖ∏ÔºåÂ∞ÜËµ∑ÁÇπÂà∞Ëá™Ë∫´ÁöÑË∑ùÁ¶ªËÆæ‰∏∫0\n    distances = {node: 0 for node in graph}\n    distances[start] = 0\n    # ÂàùÂßãÂåñ‰ºòÂÖàÈòüÂàóÔºåÁî®‰∫éÂ≠òÂÇ®ÂæÖËÆøÈóÆËäÇÁÇπ\n    queue = [(0, start)]\n    # ËÆ∞ÂΩïÂ∑≤ËÆøÈóÆÁöÑËäÇÁÇπÈõÜÂêà\n    visited = set()\n\n    while queue:\n        # ÂèñÂá∫ÂΩìÂâçË∑ùÁ¶ªÊúÄÂ∞èÁöÑËäÇÁÇπ\n        current_distance, current_node = heapq.heappop(queue)\n        # Â¶ÇÊûúÂΩìÂâçËäÇÁÇπÂ∑≤ÁªèË¢´ËÆøÈóÆËøáÔºåË∑≥Ëøá\n        if current_node in visited:\n            continue\n        # Â∞ÜÂΩìÂâçËäÇÁÇπÊ†áËÆ∞‰∏∫Â∑≤ËÆøÈóÆ\n        visited.add(current_node)\n        # ÈÅçÂéÜÂΩìÂâçËäÇÁÇπÁöÑÈÇªÂ±ÖËäÇÁÇπ\n        for neighbor, weight in graph[current_node]:\n            # Â¶ÇÊûúÊâæÂà∞‰∫ÜÊõ¥Áü≠ÁöÑË∑ØÂæÑÔºåÊõ¥Êñ∞ÂΩìÂâçËäÇÁÇπÂà∞ÈÇªÂ±ÖËäÇÁÇπÁöÑË∑ùÁ¶ªÂπ∂Ê†áËÆ∞‰∏∫Â∑≤ËÆøÈóÆ\n            new_distance = current_distance + weight\n            if new_distance < distances[neighbor]:\n                distances[neighbor] = new_distance\n                heapq.heappush(queue, (new_distance, neighbor))\n    return distances\n```\n‰ª•‰∏ä‰ª£Á†ÅÂÆûÁé∞‰∫Ü‰∏Ä‰∏™ÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢òÁöÑÁªèÂÖ∏ÁÆóÊ≥ï Dijkstra ÁÆóÊ≥ïÔºåÂèØ‰ª•ÈÄöËøáÁªôÂÆöÁöÑÊúâÂêëÂõæ `graph` ÂíåËµ∑ÁÇπ `start` Êù•ÊâæÂà∞‰ªéËµ∑ÁÇπÂà∞ÊâÄÊúâÂÖ∂‰ªñËäÇÁÇπÁöÑÊúÄÁü≠Ë∑ØÂæÑ„ÄÇËøîÂõûÁöÑË∑ùÁ¶ªÂ≠óÂÖ∏‰∏≠ÔºåÊØè‰∏™ËäÇÁÇπÁöÑÂÄºË°®Á§∫‰ªéËµ∑ÁÇπÂà∞ËØ•ËäÇÁÇπÁöÑÊúÄÁü≠Ë∑ùÁ¶ª„ÄÇ', 141, 'assistant', '2024-03-05 23:52:26');
