INSERT INTO `xz_dialogue` VALUES (1108, 'È¢òÁõÆÔºöÊ±ÇËß£‰∫åÂàÜÂõæ‰∏≠ÁöÑÊúÄÂ§ßÂåπÈÖçÈóÆÈ¢ò\n\nÂÅáËÆæÁªôÂÆö‰∏Ä‰∏™‰∫åÂàÜÂõæÔºåÂÖ∂‰∏≠ËäÇÁÇπÈõÜ‰∏∫V={v1,v2,...,vm},ËæπÈõÜ‰∏∫E={e1,e2,...,en},‰∏îEÊòØÁî±ÊâÄÊúâËøûÈÄöËæπÁöÑÈõÜÂêàÊûÑÊàê„ÄÇÊàë‰ª¨ÁöÑÁõÆÊ†áÊòØÊâæÂá∫Âõæ‰∏≠ÁöÑÊúÄÂ§ßÂåπÈÖçÔºåÂç≥ÊúÄÂ§ßÁöÑÂèØ‰ª•Áõ∏‰∫íÈÖçÂØπÁöÑËæπÁöÑÈõÜÂêà„ÄÇ\n\nÂú®Ëß£ÂÜ≥Ëøô‰∏™ÈóÆÈ¢òÊó∂ÔºåÊàë‰ª¨ÂèØ‰ª•‰ΩøÁî®ÂìàÂ∏åË°®ÂíåÂèåÊåáÈíàÊäÄÊúØÊù•ÊúâÊïàÂú∞ÊâæÂá∫ÊúÄÂ§ßÂåπÈÖç„ÄÇËøôÁßçÊñπÊ≥ïÈ¶ñÂÖàÂàõÂª∫‰∏Ä‰∏™ÂìàÂ∏åË°®ÔºåÁî®Êù•Â≠òÂÇ®ÊâÄÊúâ‰∏éÁªôÂÆöËäÇÁÇπÁõ∏ÂåπÈÖçÁöÑËæπÁöÑÁ¥¢ÂºïÔºåÁÑ∂ÂêéÂú®ÈÅçÂéÜËæπÁöÑËøáÁ®ã‰∏≠Êõ¥Êñ∞ÊúÄÂ§ßÂåπÈÖçÂÄº„ÄÇ\n\nËøôÊòØ‰∏Ä‰∏™ Python ÁöÑÂÆûÁé∞ÊñπÂºèÔºö\n\n\n```python\ndef max_matching(graph):\n    m = len(graph)  # ËäÇÁÇπÊï∞\n    matching = [[] for _ in range(m)]  # ÂàùÂßãÂåñÂåπÈÖçÂàóË°®\n    for u, v in graph:  # ÈÅçÂéÜÊâÄÊúâËæπ\n        if u not in matching[v]:  # Â¶ÇÊûúÂΩìÂâçËæπ‰∏çÂú®ÂåπÈÖçÂàóË°®‰∏≠\n            matching[v].append(u)  # Ê∑ªÂä†Âà∞ÂåπÈÖçÂàóË°®‰∏≠\n            matching[u].append(v)  # Âπ∂‰∏îÊ∑ªÂä†Âà∞ÂØπÊñπÁöÑÂåπÈÖçÂàóË°®‰∏≠\n    max_matching = 0  # ÂàùÂßãÂåñÊúÄÂ§ßÂåπÈÖçÂÄº‰∏∫0\n    for i in range(m):\n        if len(matching[i]) > max_matching:  # Â¶ÇÊûúÂΩìÂâçËäÇÁÇπÁöÑÂåπÈÖçÊï∞Â§ß‰∫éÊúÄÂ§ßÂåπÈÖçÂÄº\n            max_matching = len(matching[i])  # Êõ¥Êñ∞ÊúÄÂ§ßÂåπÈÖçÂÄº\n    return max_matching\n```\nËøô‰∏™ÁÆóÊ≥ïÁöÑÊó∂Èó¥Â§çÊùÇÂ∫¶ÊòØO(E)ÔºåÂÖ∂‰∏≠EÊòØËæπÁöÑÊï∞Èáè„ÄÇÁ©∫Èó¥Â§çÊùÇÂ∫¶‰πüÊòØO(E)ÔºåÂõ†‰∏∫ÂÆÉÈúÄË¶ÅÂ≠òÂÇ®ÊâÄÊúâÁöÑÂåπÈÖçÂàóË°®„ÄÇ\n\nËøôÊòØ‰∏Ä‰∏™ Java ÁöÑÂÆûÁé∞ÊñπÂºèÔºö\n\n\n```java\nimport java.util.*;\n\npublic class Main {\n    public static int maxMatching(int[][] graph) {\n        int m = graph.length; // ËäÇÁÇπÊï∞\n        int[] match = new int[m]; // ÂàùÂßãÂåñÂåπÈÖçÂàóË°®\n        for (int[] edge : graph) { // ÈÅçÂéÜÊâÄÊúâËæπ\n            int u = edge[0]; int v = edge[1];\n            if (match[u] == 0) { // Â¶ÇÊûúÂΩìÂâçËæπ‰∏çÂú®ÂåπÈÖçÂàóË°®‰∏≠\n                match[u] = v; // Ê∑ªÂä†Âà∞ÂåπÈÖçÂàóË°®‰∏≠\n                match[v] = u; // Âπ∂‰∏îÊ∑ªÂä†Âà∞ÂØπÊñπÁöÑÂåπÈÖçÂàóË°®‰∏≠\n            }\n        }\n        int max_matching = 0; // ÂàùÂßãÂåñÊúÄÂ§ßÂåπÈÖçÂÄº‰∏∫0\n        for (int i = 0; i < m; i++) { // ÈÅçÂéÜÊâÄÊúâËäÇÁÇπ\n            if (match[i] > max_matching) { // Â¶ÇÊûúÂΩìÂâçËäÇÁÇπÁöÑÂåπÈÖçÊï∞Â§ß‰∫éÊúÄÂ§ßÂåπÈÖçÂÄº\n                max_matching = match[i]; // Êõ¥Êñ∞ÊúÄÂ§ßÂåπÈÖçÂÄº\n            }\n        }\n        return max_matching;\n    }\n}\n```\nËøô‰∏™ÁÆóÊ≥ïÁöÑÊó∂Èó¥Â§çÊùÇÂ∫¶ÊòØO(E)ÔºåÂÖ∂‰∏≠EÊòØËæπÁöÑÊï∞Èáè„ÄÇÁ©∫Èó¥Â§çÊùÇÂ∫¶‰πüÊòØO(E)ÔºåÂõ†‰∏∫ÂÆÉÈúÄË¶ÅÂ≠òÂÇ®ÊâÄÊúâÁöÑÂåπÈÖçÂàóË°®„ÄÇÊ≥®ÊÑèÔºåJava ÁöÑÊï∞ÁªÑÁ¥¢ÂºïÊòØ‰ªé0ÂºÄÂßãÁöÑÔºåÊâÄ‰ª•Êàë‰ª¨Âú®ÂàõÂª∫Êï∞ÁªÑÊó∂ÈúÄË¶ÅÂ∞ÜÂÖ∂ÈïøÂ∫¶ËÆæÁΩÆ‰∏∫m„ÄÇ', 141, 'assistant', '2024-03-06 00:00:36');
