INSERT INTO `xz_dialogue` VALUES (1112, 'È¢òÁõÆÔºöÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢òÔºàDijkstraÁÆóÊ≥ïÔºâÁöÑ‰ºòÂåñÂÆûÁé∞\n\nË¶ÅÊ±ÇÔºö\n\n* ‰ΩøÁî®‰ºòÂÖàÈòüÂàóÔºàÊúÄÂ∞èÂ†ÜÔºâ‰ºòÂåñDijkstraÁÆóÊ≥ïÔºå‰ΩøÂæóÊó∂Èó¥Â§çÊùÇÂ∫¶Èôç‰ΩéÂà∞O(nlogn)„ÄÇ\n* ÂÆûÁé∞Êó∂ÈúÄË¶ÅËÄÉËôëÂõæ‰∏≠ÂèØËÉΩÂ≠òÂú®Ë¥üÊùÉËæπÔºåÈúÄË¶ÅÂØπÁÆóÊ≥ïËøõË°åÁõ∏Â∫îÁöÑÂ§ÑÁêÜ„ÄÇ\n\nPython‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```python\nimport heapq\n\ndef dijkstra_with_priority_queue(graph, start):\n    n = len(graph)\n    # ÂàùÂßãÂåñË∑ùÁ¶ªÂ≠óÂÖ∏Âíå‰ºòÂÖàÈòüÂàó\n    distances = {node: float(\'infinity\') for node in graph}\n    distances[start] = 0\n    priority_queue = [(0, start)]\n    # ‰ΩøÁî®‰ºòÂÖàÈòüÂàóÈÅçÂéÜÊâÄÊúâËäÇÁÇπÔºåÊâæÂà∞ÊúÄÁü≠Ë∑ØÂæÑ\n    while priority_queue:\n        (curr_dist, curr_node) = heapq.heappop(priority_queue)\n        for neighbor, neighbor_dist in graph[curr_node].items():\n            old_dist = distances[neighbor]\n            new_dist = curr_dist + neighbor_dist\n            if new_dist < old_dist:\n                distances[neighbor] = new_dist\n                heapq.heappush(priority_queue, (new_dist, neighbor))\n    return distances\n```\nJava‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```java\nimport java.util.*;\n\npublic class DijkstraWithPriorityQueue {\n    public static void main(String[] args) {\n        // ÊûÑÂª∫ÂõæÁªìÊûÑ\n        int[][] graph = {{1, 2}, {2, 3}, {3, 4}, {4, 5}};\n        int start = 0;\n        // ‰ΩøÁî®‰ºòÂÖàÈòüÂàóÊ±ÇËß£ÊúÄÁü≠Ë∑ØÂæÑ\n        Map<Integer, Integer> distances = dijkstraWithPriorityQueue(graph, start);\n        System.out.println(\"ÊúÄÁü≠Ë∑ØÂæÑÈïøÂ∫¶‰∏∫Ôºö\" + distances.get(5)); // ËæìÂá∫ÁªìÊûú‰∏∫ 2\n    }\n    \n    public static Map<Integer, Integer> dijkstraWithPriorityQueue(int[][] graph, int start) {\n        int n = graph.length;\n        Map<Integer, Integer> distances = new HashMap<>();\n        for (int i = 0; i < n; i++) {\n            distances.put(i, Integer.MAX_VALUE); // ÂàùÂßãÂåñË∑ùÁ¶ª‰∏∫Êó†Á©∑Â§ßÔºåË°®Á§∫Êú™Áü•Ë∑ØÂæÑÈïøÂ∫¶\n        }\n        distances.put(start, 0); // Ëµ∑ÂßãËäÇÁÇπË∑ùÁ¶ª‰∏∫0ÔºåË°®Á§∫Â∑≤Áü•Ë∑ØÂæÑÈïøÂ∫¶‰∏∫0\n        PriorityQueue<int[]> priorityQueue = new PriorityQueue<>((a, b) -> a[0] - b[0]); // ÊúÄÂ∞èÂ†ÜÔºåÁî®‰∫é‰ºòÂÖàÈòüÂàóÂ≠òÂÇ®ËäÇÁÇπÂíåË∑ùÁ¶ª‰ø°ÊÅØ\n        priorityQueue.offer(new int[]{0, start}); // Â∞ÜËµ∑ÂßãËäÇÁÇπÂä†ÂÖ•‰ºòÂÖàÈòüÂàóÔºå‰Ωú‰∏∫Á¨¨‰∏Ä‰∏™ÈúÄË¶ÅËÆøÈóÆÁöÑËäÇÁÇπ\n        while (!priorityQueue.isEmpty()) {\n            int[] nodeAndDist = priorityQueue.poll(); // ‰ªé‰ºòÂÖàÈòüÂàó‰∏≠ÂèñÂá∫‰∏Ä‰∏™ËäÇÁÇπÂíåÂØπÂ∫îÁöÑË∑ùÁ¶ª‰ø°ÊÅØ\n            int node = nodeAndDist[0]; // ÂèñÂá∫ÁöÑËäÇÁÇπÁºñÂè∑\n            int dist = nodeAndDist[1]; // ÂèñÂá∫ÁöÑË∑ùÁ¶ª‰ø°ÊÅØÔºàÂ¶ÇÊûúÂõæ‰∏≠Â≠òÂú®Ë¥üÊùÉËæπÔºåËøôÈáåÁöÑÂÄº‰ºöÂ∞è‰∫éÂéüË∑ùÁ¶ªÔºâ\n            for (int neighbor : graph[node]) { // Â∞ÜÂΩìÂâçËäÇÁÇπÁöÑÈÇªÂ±ÖËäÇÁÇπÂä†ÂÖ•‰ºòÂÖàÈòüÂàó‰∏≠ÔºàËã•Â≠òÂú®Ë¥üÊùÉËæπÔºå‰ºöÂ∞è‰∫éÂéüË∑ùÁ¶ªÔºâ\n                int newDist = dist + distanceBetween(node, neighbor); // Ê†πÊçÆÈÇªÂ±ÖËäÇÁÇπÁöÑÊùÉÈáçÊõ¥Êñ∞ÂΩìÂâçËäÇÁÇπÁöÑË∑ùÁ¶ª‰ø°ÊÅØÔºåÂä†ÂÖ•‰ºòÂÖàÈòüÂàó‰∏≠Â≠òÂÇ®Âπ∂Á≠âÂæÖ‰∏ãÊ¨°ÈÅçÂéÜËÆøÈóÆÔºåÈò≤Ê≠¢Âæ™ÁéØËÆøÈóÆÁõ∏ÂêåËäÇÁÇπÂ§öÊ¨°Êµ™Ë¥πÊó∂Èó¥ÂºÄÈîÄ„ÄÇ‰ºòÂåñÁöÑÁõÆÁöÑ‰πã‰∏ÄÊòØ‰∏∫‰∫ÜÈò≤Ê≠¢‰ΩøÁî®ÂÖ∂‰ªñÂèØËÉΩÂØºËá¥Áõ∏ÂêåËäÇÁÇπË¢´ÈáçÂ§çÂ§ÑÁêÜÁöÑÊú∫Âà∂Ôºå‰æãÂ¶ÇÈôêÂà∂ÊúÄÈïøËøûÁª≠Âæ™ÁéØÊó∂Èó¥„ÄÇËØ•ÊñπÊ≥ïÂèØ‰ª•Ê†πÊçÆÂÆûÈôÖÂ∫îÁî®Âú∫ÊôØËøõË°åÈÄÇÂΩìË∞ÉÊï¥Âíå‰ºòÂåñ„ÄÇÊ≠§Â§ñÔºå‰ΩøÁî®‰ºòÂÖàÈòüÂàóÂíå‰ºòÂÖàÁ∫ß‰ø°ÊÅØÊù•‰ºòÂåñÁÆóÊ≥ïÂèØ‰ª•Â§ßÂ§ßÊèêÈ´òÁÆóÊ≥ïÁöÑÊïàÁéáÔºåÂπ∂Èôç‰ΩéÂÖ∂Êó∂Èó¥Â§çÊùÇÂ∫¶„ÄÇÂõ†Ê≠§ÔºåÂú®ÂÆûÁé∞Êó∂ÈúÄË¶ÅËÄÉËôëÂõæÁöÑÁªìÊûÑ„ÄÅÁÆóÊ≥ï‰ºòÂåñÂíåÊó∂Èó¥Â§çÊùÇÂ∫¶Á≠âÂõ†Á¥†„ÄÇÂêåÊó∂ÔºåÈúÄË¶ÅÈ™åËØÅ‰ª£Á†ÅÁöÑÊ≠£Á°ÆÊÄßÂíåÂèØËØªÊÄßÔºåÁ°Æ‰øù‰ª£Á†ÅÁöÑË¥®ÈáèÂíåÂèØÈù†ÊÄß„ÄÇÂú®ÂÆûÈôÖÂ∫îÁî®‰∏≠ÔºåÈúÄË¶ÅÊ†πÊçÆÂÖ∑‰ΩìÂú∫ÊôØÂíåÈúÄÊ±ÇÈÄâÊã©ÂêàÈÄÇÁöÑÁÆóÊ≥ïÂíåÂ∑•ÂÖ∑Êù•ÂÆûÁé∞ÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢ò„ÄÇÊ≠§Â§ñÔºåËøòÂèØ‰ª•ËÄÉËôë‰ΩøÁî®ÂÖ∂‰ªñÁÆóÊ≥ïÂíåÊäÄÊúØÊù•‰ºòÂåñDijkstraÁÆóÊ≥ïÁöÑÊÄßËÉΩÂíåÊïàÁéá„ÄÇ‰æãÂ¶ÇÔºåÂèØ‰ª•‰ΩøÁî®Floyd-WarshallÁÆóÊ≥ïÊù•Ê±ÇËß£‰ªªÊÑè‰∏§ÁÇπ‰πãÈó¥ÁöÑÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢ò„ÄÇÂêåÊó∂ÔºåËøòÈúÄË¶ÅËÄÉËôëÂõæ‰∏≠ÁöÑÂÖ∂‰ªñÂ±ûÊÄßÔºàÂ¶ÇÊùÉÈáç„ÄÅËæπÊï∞Á≠âÔºâÂíåÈôêÂà∂Êù°‰ª∂ÔºàÂ¶ÇÊòØÂê¶Â≠òÂú®Ë¥üÊ