INSERT INTO `xz_dialogue` VALUES (1116, 'È¢òÁõÆÔºöÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢òÔºåDijkstraÁÆóÊ≥ïÂú®Á®ÄÁñèÂõæ‰∏≠ÁöÑÂ∫îÁî®\n\nËøô‰∏™ÈóÆÈ¢òÊòØÂÖ≥‰∫éÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢òÔºåÊòØ‰∏Ä‰∏™Â∏∏ËßÅÁöÑÁÆóÊ≥ïÈóÆÈ¢ò„ÄÇÂú®ÂõæËÆ∫‰∏≠ÔºåÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢òÈÄöÂ∏∏Ê∂âÂèä‰ªé‰∏Ä‰∏™ËäÇÁÇπÂà∞Âè¶‰∏Ä‰∏™ËäÇÁÇπÁöÑÊúÄÁü≠Ë∑ØÂæÑ„ÄÇËøôÈáåÔºåÊàë‰ª¨Â∞Ü‰ΩøÁî®DijkstraÁÆóÊ≥ïÊù•Ëß£ÂÜ≥Ëøô‰∏™ÈóÆÈ¢ò„ÄÇÂÅáËÆæÊàë‰ª¨Êúâ‰∏Ä‰∏™Á®ÄÁñèÂõæÔºåÊØè‰∏™ËäÇÁÇπÈÉΩ‰∏éÂÖ∂Áõ∏ÈÇªÁöÑËäÇÁÇπÊúâËøûÊé•ÂÖ≥Á≥ª„ÄÇ\n\nÈ¢òÁõÆË¶ÅÊ±ÇÔºö\nÁªôÂÆö‰∏Ä‰∏™Ë°®Á§∫Á®ÄÁñèÂõæÁöÑÈÇªÊé•Áü©ÈòµÂíå‰∏Ä‰∏™Ëµ∑ÂßãËäÇÁÇπÔºåÁºñÂÜô‰∏Ä‰∏™ÁÆóÊ≥ïÊâæÂá∫‰ªéËµ∑ÂßãËäÇÁÇπÂà∞ÊâÄÊúâÂÖ∂‰ªñËäÇÁÇπÁöÑÊúÄÁü≠Ë∑ØÂæÑ„ÄÇ\n\nPython‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```python\nimport sys\nimport heapq\n\ndef shortest_path(adj_matrix, start_node):\n    # ÂàùÂßãÂåñË∑ùÁ¶ªÂ≠óÂÖ∏ÔºåÂ∞ÜËµ∑ÂßãËäÇÁÇπÁöÑË∑ùÁ¶ªËÆæ‰∏∫0\n    distances = {node: float(\'infinity\') for node in range(len(adj_matrix))}\n    distances[start_node] = 0\n    \n    # ÂàùÂßãÂåñ‰ºòÂÖàÈòüÂàóÔºåÂåÖÂê´Êú™ËÆøÈóÆËäÇÁÇπÂíåË∑ùÁ¶ªÂ≠óÂÖ∏‰∏≠ÂØπÂ∫îËäÇÁÇπÁöÑË∑ùÁ¶ª\n    pq = [(0, start_node)]\n    \n    while pq:\n        # ÂèñÂá∫ÂΩìÂâçË∑ùÁ¶ªÊúÄÂ∞èÁöÑËäÇÁÇπ\n        current_distance, current_node = heapq.heappop(pq)\n        \n        # Â¶ÇÊûúÂΩìÂâçËäÇÁÇπÁöÑË∑ùÁ¶ªÂ§ß‰∫éÂ≠óÂÖ∏‰∏≠ËÆ∞ÂΩïÁöÑË∑ùÁ¶ªÔºåËØ¥ÊòéËøô‰∏™ËäÇÁÇπÂ∑≤ÁªèÂ§ÑÁêÜËøá‰∫ÜÔºåÁõ¥Êé•Ë∑≥Ëøá\n        if current_distance > distances[current_node]:\n            continue\n        \n        # ÈÅçÂéÜÂΩìÂâçËäÇÁÇπÁöÑÈÇªÂ±ÖËäÇÁÇπÔºåÂπ∂Êõ¥Êñ∞ÂÖ∂Ë∑ùÁ¶ª\n        for neighbor, weight in enumerate(adj_matrix[current_node]):\n            if weight > 0:  # Â¶ÇÊûúÂ≠òÂú®ËøûÊé•ÂÖ≥Á≥ª\n                alt_distance = distances[current_node] + weight\n                if alt_distance < distances[neighbor]:  # Â¶ÇÊûúÊõ¥Êñ∞ÂêéÁöÑË∑ùÁ¶ªÊõ¥Â∞èÔºåÊõ¥Êñ∞Â≠óÂÖ∏Âπ∂Âä†ÂÖ•‰ºòÂÖàÈòüÂàó\n                    distances[neighbor] = alt_distance\n                    heapq.heappush(pq, (alt_distance, neighbor))\n    \n    return distances\n```\nJava‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```java\nimport java.util.*;\n\npublic class ShortestPath {\n    public static Map<Integer, Integer> shortestPath(int[][] adjMatrix, int startNode) {\n        Map<Integer, Integer> distances = new HashMap<>();\n        distances.put(startNode, 0);\n        Queue<Integer> pq = new LinkedList<>();\n        pq.offer(startNode);\n        while (!pq.isEmpty()) {\n            int currentNode = pq.poll();\n            for (int neighbor = 0; neighbor < adjMatrix[currentNode].length; neighbor++) {\n                if (adjMatrix[currentNode][neighbor] > 0) {  // Â¶ÇÊûúÂ≠òÂú®ËøûÊé•ÂÖ≥Á≥ª\n                    int altDistance = distances.getOrDefault(currentNode) + adjMatrix[currentNode][neighbor];\n                    if (altDistance < distances.getOrDefault(neighbor)) {  // Â¶ÇÊûúÊõ¥Êñ∞ÂêéÁöÑË∑ùÁ¶ªÊõ¥Â∞èÔºåÊõ¥Êñ∞Â≠óÂÖ∏Âπ∂Âä†ÂÖ•‰ºòÂÖàÈòüÂàó\n                        distances.put(neighbor, altDistance);\n                        pq.offer(neighbor);\n                    }\n                }\n            }\n        }\n        return distances;\n    }\n}\n```\nC++‰ª£Á†ÅÂÆûÁé∞Ôºö\n\nËØ∑Ê≥®ÊÑèÔºåC++‰ª£Á†ÅÈúÄË¶Å‰ΩøÁî®Ê†áÂáÜÂ∫ì‰∏≠ÁöÑ‰ºòÂÖàÈòüÂàóÂíåÈÇªÊé•Áü©ÈòµÊï∞ÊçÆÁªìÊûÑ„ÄÇÁî±‰∫éC++‰ª£Á†ÅËæÉÈïøÔºåËøôÈáåÂè™ÁªôÂá∫‰∏ªË¶ÅÈÉ®ÂàÜÁöÑ‰ª£Á†ÅÔºö\n\n\n```cpp\n#include <queue>\n#include <vector>\nusing namespace std;\n\nvector<vector<int>> adjMatrix; // ÈÇªÊé•Áü©ÈòµË°®Á§∫ÁöÑÁ®ÄÁñèÂõæ\nint startNode; // Ëµ∑ÂßãËäÇÁÇπÁºñÂè∑\nvector<int> shortestPath(adjMatrix.size()); // ÊúÄÁü≠Ë∑ØÂæÑÁªìÊûúÊï∞ÁªÑÔºåÂàùÂßãÂåñ‰∏∫Êó†Á©∑Â§ßÔºàË°®Á§∫Êú™ÊâæÂà∞Ë∑ØÂæÑÔºâ\nint shortestPathDistances[MAX_NODES]; // Â≠òÂÇ®‰ªéËµ∑ÂßãËäÇÁÇπÂà∞ÂÖ∂‰ªñËäÇÁÇπÁöÑÊúÄÁü≠Ë∑ùÁ¶ªÁöÑÊï∞ÁªÑÔºåÂàùÂßãÂåñ‰∏∫Êó†Á©∑Â§ßÔºàË°®Á§∫Êú™ÊâæÂà∞Ë∑ØÂæÑÔºâ\nbool visited[MAX_NODES]; // Ê†áËÆ∞ËäÇÁÇπÊòØÂê¶Ë¢´ËÆøÈóÆËøáÔºàÁî®‰∫éDijkstraÁÆóÊ≥ïÔºâ\nvector<int> path; // ÊúÄÁü≠Ë∑ØÂæÑÁªìÊûúÊï∞ÁªÑÔºåÂ≠òÂÇ®‰ªéËµ∑ÂßãËäÇÁÇπÂà∞ÁõÆÊ†áËäÇÁÇπÁöÑË∑ØÂæÑÔºàÂ¶ÇÊûúÊúâÁöÑËØùÔºâ\nvector<pair<int, int>> pq; // ‰ºòÂÖàÈòüÂàóÔºåÂåÖÂê´Êú™ËÆøÈóÆËäÇÁÇπÂíåË∑ùÁ¶ªÂ≠óÂÖ∏‰∏≠ÂØπÂ∫îËäÇÁÇπÁöÑË∑ùÁ¶ªÔºàÁî®‰∫éDijkstraÁÆóÊ≥ïÔºâ\nint main() { // ...ÁúÅÁï•‰∫ÜmainÂáΩÊï∞‰∏≠ÁöÑ‰ª£Á†Å.