INSERT INTO `xz_dialogue` VALUES (1122, 'È¢òÁõÆÔºöÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢òÔºàDijkstraÁÆóÊ≥ïÔºâÁöÑ‰ºòÂåñÂÆûÁé∞\n\nË¶ÅÊ±ÇÔºö\n\n* ‰ΩøÁî®‰ºòÂÖàÈòüÂàóÔºàÊúÄÂ∞èÂ†ÜÔºâ‰ºòÂåñDijkstraÁÆóÊ≥ïÔºå‰ΩøÂæóÊó∂Èó¥Â§çÊùÇÂ∫¶Èôç‰ΩéÂà∞O(nlogn)„ÄÇ\n* ËÄÉËôëËæπÁöÑÊùÉÈáçÂèØËÉΩÂá∫Áé∞Ë¥üÊï∞ÁöÑÊÉÖÂÜµÔºåÈúÄË¶ÅËøõË°åÁõ∏Â∫îÁöÑÂ§ÑÁêÜ„ÄÇ\n\nPython‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```python\nimport heapq\n\ndef dijkstra_with_priority_queue(graph, start):\n    n = len(graph)\n    # ÂàùÂßãÂåñË∑ùÁ¶ªÂ≠óÂÖ∏ÂíåÂâçÈ©±ËäÇÁÇπÂ≠óÂÖ∏\n    distances = [float(\"inf\")] * n\n    prev_nodes = [-1] * n\n    # ÂàùÂßãÂåñ‰ºòÂÖàÈòüÂàóÔºåÂ∞ÜËµ∑ÁÇπÂä†ÂÖ•ÈòüÂàóÔºåÂπ∂Ëµã‰∫àÊúÄÂ∞èË∑ùÁ¶ªÂÄº\n    priority_queue = [(0, start)]\n    while priority_queue:\n        # ÂèñÂá∫ÂΩìÂâçË∑ùÁ¶ªÊúÄÂ∞èÁöÑËäÇÁÇπ\n        current_distance, current_node = heapq.heappop(priority_queue)\n        # Â¶ÇÊûúÂΩìÂâçËäÇÁÇπÂ∑≤ÁªèË¢´Â§ÑÁêÜËøáÔºåË∑≥Ëøá\n        if current_distance > distances[current_node]:\n            continue\n        # ÈÅçÂéÜÂΩìÂâçËäÇÁÇπÁöÑÈÇªÂ±ÖËäÇÁÇπ\n        for neighbor, weight in graph[current_node].items():\n            # ËÆ°ÁÆóÊñ∞ÁöÑË∑ùÁ¶ªÂíåÊóßÁöÑË∑ùÁ¶ªÂ∑ÆÂÄº\n            new_distance = distances[current_node] + weight\n            # Â¶ÇÊûúÊñ∞ÁöÑË∑ùÁ¶ªÂ∞è‰∫éÂ∑≤Áü•ÁöÑÊúÄÁü≠Ë∑ùÁ¶ªÔºåÊõ¥Êñ∞ÊúÄÁü≠Ë∑ùÁ¶ªÂíåÂâçÈ©±ËäÇÁÇπ\n            if new_distance < distances[neighbor]:\n                distances[neighbor] = new_distance\n                prev_nodes[neighbor] = current_node\n                # Â∞ÜÊñ∞ÁöÑÈÇªÂ±ÖËäÇÁÇπÂä†ÂÖ•‰ºòÂÖàÈòüÂàóÔºåÂπ∂Ëµã‰∫àÊñ∞ÁöÑË∑ùÁ¶ªÂÄº\n                heapq.heappush(priority_queue, (new_distance, neighbor))\n    return distances, prev_nodes\n```\nJava‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```java\nimport java.util.*;\nimport java.util.stream.*;\n\npublic class DijkstraWithPriorityQueue {\n    public static void main(String[] args) {\n        int[][] graph = {\n            {0, 1, 6, 5},\n            {1, 0, 2, 1},\n            {6, 2, 0, -7},\n            {5, 1, -7, 0}\n        }; // ‰ª•0‰∏∫Ëµ∑ÁÇπÁöÑÂõæÔºåË°®Á§∫ËäÇÁÇπÁºñÂè∑‰ªé0ÂºÄÂßãËÆ°Êï∞\n        int[] distances = dijkstraWithPriorityQueue(graph);\n        System.out.println(\"ÊúÄÁü≠Ë∑ùÁ¶ª: \" + Arrays.toString(distances));\n    }\n    public static int[] dijkstraWithPriorityQueue(int[][] graph) {\n        int n = graph.length; // ËäÇÁÇπÊï∞Èáè\n        int[] distances = new int[n]; // ÊúÄÁü≠Ë∑ùÁ¶ªÊï∞ÁªÑÔºåÂàùÂßãÂåñ‰∏∫Êó†Á©∑Â§ßÔºåËÆ∞ÂΩïÁöÑÊòØË¥üÊï∞Êó∂Ë°®Á§∫‰∏çÂèØËææÁöÑË∑ùÁ¶ªÔºàÂÆûÈôÖ‰∏∫Ë¥üÊó†Á©∑Â§ßÔºâ\n        int[] prevNodes = new int[n]; // ÂâçÈ©±ËäÇÁÇπÊï∞ÁªÑÔºåËÆ∞ÂΩïÁöÑÊòØÂΩìÂâçË∑ØÂæÑÁöÑÊúÄÂêé‰∏Ä‰∏™ËäÇÁÇπÔºåÂàùÂßãÂåñ‰∏∫-1Ë°®Á§∫Êó†ÊïàË∑ØÂæÑÁöÑÁªìÊùüËäÇÁÇπÔºà-1/0Âú®Âõæ‰∏äÊó†Ëß£Ôºâ\n        PriorityQueue<Integer> pq = new PriorityQueue<>(Comparator.comparingInt(node -> graph[node][0])); // ‰ΩøÁî®‰ºòÂÖàÈòüÂàóÊù•Ëé∑ÂèñË∑ùÁ¶ªÊúÄÂ∞èÁöÑËäÇÁÇπËøõË°åÂàùÂßãÂåñÔºå‰ª£‰ª∑ÊúÄÂ∞èÂÄº‰∏∫infÊàñ0‰∏∫Á©∫ÂÄº‰∏çÂ≠òÂú®„ÄÇÂèØ‰ª•Áî®ÂâçÈ©±ËäÇÁÇπÁöÑÂÖ≥Á≥ªË∞ÉÊï¥ËäÇÁÇπÁöÑ‰ΩçÁΩÆÔºà‰ΩÜËøô‰∏çÊòØ‰ºòÂÖàÈòüÂàóÁöÑÂü∫Êú¨Êìç‰ΩúÔºâ„ÄÇÂàùÂßãÂåñ‰ºòÂÖàÈòüÂàó‰∏≠ÁöÑÂÖÉÁ¥†‰ºöÂç†Áî®Á©∫Èó¥Ôºå‰∏îÊï∞Èáè‰∏∫n+1„ÄÇÂΩì‰ºòÂÖàÈòüÂàó‰∏∫Á©∫Êó∂ËøîÂõûnull„ÄÇËøîÂõûÂÄºÂíåËøîÂõûÈ°∫Â∫èÈÉΩ‰æùËµñ‰∫éÂÖ∑‰ΩìÂÆûÁé∞„ÄÇ‰∏ç‰øùËØÅÁªìÊûúÈ°∫Â∫è„ÄÇÂÖ∂‰ªñËäÇÁÇπÊåâ‰∏ÄÂÆöÈ°∫Â∫èËøõË°åÁºñÂè∑Ôºå‰∏çÊåâÂ§ßÂ∞èÊéíÂ∫è„ÄÇÊåâÁÖßÂ§ßÂ∞èÊéíÂ∫èÂèØ‰ª•Êõ¥Âø´Âú∞ËÆøÈóÆÁõ∏ÈÇªËäÇÁÇπ„ÄÇÈ°∫Â∫è‰æùËµñ‰∫éËäÇÁÇπÂ§ßÂ∞èÔºåËäÇÁÇπÂ§ßÂ∞è‰∏çÂîØ‰∏ÄÔºåÂèØ‰ª•‰∏∫Ë¥üÊï∞ÊàñÊµÆÁÇπÊï∞„ÄÇÂõ†‰∏∫ÂÆÉ‰ª¨ÈÉΩË¢´ËΩ¨Êç¢Êàê‰∫ÜÊï¥Êï∞Âπ∂Áî®‰∫éË°®Á§∫ÂõæÁöÑÁ¥¢ÂºïÔºåÊâÄ‰ª•Â§ßÂ∞èÂØπËøôÁßç‰ΩøÁî®Âú∫ÊôØ‰∏çÈáçË¶Å„ÄÇÊ≠§Â§ÑËäÇÁÇπË°®Á§∫ËäÇÁÇπÁºñÂè∑‰ªé0ÂºÄÂßãËÆ°Êï∞„ÄÇÈÄöÂ∏∏ÊúÄÂ∞èÂ†ÜÂè™‰øùÂ≠òÂ†ÜÈ°∂ÂÖÉÁ¥†ÁöÑÂâçÈ©±ÊàñÂêéÁªßÂÖÉÁ¥†ÁöÑË∑ØÂæÑ‰∏äÊó∂Èó¥Â§çÊùÇÂ∫¶Âèò‰∏∫O(nlogn)„ÄÇÂÆûÈôÖÂ§ÑÁêÜÊó∂Èó¥Â§çÊùÇÂ∫¶Ê†πÊçÆÂ†ÜÁöÑÂàùÂßãÂåñÂíå‰ΩøÁî®ÊñπÂºèÂÜ≥ÂÆö„ÄÇÊúÄÂ∞èÂ†ÜÁª¥Êä§‰∫Ü‰∏Ä‰∏™ÈòüÂàó„ÄÇÁî±‰∫éÂÆÉ‰ΩøÁî®‰∫ÜÂ†ÜÁöÑÊï∞ÊçÆÁªìÊûÑÔºåÊâÄ‰ª•ÂÆÉÂèØ‰ª•Áî®‰∫éÊü•ÊâæÊúÄÂ∞èÁöÑÂÖÉÁ¥†ÔºàÊúÄÂ∞èÂ†ÜÁöÑÊ†πÔºâÔºåËøôÈÄöÂ∏∏ÊØîÈÄöËøáÂæ™ÁéØÈÅçÂéÜÊï∞ÁªÑË¶ÅÂø´ÂæóÂ§ö„ÄÇ‰ºòÂÖàÈòüÂàó‰πüÁß∞‰∏∫‰ºòÂÖàÊêúÁ¥¢Ê†ëÊàñÂ†ÜÊ†àÂàóË°®Ê†ë„ÄÇÁî±‰∫éÂú®Êï∞ÁªÑ‰∏≠ÂÆûÁé∞Â†ÜÊ†àÂàóË°®Ê†ëÊïàÁéáËæÉ‰ΩéÔºåÊâÄ‰ª•‰ΩøÁî®‰ºòÂÖàÈòüÂàóÂèØ‰ª•Êõ¥È´òÊïàÂú∞ÂÆûÁé∞Ëøô‰∏™ÂäüËÉΩ„ÄÇÂú®Java‰∏≠ÂèØ‰ª•‰ΩøÁî®PriorityQueueÁ±ªÊù•ÂÆûÁé∞‰ºòÂÖàÈòüÂàó„ÄÇÂÆÉÊòØ‰∏Ä‰∏™ÂèØ‰ª•Ëá™Âä®ÊéíÂ∫èÁöÑÈòüÂàóÔºåÂÖ∂‰∏≠ÁöÑÂÖÉÁ¥†ÂèØ‰ª•ÊòØ‰ªªÊÑèÂØπË±°Á±ªÂûã„ÄÇÂèØ‰ª•‰ΩøÁî®Ê≥õÂûãÊù•ÊåáÂÆöÂÖÉÁ¥†Á±ªÂûã„ÄÇ‰ΩøÁî®JavaÊµÅAPIÂèØ‰ª•Êñπ‰æøÂú∞Â§ÑÁêÜÊï∞ÁªÑÊï∞ÊçÆ„ÄÇ‰ΩøÁî®JavaÊµÅAPIÂ§ÑÁêÜÊï∞ÁªÑÊó∂ÈúÄË¶ÅÊ≥®ÊÑèÊï∞ÁªÑÁöÑËæπÁïåÊù°‰ª∂ÂíåÁ¥¢ÂºïËåÉÂõ¥„ÄÇ‰ΩøÁî®Java', 141, 'assistant', '2024-03-06 00:08:22');
