INSERT INTO `xz_dialogue` VALUES (1126, 'È¢òÁõÆÔºöÊúÄÁü≠Ë∑ØÂæÑÈóÆÈ¢òÔºàDijkstraÁÆóÊ≥ïÔºâÁöÑ‰ºòÂåñÁâàÊú¨\n\nÊèèËø∞Ôºö\nÁªôÂÆö‰∏Ä‰∏™Â∏¶ÊùÉÊúâÂêëÂõæÔºåÂÖ∂‰∏≠ÊØè‰∏™ËäÇÁÇπÈÉΩÊúâ‰∏Ä‰∏™Ëµ∑ÂßãËäÇÁÇπÂíå‰∏Ä‰∏™ÁõÆÊ†áËäÇÁÇπ„ÄÇË¶ÅÊ±ÇÊâæÂà∞‰ªéËµ∑ÂßãËäÇÁÇπÂà∞ÁõÆÊ†áËäÇÁÇπÁöÑÊúÄÁü≠Ë∑ØÂæÑ„ÄÇ\n\nË¶ÅÊ±ÇÔºö\n\n* ÁÆóÊ≥ïÁöÑÊó∂Èó¥Â§çÊùÇÂ∫¶‰∏∫O(V^2)ÔºåÂÖ∂‰∏≠VÊòØÂõæ‰∏≠ËäÇÁÇπÁöÑÊï∞Èáè„ÄÇ\n* ÁÆóÊ≥ïÂ∫îËØ•‰ΩøÁî®‰ºòÂÖàÈòüÂàóÊï∞ÊçÆÁªìÊûÑÊù•‰ºòÂåñDijkstraÁÆóÊ≥ïÔºå‰ª•ÂáèÂ∞ëÊó∂Èó¥Â§çÊùÇÂ∫¶„ÄÇ\n\nPython‰ª£Á†ÅÔºö\n\n\n```python\nimport heapq\n\ndef shortest_path(graph, start, end):\n    queue = [(0, start)]  # ‰ΩøÁî®‰ºòÂÖàÈòüÂàóÂ≠òÂÇ®ËäÇÁÇπÂíåÂØπÂ∫îÁöÑË∑ùÁ¶ª\n    visited = set()  # ËÆ∞ÂΩïÂ∑≤ÁªèËÆøÈóÆËøáÁöÑËäÇÁÇπ\n    shortest_path = None  # ÊúÄÁü≠Ë∑ØÂæÑÁªìÊûú\n\n    while queue:\n        (distance, current) = heapq.heappop(queue)  # ÂèñÂá∫ÂΩìÂâçË∑ùÁ¶ªÊúÄÂ∞èÁöÑËäÇÁÇπ\n        if current in visited:  # Â¶ÇÊûúËäÇÁÇπÂ∑≤ÁªèË¢´ËÆøÈóÆËøáÔºåÂàôË∑≥Ëøá\n            continue\n        visited.add(current)  # Ê†áËÆ∞ÂΩìÂâçËäÇÁÇπ‰∏∫Â∑≤ËÆøÈóÆ\n        if current == end:  # Â¶ÇÊûúÊâæÂà∞‰∫ÜÁõÆÊ†áËäÇÁÇπÔºåÂàôÊõ¥Êñ∞ÊúÄÁü≠Ë∑ØÂæÑÁªìÊûú\n            shortest_path = distance\n            continue\n        for neighbor, weight in graph[current].items():  # ÈÅçÂéÜÂΩìÂâçËäÇÁÇπÁöÑÈÇªÂ±ÖËäÇÁÇπÂíåÂØπÂ∫îÁöÑÊùÉÈáç\n            if neighbor not in visited:  # Â¶ÇÊûúÈÇªÂ±ÖËäÇÁÇπÊú™Ë¢´ËÆøÈóÆËøáÔºåÂàôÊõ¥Êñ∞ÂÖ∂Ë∑ùÁ¶ªÂπ∂Âä†ÂÖ•ÈòüÂàó\n                heapq.heappush(queue, (distance + weight, neighbor))\n    return shortest_path if shortest_path is not None else \"Êó†Ê≥ïÂà∞ËææÁõÆÊ†áËäÇÁÇπ\"\n```\nJava‰ª£Á†ÅÔºö\n\n\n```java\nimport java.util.*;\n\npublic class DijkstraOptimized {\n    public int shortestPath(Map<Integer, Map<Integer, Integer>> graph, int start, int end) {\n        PriorityQueue<int[]> queue = new PriorityQueue<>((a, b) -> a[0] - b[0]);  // ‰ΩøÁî®‰ºòÂÖàÈòüÂàóÂ≠òÂÇ®ËäÇÁÇπÂíåÂØπÂ∫îÁöÑË∑ùÁ¶ª\n        Set<Integer> visited = new HashSet<>();  // ËÆ∞ÂΩïÂ∑≤ÁªèËÆøÈóÆËøáÁöÑËäÇÁÇπ\n        int shortestPath = -1;  // ÊúÄÁü≠Ë∑ØÂæÑÁªìÊûú\n        int[] distance = new int[graph.size()];  // Â≠òÂÇ®‰ªéËµ∑ÂßãËäÇÁÇπÂà∞ÊØè‰∏™ËäÇÁÇπÁöÑË∑ùÁ¶ª\n        Arrays.fill(distance, Integer.MAX_VALUE);  // ÂàùÂßãÂåñ‰∏∫Êó†Á©∑Â§ßÔºåË°®Á§∫Ê≤°ÊúâË∑ØÂæÑÂ≠òÂú®\n        distance[start] = 0;  // Â∞ÜËµ∑ÂßãËäÇÁÇπÁöÑË∑ùÁ¶ªËÆæÁΩÆ‰∏∫0ÔºåË°®Á§∫ÂèØ‰ª•‰ªéËµ∑ÂßãËäÇÁÇπÂà∞ËææËØ•ËäÇÁÇπ\n\n        queue.offer(new int[]{0, start});  // Â∞ÜËµ∑ÂßãËäÇÁÇπÂä†ÂÖ•ÈòüÂàó‰∏≠\n\n        while (!queue.isEmpty()) {\n            int[] node = queue.poll();  // ÂèñÂá∫ÂΩìÂâçË∑ùÁ¶ªÊúÄÂ∞èÁöÑËäÇÁÇπÂíåÂØπÂ∫îÁöÑË∑ùÁ¶ªÂÄº\n            if (visited.contains(node[1])) {  // Â¶ÇÊûúËäÇÁÇπÂ∑≤ÁªèË¢´ËÆøÈóÆËøáÔºåÂàôË∑≥Ëøá\n                continue;\n            }\n            visited.add(node[1]);  // Ê†áËÆ∞ÂΩìÂâçËäÇÁÇπ‰∏∫Â∑≤ËÆøÈóÆ\n            if (node[1] == end) {  // Â¶ÇÊûúÊâæÂà∞‰∫ÜÁõÆÊ†áËäÇÁÇπÔºåÂàôÊõ¥Êñ∞ÊúÄÁü≠Ë∑ØÂæÑÁªìÊûúÂπ∂ËøîÂõûË∑ùÁ¶ªÂÄº\n                shortestPath = node[0];\n                continue;\n            }\n            for (Map.Entry<Integer, Integer> neighbor : graph.get(node[1]).entrySet()) {  // ÈÅçÂéÜÂΩìÂâçËäÇÁÇπÁöÑÈÇªÂ±ÖËäÇÁÇπÂíåÂØπÂ∫îÁöÑÊùÉÈáçÔºàÊàñ‰ª£‰ª∑ÔºâÂπ∂Êõ¥Êñ∞Ë∑ùÁ¶ªÂÄºÔºàÂèØËÉΩÂä†Êàñ‰∏çÂä†ÊùÉÈáçÔºâ\n                int newDistance = distance[node[1]] + neighbor.getValue();  // Â¶ÇÊûúÈÇªÂ±ÖËäÇÁÇπÊú™Ë¢´ËÆøÈóÆËøáÔºåÂàôÂ∞ÜË∑ùÁ¶ªÂÄºÂä†ÊùÉÊõ¥Êñ∞Âà∞Ë∑ùÁ¶ªÊï∞ÁªÑ‰∏≠Âπ∂Âä†ÂÖ•ÈòüÂàó‰∏≠ÔºåÊ∑ªÂä†‰πãÂâçÂøÖÈ°ªÁ°Æ‰øùÊï∞ÁªÑ‰∏ç‰∏∫Á©∫‰∏îÈïøÂ∫¶Ë∂≥Â§üÂ§ßÔºàÂç≥ÈïøÂ∫¶Â§ß‰∫éÁ≠â‰∫éÈÇªÂ±ÖËäÇÁÇπÁöÑÊï∞ÈáèÔºâ\n                if (newDistance < distance[neighbor.getKey()]) {  // Â¶ÇÊûúÊõ¥Êñ∞ÂêéÁöÑË∑ùÁ¶ªÂÄºÂ∞è‰∫éÂéüË∑ùÁ¶ªÂÄºÔºåÂàôÊõ¥Êñ∞ÂéüË∑ùÁ¶ªÂÄºÂπ∂Âä†ÂÖ•ÈòüÂàó‰∏≠ÔºàÂç≥Âä†ÂÖ•‰ºòÂÖàÈòüÂàóÔºâ\n                    queue.offer(new int[]{newDistance, neighbor.getKey()});\n                } else if (newDistance == distance[neighbor.getKey()]) {  // Â¶ÇÊûúÊõ¥Êñ∞ÂêéÁöÑË∑ùÁ¶ªÂÄºÁ≠â‰∫éÂéüË∑ùÁ¶ªÂÄºÔºåËØ¥ÊòéÈúÄË¶ÅÂÜçÂà§Êñ≠‰∏ÄÊ¨°ÊòØÂê¶Â∑≤ÁªèËææÂà∞ÊúÄÂ§ßÂÖÅËÆ∏ÂÄºÔºàÂ¶ÇÊûúËææÂà∞‰∫ÜÊúÄÂ§ßÂÖÅËÆ∏ÂÄºÂ∞±ÁªßÁª≠Â∞ùËØïÂØªÊâæÊõ¥Áü≠ÁöÑË∑ØÂæÑÔºâÂê¶ÂàôÂ∞ÜËøô‰∏™ËäÇÁÇπÊ†áËÆ∞‰∏∫Â∑≤ËÆøÈóÆÔºàÂ∞ÜËäÇ