INSERT INTO `xz_dialogue` VALUES (1128, 'È¢òÁõÆÔºöÂä®ÊÄÅËßÑÂàíÁöÑÊúÄ‰ºò‰∫åÂèâÊêúÁ¥¢Ê†ëÈóÆÈ¢ò\n\nÈ¢òÁõÆÊèèËø∞Ôºö\nÁªôÂÆö‰∏ÄÁªÑÁâ©ÂìÅÂíå‰∏ÄÁªÑ‰ª∑ÂÄºÔºåÂÖ∂‰∏≠ÊØè‰∏™Áâ©ÂìÅÈÉΩÊúâ‰∏Ä‰∏™‰∏é‰πãÂÖ≥ËÅîÁöÑÊùÉÈáçÂíå‰∏Ä‰∏™‰ª∑ÂÄº„ÄÇÈóÆÈ¢òÊòØÂ∞Ü‰∏ÄÁªÑÁâ©ÂìÅÊåâÁÖßÂÖ∂‰ª∑ÂÄºËøõË°åÊéíÂ∫èÔºåÁÑ∂ÂêéÊûÑÈÄ†‰∏Ä‰∏™ÊúÄ‰ºò‰∫åÂèâÊêúÁ¥¢Ê†ëÔºàÊúÄÂ∞èÂ†ÜÔºâÔºå‰ΩøÂæóÊÄªÊùÉÈáç‰∏çË∂ÖËøáÁªôÂÆöÁöÑÊùÉÈáçÈôêÂà∂ÔºåÂêåÊó∂ÊúÄÂ§ßÂåñÂ†Ü‰∏≠Áâ©ÂìÅÁöÑÊÄª‰ª∑ÂÄº„ÄÇ\n\nPython ‰ª£Á†ÅÂÆûÁé∞Ôºö\n\n\n```python\nclass Solution:\n    def __init__(self, weights: List[int], values: List[int]):\n        self.weights = weights\n        self.values = values\n        self.heap = []\n        self.total_weight = sum(weights)\n        self.total_value = sum(values)\n\n    def insert(self, weight, value):\n        if len(self.heap) == len(self.weights) - 1:\n            return False  # ÊâÄÊúâÁâ©ÂìÅÂ∑≤ÊèíÂÖ•ÔºåÊó†Ê≥ïÊèíÂÖ•Êñ∞Áâ©ÂìÅ\n        if weight + self.heap[-1] > self.total_weight:\n            return False  # ÊÄªÊùÉÈáçË∂ÖËøáÈôêÂà∂ÔºåÊó†Ê≥ïÊèíÂÖ•Êñ∞Áâ©ÂìÅ\n        if len(self.heap) == 0 or value > self.values[len(self.heap) - 1]:\n            self.heap.append((weight, value))\n            return True\n        return False  # ÊèíÂÖ•Â§±Ë¥•ÔºåÈúÄË¶ÅÈáçÊñ∞Ë∞ÉÊï¥Â†ÜÁªìÊûÑ\n\n    def build_heap(self):\n        while len(self.heap) > 1:\n            left = self.heap[0]\n            right = self.heap[1]\n            if left[0] > right[0]:  # Â∑¶Â≠êÊ†ëÊØîÂè≥Â≠êÊ†ëÂ∞èÔºå‰∫§Êç¢‰ΩçÁΩÆÂπ∂Âà†Èô§Âè≥Â≠êÊ†ë\n                self.heap[0], self.heap[1] = self.heap[1], self.heap[0]\n                del self.heap[1]\n            else:  # Â∑¶Â≠êÊ†ëÊØîÂè≥Â≠êÊ†ëÂ∞èÊàñÁ≠â‰∫éÔºåÊâæÂà∞ÂÆÉ‰ª¨ÁöÑÊúÄÂ§ßÊùÉÂÄºÂπ∂Â∞ÜÂÖ∂Ê∑ªÂä†Âà∞Â∑¶Â≠êÊ†ë‰∏≠ÔºåÊúÄÂêé‰∫§Êç¢Ëøô‰∏§‰∏™Â≠êÊ†ëÁöÑÁ¥¢Âºï\n                idx = max((idx for (idx, item) in enumerate(self.weights)), key=itemgetter(0))\n                parent_weight, parent_value = left[0], self.values[idx] + left[1] + right[1]\n                left_weight, left_value = left[0], left[1] + parent_value\n                rig