INSERT INTO `xz_dialogue` VALUES (1134, 'È¢òÁõÆÔºöÊ±ÇËß£‰∫åÂèâÊ†ë‰∏≠ÁöÑÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÔºàLongest Increasing SubsequenceÔºåLISÔºâÈóÆÈ¢ò\n\nÊèèËø∞Ôºö\nÁªôÂÆö‰∏Ä‰∏™‰∫åÂèâÊ†ëÔºåË¶ÅÊ±ÇÊ±ÇËß£ËØ•‰∫åÂèâÊ†ë‰∏≠ÁöÑÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÁöÑÈïøÂ∫¶„ÄÇÂÅáËÆæÊ†ëÁöÑËäÇÁÇπÁªìÊûÑ‰∏∫Ôºö\n\n\n```arduino\nstruct TreeNode {\n    int val;\n    TreeNode *left;\n    TreeNode *right;\n    TreeNode(int x) : val(x), left(NULL), right(NULL) {}\n};\n```\nË¶ÅÊ±Ç‰ΩøÁî®Âä®ÊÄÅËßÑÂàíÁÆóÊ≥ïÊ±ÇËß£„ÄÇ\n\nPython‰ª£Á†ÅÔºö\n\n\n```python\nclass Solution:\n    def longestIncreasingSubsequence(self, root: TreeNode) -> int:\n        self.dp = [[0] * 5 for _ in range(5)]  # ÂàùÂßãÂåñdpÊï∞ÁªÑÔºåÁî®‰∫éËÆ∞ÂΩïÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÁöÑÈïøÂ∫¶\n        self.max_len = 0  # ÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÁöÑÈïøÂ∫¶\n        self.dfs(root)  # Ê∑±Â∫¶‰ºòÂÖàÊêúÁ¥¢\n        return self.max_len  # ËøîÂõûÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÁöÑÈïøÂ∫¶\n    \n    def dfs(self, node):\n        if not node:  # Â¶ÇÊûúËäÇÁÇπ‰∏∫Á©∫ÔºåËøîÂõû0\n            return 0\n        # ÈÄíÂΩíÊ±ÇÂ∑¶Âè≥Â≠êÊ†ëÁöÑÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÈïøÂ∫¶\n        left = self.dfs(node.left)  # dp[node.val - 1] = max(dp[node.val - 1], left)\n        right = self.dfs(node.right)  # dp[node.val] = max(dp[node.val], right)\n        # Êõ¥Êñ∞ÂΩìÂâçËäÇÁÇπÁöÑÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÈïøÂ∫¶\n        for i in range(1, 5):  # Êõ¥Êñ∞dp[node.val] = max(dp[node.val], dp[node.val - i] + i)\n            if node.val - i <= 0:  # Â¶ÇÊûúÂΩìÂâçËäÇÁÇπÁöÑÂÄºÂ∞è‰∫éÁ≠â‰∫é0ÔºåÂàôÊó†Ê≥ïÊûÑÊàêÈÄíÂ¢ûÂ∫èÂàóÔºåÁõ¥Êé•Ë∑≥Ëøá\n                continue\n            if left + i > dp[node.val - i]:  # Â¶ÇÊûúÂ∑¶Â≠êÊ†ëÁöÑÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÈïøÂ∫¶Âä†‰∏äÂΩìÂâçËäÇÁÇπÁöÑÂÄºÂ§ß‰∫édp[node.val - i]ÔºåÂàôÊõ¥Êñ∞dp[node.val]\n                dp[node.val] = left + i\n            if right + i > dp[node.val]:  # Â¶ÇÊûúÂè≥Â≠êÊ†ëÁöÑÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÈïøÂ∫¶Âä†‰∏äÂΩìÂâçËäÇÁÇπÁöÑÂÄºÂ§ß‰∫édp[node.val]ÔºåÂàôÊõ¥Êñ∞dp[node.val]\n                dp[node.val - i] = right + i\n        return max(left, right) + 1  # ÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÈïøÂ∫¶ÂèñÂ∑¶Âè≥Â≠êÊ†ëÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÈïøÂ∫¶ÁöÑÊúÄÂ§ßÂÄºÂä†1ÔºåÂÜçÂèñÊúÄÂ§ßÂÄºËøîÂõû\n```\nJava‰ª£Á†ÅÔºö\n\n\n```java\npublic class Solution {\n    public int longestIncreasingSubsequence(TreeNode root) {\n        int[] dp = new int[5];  // dpÊï∞ÁªÑÁî®‰∫éËÆ∞ÂΩïÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÁöÑÈïøÂ∫¶ÔºåÊï∞ÁªÑ‰∏ãÊ†áË°®Á§∫ÂΩìÂâçËäÇÁÇπÁöÑÂÄº‰∏éÂâç‰∏Ä‰∏™ËäÇÁÇπÁöÑÂ∑ÆÂÄºÔºådp[i]Ë°®Á§∫ÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÁöÑÈïøÂ∫¶‰∏éÂâç‰∏Ä‰∏™ËäÇÁÇπÁöÑÂ∑ÆÂÄº‰∏∫iÊó∂ÁöÑÊúÄÂ§ßÂÄº\n        int maxLen = 0;  // ÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÁöÑÈïøÂ∫¶\n        dfs(root);  // Ê∑±Â∫¶‰ºòÂÖàÊêúÁ¥¢ÔºåÂπ∂Êõ¥Êñ∞ÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÁöÑÈïøÂ∫¶ÂíådpÊï∞ÁªÑÁöÑÂÄº\n        return maxLen;  // ËøîÂõûÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÁöÑÈïøÂ∫¶\n    }\n    \n    private int dfs(TreeNode node) {\n        if (node == null) {  // Â¶ÇÊûúËäÇÁÇπ‰∏∫Á©∫ÔºåËøîÂõû0\n            return 0;\n        } else {  // Âê¶ÂàôÈÄíÂΩíÊ±ÇÂ∑¶Âè≥Â≠êÊ†ëÁöÑÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÈïøÂ∫¶ÂíåÂΩìÂâçËäÇÁÇπÁöÑÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÈïøÂ∫¶ÔºåÂπ∂Êõ¥Êñ∞dpÊï∞ÁªÑÁöÑÂÄºÂíåÊúÄÈïøÈÄíÂ¢ûÂ≠êÂ∫èÂàóÁöÑÈïøÂ∫¶\n            int left = dfs(node.left);  // dp[node.val - 1] = max(dp[node.val - 1], left)\n            int right = dfs(node.right);  // dp[node.val] = max(dp[node.val], right)\n            for (int i = 1; i < 5; i++) {  // Ê†πÊçÆÂΩìÂâçËäÇÁÇπÁöÑÂÄºÊõ¥Êñ∞dpÊï∞ÁªÑÁöÑÂÄºÔºåÊõ¥Êñ∞ÊñπÂºè‰∏éPython‰ª£Á†Å‰∏≠Áõ∏Âêå\n                if (node.val - i <= 0) {  // Â¶ÇÊûúÂΩìÂâçËäÇÁÇπÁöÑÂÄºÂ∞è‰∫éÁ≠â‰∫é0ÔºåÂàôÊó†Ê≥ïÊûÑÊàêÈÄíÂ¢ûÂ∫èÂàóÔºåÁõ¥Êé•Ë∑≥Ëøá\n                    continue;\n                } else if (left + i > dp[node.val - i]) {  // Â¶ÇÊûúÂ∑¶', 141, 'assistant', '2024-03-06 00:09:43');
