INSERT INTO `xz_dialogue` VALUES (1293, 'È¢òÁõÆÔºöÂú®‰∏Ä‰∏™Êï∞ÁªÑ‰∏≠ÊâæÂà∞‰∏§‰∏™Êï∞Ôºå‰ΩøÂæóÂÆÉ‰ª¨ÁöÑÂíåÁ≠â‰∫é‰∏Ä‰∏™ÁªôÂÆöÁöÑÁõÆÊ†áÂÄº„ÄÇ\n\nËß£Á≠îÔºö\n\n\n```cpp\n#include <iostream>\n#include <unordered_map>\n\nusing namespace std;\n\nvector<int> twoSum(vector<int>& nums, int target) {\n    unordered_map<int, int> map;\n    for (int i = 0; i < nums.size(); i++) {\n        int complement = target - nums[i];\n        if (map.count(complement)) {\n            return {map[complement], i};\n        }\n        map[nums[i]] = i;\n    }\n    return {};\n}\n\nint main() {\n    vector<int> nums = {2, 7, 11, 15};\n    int target = 9;\n    vector<int> result = twoSum(nums, target);\n    if (result.size() == 2) {\n        cout << \"The two numbers are \" << result[0] + 1 << \" and \" << result[1] + 1 << endl;\n    } else {\n        cout << \"No two numbers add up to the target.\" << endl;\n    }\n    return 0;\n}\n```\nËß£ÈáäÔºö\n\nËøô‰∏™ÈóÆÈ¢òÂèØ‰ª•‰ΩøÁî®ÂìàÂ∏åË°®Êù•Ëß£ÂÜ≥„ÄÇÊàë‰ª¨ÈÅçÂéÜÊï∞ÁªÑ‰∏≠ÁöÑÊØè‰∏™ÂÖÉÁ¥†ÔºåÂ∞ÜÂÖ∂‰∏éÁõÆÊ†áÂÄºÁõ∏ÂáèÂæóÂà∞Âè¶‰∏Ä‰∏™Êï∞ÔºåÂ¶ÇÊûúËøô‰∏™Êï∞Â∑≤ÁªèÂú®ÂìàÂ∏åË°®‰∏≠Â≠òÂú®ÔºåÈÇ£‰πàÂ∞±ÊâæÂà∞‰∫ÜËøô‰∏§‰∏™Êï∞ÔºåÂê¶ÂàôÊàë‰ª¨Â∞ÜÂΩìÂâçÊï∞Ê∑ªÂä†Âà∞ÂìàÂ∏åË°®‰∏≠Ôºå‰ª•‰æøÂêéÁª≠‰ΩøÁî®„ÄÇÊó∂Èó¥Â§çÊùÇÂ∫¶‰∏∫O(n)ÔºåÂÖ∂‰∏≠n‰∏∫Êï∞ÁªÑÁöÑÂ§ßÂ∞è„ÄÇ', 141, 'assistant', '2024-03-20 00:47:59');
