INSERT INTO `xz_dialogue` VALUES (1426, 'import sys\n\nN = 5001\nf = [[False for _ in range(N)] for _ in range(N)]\ninput = sys.stdin.readline\nfor i in range(N):\n    for j in range(N):\n        if not f[i][j]:\n            k = 1\n            while k + i < N:\n                s = 0\n                while j + s * k < N:\n                    f[i + k][j + s * k] = True\n                    s += 1\n                k += 1\n\n            k = 1\n            while k + j < N:\n                s = 0\n                while i + s * k < N:\n                    f[i + s * k][j + k] = True\n                    s += 1\n                k += 1\n\nfor _ in range(int(input())):\n    n, m = list(map(int, input().split()))\n    if f[n][m]:\n        print(\"Alice\")\n    else:\n        print(\"Bob\")\nÁøªËØëÊàêc++', 178, 'user', '2024-05-04 20:32:52');
INSERT INTO `xz_dialogue` VALUES (1427, 'ÂΩìÁÑ∂ÂèØ‰ª•„ÄÇ‰∏ãÈù¢ÊòØÂØπÂ∫îÁöÑC++‰ª£Á†Å„ÄÇËøô‰∏™‰ª£Á†ÅÂÆûÁé∞‰∫Ü‰∏Ä‰∏™Áî®‰∫éÂ§ÑÁêÜ‰∫åÁª¥ÁΩëÊ†ºÁöÑÁÆóÊ≥ïÔºåÁúãËµ∑Êù•ÂÉèÊòØÂú®È¢ÑÂ§ÑÁêÜ‰∏Ä‰∏™ÂèØ‰ª•Áî®‰∫éÂà§Êñ≠Êüê‰∏™ÁÇπÊòØÂê¶Ë¢´ÊüêÁßçÊ®°ÂºèË¶ÜÁõñÁöÑÁΩëÊ†º„ÄÇËøô‰∏™Ê®°ÂºèÁúãËµ∑Êù•ÂÉèÊòØ‰ª•ÁâπÂÆöÁöÑÊñúÁ∫øÊñπÂºèÂ°´ÂÖÖÁΩëÊ†º„ÄÇ‰πãÂêéÔºåÂØπ‰∫éËæìÂÖ•ÁöÑÊØè‰∏ÄÂØπÂùêÊ†áÔºåÂÆÉÊ£ÄÊü•Ëøô‰∫õÂùêÊ†áÂú®È¢ÑÂ§ÑÁêÜÁöÑÁΩëÊ†º‰∏≠ÁöÑÁä∂ÊÄÅÔºåÂπ∂ËæìÂá∫\"Alice\"Êàñ\"Bob\"„ÄÇ\n\n\n```cpp\n#include <iostream>\n#include <vector>\n#include <string>\n\nusing namespace std;\n\nint main() {\n    const int N = 5001;\n    vector<vector<bool>> f(N, vector<bool>(N, false));\n\n    for (int i = 0; i < N; ++i) {\n        for (int j = 0; j < N; ++j) {\n            if (!f[i][j]) {\n                int k = 1;\n                while (k + i < N) {\n                    int s = 0;\n                    while (j + s * k < N) {\n                        f[i + k][j + s * k] = true;\n                        s += 1;\n                    }\n                    k += 1;\n                }\n\n                k = 1;\n                while (k + j < N) {\n                    int s = 0;\n                    while (i + s * k < N) {\n                        f[i + s * k][j + k] = true;\n                        s += 1;\n                    }\n                    k += 1;\n                }\n            }\n        }\n    }\n\n    int t;\n    cin >> t;\n    while (t--) {\n        int n, m;\n        cin >> n >> m;\n        if (f[n][m]) {\n            cout << \"Alice\" << endl;\n        } else {\n            cout << \"Bob\" << endl;\n        }\n    }\n\n    return 0;\n}\n```\nËØ∑Ê≥®ÊÑèÔºåÁî±‰∫éËøôÊÆµ‰ª£Á†ÅÊ∂âÂèäÂà∞Â§ßÈáèÁöÑÊï∞ÁªÑËÆøÈóÆÂíåÂæ™ÁéØÔºåÊâÄ‰ª•ÂÆÉÂèØËÉΩÈúÄË¶Å‰∏Ä‰∫õÊó∂Èó¥Êù•ËøêË°åÔºåÁâπÂà´ÊòØÂú®ËæÉÂ§ßÁöÑËæìÂÖ•‰∏ä„ÄÇ‰Ω†ÂèØ‰ª•Â∞ùËØï‰ºòÂåñÂÆÉ‰ª•ÊèêÈ´òÊïàÁéáÔºå‰æãÂ¶ÇÈÄöËøáÊîπÂèòÊï∞ÊçÆÁªìÊûÑÊàñÁÆóÊ≥ï„ÄÇ‰ΩÜÊòØÔºåÂØπ‰∫éËøô‰∏™ÁâπÂÆöÁöÑ‰ª£Á†ÅÁâáÊÆµÔºåÂÆÉÂ∫îËØ•ËÉΩÂ§üÂú®ÂêàÁêÜÁöÑÊó∂Èó¥ÂÜÖÂ§ÑÁêÜÂ§ßÂ∞è‰∏∫5001ÁöÑÁΩëÊ†º„ÄÇ', 178, 'assistant', '2024-0