INSERT INTO `xz_dialogue` VALUES (1463, '‰∏∫‰∫ÜÂÆåÂñÑ`Date::AddDay(int days)`ÂáΩÊï∞ÔºåÊàë‰ª¨ÈúÄË¶ÅËÄÉËôëÂá†‰∏™Âõ†Á¥†ÔºåÂåÖÊã¨ÊØèÊúàÁöÑÂ§©Êï∞‰∏çÂêå„ÄÅÈó∞Âπ¥‰ª•ÂèäË∑®Ë∂äÂ§ö‰∏™Êúà‰ªΩÊàñÂπ¥‰ªΩÁöÑÊÉÖÂÜµ„ÄÇ‰∏ãÈù¢ÊòØ‰∏Ä‰∏™ÁÆÄÂçïÁöÑÂÆûÁé∞ÔºåÂÆÉÈÄêÊ≠•Â¢ûÂä†Â§©Êï∞ÔºåÂêåÊó∂Êõ¥Êñ∞Êó•„ÄÅÊúàÂíåÂπ¥Â±ûÊÄß„ÄÇ\n\nÈ¶ñÂÖàÔºåÂÅáËÆæ‰Ω†ÁöÑ`Date`Á±ªÂ§ßËá¥ÂÆö‰πâÂ¶Ç‰∏ãÔºö\n\n```cpp\nclass Date {\nprivate:\n    int day;\n    int month;\n    int year;\n    \n    // ËæÖÂä©ÂáΩÊï∞ÔºåÁî®‰∫éÂà§Êñ≠ÊòØÂê¶‰∏∫Èó∞Âπ¥\n    bool IsLeapYear(int y) const {\n        return (y % 4 == 0 && y % 100 != 0) || (y % 400 == 0);\n    }\n\n    // ËæÖÂä©ÂáΩÊï∞ÔºåËøîÂõûÁªôÂÆöÊúà‰ªΩÁöÑÂ§©Êï∞\n    int DaysInMonth(int m, int y) const {\n        switch (m) {\n            case 1: case 3: case 5: case 7: case 8: case 10: case 12:\n                return 31;\n            case 4: case 6: case 9: case 11:\n                return 30;\n            case 2:\n                return IsLeapYear(y) ? 29 : 28;\n            default:\n                return 0; // Êó†ÊïàÊúà‰ªΩ\n        }\n    }\n\npublic:\n    Date(int d, int m, int y) : day(d), month(m), year(y) {}\n\n    // ... ÂÖ∂‰ªñÊàêÂëòÂáΩÊï∞ ...\n\n    void AddDay(int days);\n    \n    // ... ÂèØËÉΩËøòÊúâÂÖ∂‰ªñÊàêÂëòÂáΩÊï∞ÔºåÊØîÂ¶ÇËé∑ÂèñÊó•ÊúüÁöÑÂáΩÊï∞Á≠â ...\n};\n```\n\nÁé∞Âú®ÔºåÊàë‰ª¨Êù•ÂÆûÁé∞`AddDay`ÂáΩÊï∞Ôºö\n\n```cpp\nvoid Date::AddDay(int days) {\n    while (days > 0) { // ÂèØ‰ª•Â§ÑÁêÜË¥üÊï∞ÔºåË°®Á§∫ÂêëÂâçÂáèÂ∞ëÂ§©Êï∞\n        int currentDaysInMonth = DaysInMonth(month, year);\n        \n        if (day + days <= currentDaysInMonth) {\n            // Â¶ÇÊûúÂ¢ûÂä†ÁöÑÂ§©Êï∞‰∏ç‰ºöË∂ÖÂá∫ÂΩìÂâçÊúàÁöÑÂ§©Êï∞\n            day += days;\n            days = 0; // ÂÆåÊàêÂ¢ûÂä†\n        } else {\n            // Â¶ÇÊûúÂ¢ûÂä†ÁöÑÂ§©Êï∞‰ºöË∂ÖÂá∫ÂΩìÂâçÊúàÁöÑÂ§©Êï∞\n            days -= (currentDaysInMonth - day + 1);\n            day = 1; // ËÆæÁΩÆ‰∏∫‰∏ã‰∏Ä‰∏™ÊúàÁöÑÁ¨¨‰∏ÄÂ§©\n            month++; // ËøõÂÖ•‰∏ã‰∏Ä‰∏™Êúà\n            \n            if (month > 12) {\n                // Â¶ÇÊûúÊúà‰ªΩË∂ÖËøá12ÔºåË°®Á§∫ËøõÂÖ•‰∫ÜÊñ∞ÁöÑ‰∏ÄÂπ¥\n                month = 1; // ÈáçÁΩÆÊúà‰ªΩ‰∏∫1\n                year++; // Âπ¥‰ªΩÂ¢ûÂä†\n            }\n        }\n    }\n\n    // Â¶ÇÊûúÈúÄË¶ÅÂ§ÑÁêÜÂáèÂ∞ëÂ§©Êï∞Ôºàdays < 0ÔºâÔºåÂèØ‰ª•Ê∑ªÂä†Áõ∏Â∫îÁöÑÈÄªËæë\n}\n```\n\nËøô‰∏™`AddDay`ÂáΩÊï∞ÈÄöËøáÂæ™ÁéØÈÄêÊ≠•Â¢ûÂä†Â§©Êï∞ÔºåÂêåÊó∂Ê£ÄÊü•ÊòØÂê¶ÈúÄË¶ÅË∑®Ë∂äÊúà‰ªΩÊàñÂπ¥‰ªΩ„ÄÇÊ≥®ÊÑèÔºåËøô‰∏™ÂáΩÊï∞‰πüÂèØ‰ª•Â§ÑÁêÜË¥üÊï∞ÂèÇÊï∞ÔºåÂç≥ÂêëÂâçÂáèÂ∞ëÂ§©Êï∞ÔºåÂ∞ΩÁÆ°Ëøô‰∏™ÂÖ∑‰ΩìÂÆûÁé∞‰∏ªË¶ÅÂÖ≥Ê≥®Â¢ûÂä†Â§©Êï∞ÁöÑÊÉÖÂÜµ„ÄÇÂ¶ÇÊûú‰Ω†ÈúÄË¶ÅÂ§ÑÁêÜÂáèÂ∞ëÂ§©Êï∞ÁöÑÈÄªËæëÔºåÂèØ‰ª•Âú®`while`Âæ™ÁéØ‰∏≠Ê∑ªÂä†Áõ∏Â∫îÁöÑ‰ª£Á†ÅÊù•Â§ÑÁêÜ`days < 0`ÁöÑÊÉÖÂÜµ„ÄÇ\n\nËØ∑Á°Æ‰øùÂú®ÂÆûÈôÖ‰ΩøÁî®ÂâçÂØπ‰Ω†ÁöÑ`Date`Á±ªËøõË°åÂÖÖÂàÜÁöÑÊµãËØïÔºå‰ª•Á°Æ‰øùÂÖ∂Ê≠£Á°ÆÊÄßÂíåÂÅ•Â£ÆÊÄß„ÄÇ', 186, 'assistant', '2024-05-11 11:16:33');
