INSERT INTO `xz_dialogue` VALUES (1493, '‰∏ãÈù¢ÊòØ‰∏Ä‰∏™Êõ¥‰∏∫Á≤æÁ°ÆÂíåÂÆåÂñÑÁöÑC++‰ª£Á†ÅÁ§∫‰æãÔºåËØ•‰ª£Á†Å‰ΩøÁî®‰∫ÜÁ±ª‰∏éÂØπË±°„ÄÅÁªßÊâø‰ª•ÂèäËøêÁÆóÁ¨¶ÈáçËΩΩÊù•ËÆ°ÁÆóË∑ùÁ¶ª‰Ω†ÁöÑÁîüÊó•ËøòÊúâÂ§öÂ∞ëÂ§©„ÄÇËøô‰∏™‰æãÂ≠ê‰∏≠ÔºåÊàë‰ª¨ÂÆö‰πâ‰∫Ü‰∏Ä‰∏™`Date`Âü∫Á±ªÔºåÁî®‰∫éË°®Á§∫Êó•ÊúüÔºåÂπ∂Ê¥æÁîü‰∫Ü‰∏Ä‰∏™`BirthdayCalculator`Á±ªÔºåÁî®‰∫éËÆ°ÁÆóË∑ùÁ¶ªÁîüÊó•ÁöÑÂ§©Êï∞„ÄÇ\n\n```cpp\n#include <iostream>\n#include <vector>\n\n// Âü∫Á±ª DateÔºåË°®Á§∫Êó•Êúü\nclass Date {\npublic:\n    Date(int y, int m, int d) : year(y), month(m), day(d) {}\n\n    int getYear() const { return year; }\n    int getMonth() const { return month; }\n    int getDay() const { return day; }\n\n    // ËæìÂá∫Êó•Êúü\n    void print() const {\n        std::cout << year << \"-\" << month << \"-\" << day << std::endl;\n    }\n\n    // Âà§Êñ≠ÊòØÂê¶‰∏∫Èó∞Âπ¥\n    bool isLeapYear() const {\n        return (year % 4 == 0 && year % 100 != 0) || (year % 400 == 0);\n    }\n\n    // Ëé∑ÂèñÊüê‰∏™ÊúàÁöÑÂ§©Êï∞\n    static int getDaysInMonth(int month, bool leapYear) {\n        std::vector<int> days = {31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};\n        if (leapYear) days[1] = 29;\n        return days[month - 1];\n    }\n\nprivate:\n    int year, month, day;\n};\n\n// Ê¥æÁîüÁ±ª BirthdayCalculatorÔºåÁî®‰∫éËÆ°ÁÆóË∑ùÁ¶ªÁîüÊó•ÁöÑÂ§©Êï∞\nclass BirthdayCalculator : public Date {\npublic:\n    BirthdayCalculator(int y, int m, int d) : Date(y, m, d) {}\n\n    // ËÆ°ÁÆóË∑ùÁ¶ªÂΩìÂâçÊó•ÊúüÁöÑÂ§©Êï∞\n    int daysUntilBirthday(const Date& currentDate) const {\n        Date nextBirthday = getNextBirthday(currentDate);\n        return (nextBirthday.getYear() - currentDate.getYear()) * 365 +\n               (nextBirthday.getMonth() - currentDate.getMonth()) * 30 +\n               (nextBirthday.getDay() - currentDate.getDay());\n    }\n\nprivate:\n    // Ëé∑Âèñ‰∏ã‰∏Ä‰∏™ÁîüÊó•Êó•Êúü\n    Date getNextBirthday(const Date& currentDate) const {\n        Date nextBirthday = Date(currentDate.getYear(), getMonth(), getDay());\n        if (nextBirthday < currentDate) { // Â¶ÇÊûúÁîüÊó•Â∑≤ËøáÔºåÂàôËÆ°ÁÆó‰∏ã‰∏ÄÂπ¥ÁöÑÁîüÊó•\n            nextBirthday.year += 1;\n        }\n        return nextBirthday;\n    }\n\n    // ÈáçËΩΩ < ËøêÁÆóÁ¨¶ÔºåÁî®‰∫éÊØîËæÉ‰∏§‰∏™Êó•Êúü\n    friend bool operator<(const Date& lhs, const Date& rhs) {\n        if (lhs.getYear() < rhs.getYear()) return true;\n        if (lhs.getYear() == rhs.getYear() && lhs.getMonth() < rhs.getMonth()) return true;\n        if (lhs.getYear() == rhs.getYear() && lhs.getMonth() == rhs.getMonth() && lhs.getDay() < rhs.getDay()) return true;\n        return false;\n    }\n};\n\nint main() {\n    // ËÆæÁΩÆÁîüÊó•Êó•Êúü\n    BirthdayCalculator birthday(1990, 3, 17);\n\n    // ËÆæÁΩÆÂΩìÂâçÊó•Êúü\n    Date today(2023, 3, 1); // ÂÅáËÆæ‰ªäÂ§©ÊòØ 2023 Âπ¥ 3 Êúà 1 Êó•\n\n    // ËÆ°ÁÆóË∑ùÁ¶ªÁîüÊó•ÁöÑÂ§©Êï∞\n    int daysUntil = birthday.daysUntilBirthday(today);\n\n    // ËæìÂá∫ÁªìÊûú\n    std::cout << \"Ë∑ùÁ¶ª‰Ω†ÁöÑÁîüÊó•ËøòÊúâ \" << daysUntil << \" Â§©\" << std::endl;\n\n    return 0;\n}\n```\n\nËØ∑Ê≥®ÊÑèÔºåËøô‰∏™‰ª£Á†ÅÁ§∫‰æã‰∏≠ÁöÑ`daysUntilBirthday`ÊñπÊ≥ïÁÆÄÂåñ‰∫ÜÂ§©Êï∞ÁöÑËÆ°ÁÆóÔºå‰ªÖ‰Ωú‰∏∫ÊºîÁ§∫„ÄÇÂú®ÂÆûÈôÖÂ∫îÁî®‰∏≠Ôºå‰Ω†ÂèØËÉΩÈúÄË¶Å‰ΩøÁî®Êõ¥Á≤æÁ°ÆÁöÑÊñπÊ≥ïÊù•ÈÄêÊó•ËÆ°ÁÆó‰ªäÂ§©Âà∞‰∏ã‰∏Ä‰∏™ÁîüÊó•‰πãÈó¥ÁöÑÂ§©Êï∞ÔºåËÄÉËôëÊØè‰∏™ÊúàÂÖ∑‰ΩìÊúâÂ§öÂ∞ëÂ§©‰ª•ÂèäÈó∞Âπ¥ÁöÑÊÉÖÂÜµ„ÄÇ\n\nÊ≠§Â§ñÔºå‰ª£Á†Å‰∏≠`Date`Á±ªÊèê‰æõ‰∫ÜÂà§Êñ≠Èó∞Âπ¥ÂíåËé∑ÂèñÊüêÊúàÂ§©Êï∞ÁöÑÊñπÊ≥ïÔºåËÄå`BirthdayCalculator`Á±ªÂàôË¥üË¥£Ê†πÊçÆÂΩìÂâçÊó•ÊúüËÆ°ÁÆóÂà∞‰∏ã‰∏Ä‰∏™ÁîüÊó•ÁöÑÂ§©Êï∞„ÄÇÊàë‰ª¨ÈáçËΩΩ‰∫Ü`<`ËøêÁÆóÁ¨¶Êù•ÊØîËæÉ‰∏§‰∏™`Date`ÂØπË±°Ôºå‰ª•‰æøÂú®`getNextBirthday`ÊñπÊ≥ï‰∏≠‰ΩøÁî®„ÄÇ', 197, 'assistant', '2024-05-21 01:05:05');
