INSERT INTO `xz_dialogue` VALUES (1495, '‰ª•‰∏ãÊòØ‰∏Ä‰∏™ÁÆÄÂçïÁöÑC++Á®ãÂ∫èÔºåÂÆÉ‰ΩøÁî®Á±ª‰∏éÂØπË±°„ÄÅÁªßÊâøÂíåËøêÁÆóÁ¨¶ÈáçËΩΩÊù•ËÆ°ÁÆóË∑ùÁ¶ª‰Ω†ÁöÑÁîüÊó•ËøòÊúâÂ§öÂ∞ëÂ§©„ÄÇ‰∏∫‰∫ÜÁÆÄÂåñÔºåÊàë‰ª¨ÂÅáËÆæÁîüÊó•ÊòØÊØèÂπ¥ÁöÑÂõ∫ÂÆöÊó•ÊúüÔºåÂπ∂‰∏î‰∏çËÄÉËôëÈó∞Âπ¥ÁöÑÂΩ±Âìç„ÄÇ\n\n\n```cpp\n#include <iostream>\n#include <ctime>\n\n// Âü∫Á°ÄÊó•ÊúüÁ±ª\nclass Date {\nprotected:\n    int year, month, day;\npublic:\n    Date(int y, int m, int d) : year(y), month(m), day(d) {}\n\n    // ËøêÁÆóÁ¨¶ÈáçËΩΩÔºåËÆ°ÁÆó‰∏§‰∏™Êó•Êúü‰πãÈó¥ÁöÑÂ∑ÆÂÄºÔºàÂ§©Êï∞Ôºâ\n    int operator-(const Date& other) const {\n        struct std::tm a = {0, 0, 0, day, month - 1, year - 1900};\n        struct std::tm b = {0, 0, 0, other.day, other.month - 1, other.year - 1900};\n        time_t x = std::mktime(&a);\n        time_t y = std::mktime(&b);\n        return (int)std::difftime(y, x) / (60 * 60 * 24);\n    }\n\n    void print() const {\n        std::cout << year << \"-\" << month << \"-\" << day << std::endl;\n    }\n};\n\n// ÁªßÊâøËá™DateÁ±ªÔºåÊ∑ªÂä†Ëé∑ÂèñÂΩìÂâçÊó•ÊúüÁöÑÂäüËÉΩ\nclass CurrentDate : public Date {\npublic:\n    CurrentDate() : Date(getYear(), getMonth(), getDay()) {}\n\n    static int getYear() {\n        return std::localtime(std::time(nullptr))->tm_year + 1900;\n    }\n\n    static int getMonth() {\n        return std::localtime(std::time(nullptr))->tm_mon + 1;\n    }\n\n    static int getDay() {\n        return std::localtime(std::time(nullptr))->tm_mday;\n    }\n};\n\nint main() {\n    // ËÆæÁΩÆ‰Ω†ÁöÑÁîüÊó•Êó•Êúü\n    Date birthday(1990, 5, 15); // ‰æãÂ¶ÇÔºö1990Âπ¥5Êúà15Êó•\n    CurrentDate today; // Ëé∑ÂèñÂΩìÂâçÊó•Êúü\n\n    if (today < birthday) {\n        // Â¶ÇÊûúÂΩìÂâçÊó•ÊúüÂ∞è‰∫éÁîüÊó•ÔºàÂç≥ÁîüÊó•Â∑≤ÁªèËøá‰∫ÜÔºâÔºåÂàôËÆ°ÁÆóÂà∞‰∏ã‰∏ÄÂπ¥ÁöÑÁîüÊó•ËøòÊúâÂ§öÂ∞ëÂ§©\n        Date nextBirthday(today.getYear() + 1, birthday.month, birthday.day);\n        int daysLeft = nextBirthday - today;\n        std::cout << \"Ë∑ùÁ¶ªÊàëÁöÑ‰∏ã‰∏Ä‰∏™ÁîüÊó•ËøòÊúâ \" << daysLeft << \" Â§©\" << std::endl;\n    } else {\n        // Â¶ÇÊûúÂΩìÂâçÊó•ÊúüÂ§ß‰∫éÊàñÁ≠â‰∫éÁîüÊó•ÔºàÂç≥ÁîüÊó•ËøòÊ≤°Âà∞ÔºâÔºåÂàôËÆ°ÁÆóÂà∞‰ªäÂπ¥ÁöÑÁîüÊó•ËøòÊúâÂ§öÂ∞ëÂ§©\n        int daysLeft = birthday - today;\n        if (daysLeft < 0) {\n            // Â¶ÇÊûú‰ªäÂπ¥ÁöÑÁîüÊó•Â∑≤ÁªèËøá‰∫ÜÔºåÂàôËÆ°ÁÆóÂà∞ÊòéÂπ¥ÁöÑÁîüÊó•ËøòÊúâÂ§öÂ∞ëÂ§©\n            Date nextBirthday(today.getYear() + 1, birthday.month, birthday.day);\n            daysLeft = nextBirthday - today;\n        }\n        std::cout << \"Ë∑ùÁ¶ªÊàëÁöÑÁîüÊó•ËøòÊúâ \" << daysLeft << \" Â§©\" << std::endl;\n    }\n\n    return 0;\n}\n```\n**Ê≥®ÊÑè**Ôºö\n\n1. Ëøô‰∏™Á®ãÂ∫è‰ΩøÁî®‰∫ÜC++Ê†áÂáÜÂ∫ì‰∏≠ÁöÑ`<ctime>`Êù•Â§ÑÁêÜÊó•ÊúüÂíåÊó∂Èó¥„ÄÇÂÆÉ‰ΩøÁî®`std::mktime`Âíå`std::difftime`ÂáΩÊï∞Êù•ËÆ°ÁÆó‰∏§‰∏™Êó•Êúü‰πãÈó¥ÁöÑÂ§©Êï∞Â∑Æ„ÄÇ\n2. ‰∏∫‰∫ÜÁÆÄÂåñÁ®ãÂ∫èÔºåÊàëÂÅáËÆæ‰∫ÜÊØèÂπ¥ÁöÑÁîüÊó•ÈÉΩÊòØÂõ∫ÂÆöÁöÑÔºåÊ≤°ÊúâËÄÉËôëÈó∞Âπ¥ÁöÑÊÉÖÂÜµ„ÄÇÂú®ÂÆûÈôÖÂ∫îÁî®‰∏≠Ôºå‰Ω†ÂèØËÉΩÈúÄË¶Å‰ΩøÁî®Êõ¥Â§çÊùÇÁöÑÊó•ÊúüÂ§ÑÁêÜÂ∫ìÊù•ÂáÜÁ°ÆÂú∞Â§ÑÁêÜËøô‰∫õÊÉÖÂÜµ„ÄÇ\n3. Á®ãÂ∫è‰∏≠ÁöÑ`CurrentDate`Á±ªÁªßÊâøËá™`Date`Á±ªÔºåÂπ∂Ê∑ªÂä†‰∫ÜËé∑ÂèñÂΩìÂâçÊó•ÊúüÁöÑÂäüËÉΩ„ÄÇËøôÂÖÅËÆ∏‰Ω†Êñπ‰æøÂú∞ËÆ°ÁÆó‰∏éÂΩìÂâçÊó•ÊúüÁöÑÂ∑ÆÂÄº„ÄÇ\n4. Âú®`main`ÂáΩÊï∞‰∏≠Ôºå‰Ω†ÂèØ‰ª•ËÆæÁΩÆËá™Â∑±ÁöÑÁîüÊó•Êó•ÊúüÔºåÂπ∂Êü•ÁúãË∑ùÁ¶ª‰∏ã‰∏Ä‰∏™ÁîüÊó•ËøòÊúâÂ§öÂ∞ëÂ§©„ÄÇÁ®ãÂ∫è‰ºöÊ†πÊçÆÂΩìÂâçÊó•Êúü‰∏éÁîüÊó•ÁöÑÊó•ÊúüÂÖ≥Á≥ªËøõË°åÁõ∏Â∫îÁöÑËÆ°ÁÆó„ÄÇ', 198, 'assistant', '2024-05-21 08:16:42');
