INSERT INTO `xz_dialogue` VALUES (1574, 'for _ in range(int(input())):\n    n, k = list(map(int, input().split()))\n    a = list(map(int, input().split()))\n    g = [[] for _ in range(n + 1)]\n    c = 0\n    for _ in range(n - 1):\n        f, t = list(map(int, input().split()))\n        g[f].append(t)\n        g[t].append(f)\n    v = 0\n    for i in range(n):\n        v ^= a[i]\n    if v == 0:\n        print(\"YES\")\n        continue\n    elif k == 2:\n        print(\"NO\")\n        continue\n\n\n    def dfs(i: int, fa: int) -> int:\n        global c\n        cur = a[i - 1]\n        if cur == v:\n            c += 1\n            cur = 0\n        for nx in g[i]:\n            if nx != fa:\n                cur ^= dfs(nx, i)\n                if cur == v:\n                    c += 1\n                    cur = 0\n        return cur\n\n\n    dfs(1, -1)\n    print(\"YES\" if c >= 3 else \"NO\")\nÊîπÊàêC++‰ª£Á†Å', 208, 'user', '2024-06-20 11:15:48');
INSERT INTO `xz_dialogue` VALUES (1575, '‰∏ãÈù¢ÊòØÂ∞ÜÊÇ®ÁªôÂá∫ÁöÑPython‰ª£Á†ÅÊîπÂÜô‰∏∫C++ÁöÑÁâàÊú¨„ÄÇËØ∑Ê≥®ÊÑèÔºåC++‰ª£Á†ÅÁöÑÁªìÊûÑÂíåËØ≠Ê≥ï‰∏éPythonÊúâÂæàÂ§ß‰∏çÂêåÔºåÂõ†Ê≠§ÊàëËøõË°å‰∫ÜÂøÖË¶ÅÁöÑË∞ÉÊï¥‰ª•Á°Æ‰øùÈÄªËæëÂíåÂäüËÉΩÁöÑ‰∏ÄËá¥ÊÄß„ÄÇ\n\n```cpp\n#include <iostream>\n#include <vector>\n#include <cstdlib>\nusing namespace std;\n\nvoid dfs(int i, int fa, const vector<vector<int>>& g, const vector<int>& a, int v, int& c, int& cur) {\n    cur = a[i];\n    if (cur == v) {\n        c++;\n        cur = 0;\n    }\n    for (int nx : g[i]) {\n        if (nx != fa) {\n            cur ^= dfs(nx, i, g, a, v, c, cur);\n            if (cur == v) {\n                c++;\n                cur = 0;\n            }\n        }\n    }\n}\n\nint main() {\n    int t, n, k, f, tt;\n    cin >> t;\n    while (t--) {\n        cin >> n >> k;\n        vector<int> a(n);\n        for (int i = 0; i < n; ++i) {\n            cin >> a[i];\n        }\n        vector<vector<int>> g(n + 1);\n        for (int i = 0; i < n - 1; ++i) {\n            cin >> f >> tt;\n            g[f].push_back(tt);\n            g[tt].push_back(f);\n        }\n\n        int v = 0;\n        for (int i = 0; i < n; ++i) {\n            v ^= a[i];\n        }\n        \n        int c = 0; // Number of times v is found as a subarray xor\n        int cur; // Current xor value during DFS\n        dfs(1, -1, g, a, v, c, cur);\n        \n        if (v == 0) {\n            cout << \"YES\" << endl;\n        } else if (k == 2) {\n            cout << \"NO\" << endl;\n        } else {\n            cout << (c >= 3 ? \"YES\" : \"NO\") << endl;\n        }\n    }\n    return 0;\n}\n```\n\nÂú®ËøôÊÆµC++‰ª£Á†Å‰∏≠ÔºåÊàë‰ΩøÁî®‰∫Ü`vector`Êù•Â≠òÂÇ®ÂõæÂíåÊï∞ÁªÑÔºåÂõ†‰∏∫ÂÆÉ‰ª¨Âú®C++‰∏≠ÊòØÂä®ÊÄÅÊï∞ÁªÑÔºåÂèØ‰ª•ÂæàÊñπ‰æøÂú∞Ë∞ÉÊï¥Â§ßÂ∞è„ÄÇÊàëËøò‰ΩøÁî®ÂºïÁî®‰º†ÈÄíÊù•‰øÆÊîπ`c`Âíå`cur`ÁöÑÂÄºÔºåËøôÊòØC++‰∏≠Â∏∏Áî®ÁöÑÂÅöÊ≥ïÔºåÂõ†‰∏∫ÂÆÉÂèØ‰ª•ÈÅøÂÖçÂ§çÂà∂Â§ßÂûãÊï∞ÊçÆÁªìÊûÑÔºåÂπ∂ÂÖÅËÆ∏ÂáΩÊï∞‰øÆÊîπÂÖ∂Ë∞ÉÁî®ËÄÖ‰º†ÂÖ•ÁöÑÊï∞ÊçÆ„ÄÇ\n\nËØ∑Ê≥®ÊÑèÔºåÊÇ®ÈúÄË¶ÅÂú®ÁºñËØëÁéØÂ¢É‰∏≠ÂåÖÂê´ÂøÖË¶ÅÁöÑÂ§¥Êñá‰ª∂ÔºåÂπ∂‰∏îÂèØËÉΩÈúÄË¶ÅÊ†πÊçÆÊÇ®ÁöÑÂÖ∑‰ΩìÁéØÂ¢ÉË∞ÉÊï¥‰∏Ä‰∫õÁªÜËäÇÔºà‰æãÂ¶ÇÔºåËæìÂÖ•ËæìÂá∫ÁöÑÊñπÂºèÔºâ„ÄÇÊ≠§Â§ñÔºåÂú®ÂÆûÈôÖ‰ΩøÁî®‰∏≠ÔºåËøòÂ∫îËØ•Ê∑ªÂä†ÈîôËØØÊ£ÄÊü•ÂíåÂºÇÂ∏∏Â§ÑÁêÜÊù•ÊèêÈ´ò‰ª£Á†ÅÁöÑÂÅ•Â£ÆÊÄß„ÄÇ', 208, 'assistant', '2024-06-20 11:15:48');
