INSERT INTO `xz_dialogue` VALUES (1636, 'from typing import List\n\nn = int(input())\ng = [[False for _ in range(n)] for _ in range(n)]\nans = []\n\n\ndef check(i, j):\n    ok = True\n    i0, j0 = i, j\n    for y in range(n):\n        if j == y: continue\n        if g[i][y]: ok = False\n    for x in range(n):\n        if i == x: continue\n        if g[x][j]: ok = False\n    for u in range(min(i, j), 0, -1):\n        if g[i - u][j - u]: ok = False\n    for d in range(max(i, j) + 1, n):\n        v = d - max(i, j)\n        if g[i + v][j + v]: ok = False\n    while 0 <= i < n and 0 <= j < n:\n        i -= 1\n        j += 1\n        if 0 <= i < n and 0 <= j < n and g[i][j]:\n            ok = False\n    i, j = i0, j0\n    while 0 <= i < n and 0 <= j < n:\n        i += 1\n        j -= 1\n        if 0 <= i < n and 0 <= j < n and g[i][j]:\n            ok = False\n    return ok\n\n\ndef dfs(i, j, cur: List):\n    if i == n - 1 and j == n - 1:\n        if len(cur) == n:\n            ans.append(list(cur))\n        return\n    f = check(i, j)\n    if f:\n        cur.append([i + 1, j + 1])\n        g[i][j] = True\n        if j + 1 < n:\n            dfs(i, j + 1, cur)\n        else:\n            dfs(i + 1, 0, cur)\n        g[i][j] = False\n        cur.pop(-1)\n    if j + 1 < n:\n        dfs(i, j + 1, cur)\n    else:\n        dfs(i + 1, 0, cur)\n\n\ndfs(0, 0, [])\nfor i in range(3):\n    for arr in ans[i]:\n        print(arr[1], end=\" \")\n    print()\nprint(len(ans))\nËøôÊòØÊàëÂÜôÁöÑÂÖ´ÁöáÂêéÔºåÂ∏ÆÊàëÁúãÁúãÂì™ÈáåÊúâÈóÆÈ¢ò', 223, 'user', '2024-07-02 23:06:12');
