INSERT INTO `xz_dialogue` VALUES (1668, 'class Trie:\n    def __init__(self):\n        self.fail = None\n        self.children = [None] * 26\n        self.cnt = 0\n\n\nroot = Trie()\n\n\ndef build(s: str):\n    cur = root\n    for c in s:\n        nx = ord(c) - ord(\'a\')\n        if cur.children[nx] is None:\n            cur.children[nx] = Trie()\n        cur = cur.children[nx]\n    cur.cnt += 1\n\n\ndef build_fail():\n    q = []\n    for i in range(26):\n        if root.children[i] is not None:\n            root.children[i].fail = root\n            q.append(root.children[i])\n    while q:\n        cur = q.pop(0)\n        for i in range(26):\n            if cur.children[i] is not None:\n                cur.children[i].fail = cur.fail.children[i] if cur.fail else root\n                q.append(cur.children[i])\n\n\ndef ac_query(s: str):\n    cur = root\n    cnt = 0\n    for c in s:\n        if cur.children[ord(c) - ord(\'a\')]:\n            cur = cur.children[ord(c) - ord(\'a\')]\n            cnt += cur.cnt\n            cur.cnt = 0\n        else:\n            while cur != root and cur.children[ord(c) - ord(\'a\')] is None:\n                cur = cur.fail\n            if cur != root:\n                cur = cur.children[ord(c) - ord(\'a\')]\n                cnt += cur.cnt\n                cur.cnt = 0\n    return cnt\n\n\nn = int(input())\nfor _ in range(n):\n    build(input())\nbuild_fail()\ns = input()\nans = ac_query(s)\nprint(ans)\nËΩ¨java', 228, 'user', '2024-07-08 20:08:44');
INSERT INTO `xz_dialogue` VALUES (1669, '‰∏ãÈù¢ÊòØÂ∞Ü‰Ω†Êèê‰æõÁöÑPython‰ª£Á†ÅËΩ¨Êç¢‰∏∫JavaÁöÑÂÆûÁé∞„ÄÇËØ∑Ê≥®ÊÑèÔºåJava‰∏≠Ê≤°ÊúâÂÜÖÁΩÆÁöÑÈòüÂàópop(0)Êìç‰ΩúÁöÑÈ´òÊïàÂÆûÁé∞ÔºåÊâÄ‰ª•ËøôÈáå‰ΩøÁî®‰∫Ü`LinkedList`‰Ωú‰∏∫ÈòüÂàóÔºå‰ª•‰æøËÉΩÂ§üÈ´òÊïàÂú∞ÊâßË°åÁ±ª‰ººÁöÑÊìç‰ΩúÔºà`removeFirst`Ôºâ„ÄÇ\n\n\n```java\nimport java.util.LinkedList;\nimport java.util.Scanner;\n\nclass Trie {\n    Trie fail;\n    Trie[] children = new Trie[26];\n    int cnt = 0;\n}\n\npublic class Main {\n    static Trie root = new Trie();\n\n    public static void build(String s) {\n        Trie cur = root;\n        for (char c : s.toCharArray()) {\n            int nx = c - \'a\';\n            if (cur.children[nx] == null) {\n                cur.children[nx] = new Trie();\n            }\n            cur = cur.children[nx];\n        }\n        cur.cnt++;\n    }\n\n    public static void buildFail() {\n        LinkedList<Trie> q = new LinkedList<>();\n        for (int i = 0; i < 26; i++) {\n            if (root.children[i] != null) {\n                root.children[i].fail = root;\n                q.add(root.children[i]);\n            }\n        }\n        while (!q.isEmpty()) {\n            Trie cur = q.removeFirst();\n            for (int i = 0; i < 26; i++) {\n                if (cur.children[i] != null) {\n                    if (cur.fail != null) {\n                        cur.children[i].fail = cur.fail.children[i] != null ? cur.fail.children[i] : root;\n                    } else {\n                        cur.children[i].fail = root;\n                    }\n                    q.add(cur.children[i]);\n                }\n            }\n        }\n    }\n\n    public static int acQuery(String s) {\n        Trie cur = root;\n        int cnt = 0;\n        for (char c : s.toCharArray()) {\n            int index = c - \'a\';\n            if (cur.children[index] != null) {\n                cur = cur.children[index];\n                cnt += cur.cnt;\n                cur.cnt = 0;\n            } else {\n                while (cur != root && cur.children[index] == null) {\n                    cur = cur.fail;\n                }\n                if (cur != root) {\n                    cur = cur.children[index];\n                    cnt += cur.cnt;\n                    cur.cnt = 0;\n                }\n            }\n        }\n        return cnt;\n    }\n\n    public static void main(String[] args) {\n        Scanner scanner = new Scanner(System.in);\n        int n = Integer.parseInt(scanner.nextLine());\n        for (int i = 0; i < n; i++) {\n            build(scanner.nextLine());\n        }\n        buildFail();\n        String s = scanner.nextLine();\n        int ans = acQuery(s);\n        System.out.println(ans);\n    }\n}\n```\nËøôÊÆµ‰ª£Á†ÅÈ¶ñÂÖàÂÆö‰πâ‰∫Ü‰∏Ä‰∏™`Trie`Á±ªÊù•Ë°®Á§∫Â≠óÂÖ∏Ê†ëÁöÑÁªìÊûÑÔºåÂπ∂Âú®`Main`Á±ª‰∏≠ÂÆûÁé∞‰∫ÜÊûÑÂª∫Â≠óÂÖ∏Ê†ë„ÄÅÊûÑÂª∫Â§±Ë¥•ÊåáÈíàÂíåÊü•ËØ¢Â≠óÁ¨¶‰∏≤ÁöÑÂäüËÉΩ„ÄÇÂú®`main`ÊñπÊ≥ï‰∏≠ÔºåÁ®ãÂ∫è‰ªéÊ†áÂáÜËæìÂÖ•ËØªÂèñÊï∞ÊçÆÔºåÂπ∂ÊâßË°åÁõ∏Â∫îÁöÑÊìç‰Ωú„ÄÇ', 228, 'assistant', '2024-07-08 20:08:44');
