INSERT INTO `xz_dialogue` VALUES (1671, 'Ë¶ÅÂ∞ÜËøôÊÆµPython‰ª£Á†ÅËΩ¨Êç¢‰∏∫JavaÔºåÊàë‰ª¨ÈúÄË¶ÅÊ≥®ÊÑèÂá†‰∏™ÂÖ≥ÈîÆÁÇπÔºö\n\n1. Âú®Java‰∏≠ÔºåÁ±ªÁöÑÂÆö‰πâÂíåÂÆû‰æãÂåñ‰∏çÂêå‰∫éPython„ÄÇ\n2. JavaÁöÑÊï∞ÁªÑÂ£∞ÊòéÂíå‰ΩøÁî®‰∏éPythonÊúâÊâÄ‰∏çÂêå„ÄÇ\n3. JavaÊ≤°ÊúâÂÜÖÁΩÆÁöÑ`ord`ÂáΩÊï∞Ôºå‰ΩÜÊàë‰ª¨ÂèØ‰ª•Áî®Â≠óÁ¨¶ÂáèÂéª`\'a\'`ÁöÑASCIIÂÄºÊù•ËÆ°ÁÆóÁ¥¢Âºï„ÄÇ\n4. Java‰∏≠ÁöÑÈòüÂàóÔºàQueueÔºâÂèØ‰ª•‰ΩøÁî®`LinkedList`ÂÆûÁé∞„ÄÇ\n\n‰∏ãÈù¢ÊòØ‰∏Ä‰∏™ÁÆÄÂåñÁöÑJavaÁâàÊú¨ÔºåÂÆûÁé∞‰∫Ü‰∏äËø∞Python‰ª£Á†ÅÁöÑÂäüËÉΩÔºö\n\n\n```java\nimport java.util.*;\n\nclass TrieNode {\n    TrieNode fail, children[];\n    int end = -1;\n\n    TrieNode() {\n        children = new TrieNode[26];\n    }\n}\n\npublic class AhoCorasick {\n    private static TrieNode root;\n    private static int[] cnt;\n\n    public static void main(String[] args) {\n        Scanner scanner = new Scanner(System.in);\n        while (true) {\n            int n = Integer.parseInt(scanner.nextLine());\n            if (n == 0) break;\n            \n            cnt = new int[1000000]; // Assuming maximum of 1 million strings\n            root = new TrieNode();\n            \n            String[] st = new String[n];\n            for (int i = 0; i < n; i++) {\n                st[i] = scanner.nextLine();\n                build(st[i], i);\n            }\n            \n            buildFail();\n            String query = scanner.nextLine();\n            acQuery(query);\n            \n            int mx = -1;\n            for (int i = 0; i < n; i++) {\n                if (cnt[i] > mx) {\n                    mx = cnt[i];\n                }\n            }\n            \n            System.out.println(mx);\n            for (int i = 0; i < n; i++) {\n                if (cnt[i] == mx) {\n                    System.out.println(st[i]);\n                }\n            }\n        }\n        scanner.close();\n    }\n\n    private static void build(String s, int i) {\n        TrieNode cur = root;\n        for (char c : s.toCharArray()) {\n            int nx = c - \'a\';\n            if (cur.children[nx] == null) {\n                cur.children[nx] = new TrieNode();\n            }\n            cur = cur.children[nx];\n        }\n        cur.end = i;\n    }\n\n    private static void buildFail() {\n        LinkedList<TrieNode> q = new LinkedList<>();\n        for (int i = 0; i < 26; i++) {\n            if (root.children[i] != null) {\n                root.children[i].fail = root;\n                q.add(root.children[i]);\n            }\n        }\n        \n        while (!q.isEmpty()) {\n            TrieNode cur = q.poll();\n            for (int i = 0; i < 26; i++) {\n                if (cur.children[i] != null) {\n                    TrieNode failNode = cur.fail;\n                    while (failNode != root && failNode.children[i] == null) {\n                        failNode = failNode.fail;\n                    }\n                    cur.children[i].fail = failNode.children[i] != null ? failNode.children[i] : root;\n                    q.add(cur.children[i]);\n                } else {\n                    cur.children[i] = cur.fail.children[i];\n                }\n            }\n        }\n    }\n\n    private static void acQuery(String s) {\n        TrieNode cur = root;\n        for (char c : s.toCharArray()) {\n            int nx = c - \'a\';\n            while (cur.children[nx] == null && cur != root) {\n                cur = cur.fail;\n            }\n            cur = cur.children[nx];\n            if (cur == null) {\n                cur = root;\n            }\n            TrieNode temp = cur;\n            while (temp != root) {\n                if (temp.end != -1) {\n                    cnt[temp.end]++;\n                }\n                temp = temp.fail;\n            }\n        }\n    }\n}\n```\nÊ≥®ÊÑèÔºöËøô‰∏™JavaÁâàÊú¨ÊòØ‰∏∫‰∫ÜÊºîÁ§∫ËΩ¨Êç¢ËøáÁ®ãËÄåÁÆÄÂåñÁöÑ„ÄÇÂú®ÂÆûÈôÖÂ∫îÁî®‰∏≠ÔºåÂèØËÉΩÈúÄË¶ÅÂØπËæìÂÖ•ÂíåÈîôËØØÂ§ÑÁêÜ', 229, 'assistant', '2024-07-08 22:26:42');
