INSERT INTO `xz_dialogue` VALUES (1673, '‰∏ãÈù¢ÊòØÂ∞Ü‰∏äËø∞Python‰ª£Á†ÅËΩ¨Êç¢‰∏∫C++ÁöÑÂÆûÁé∞„ÄÇËØ∑Ê≥®ÊÑèÔºåC++‰∏≠ÁöÑÂÜÖÂ≠òÁÆ°ÁêÜÂíåÁ±ªÂûãÂ£∞Êòé‰∏éPythonÂíåJavaÊúâÊâÄ‰∏çÂêåÔºåÂõ†Ê≠§Êàë‰ª¨ÈúÄË¶ÅÊ†ºÂ§ñÊ≥®ÊÑèÊåáÈíàÁöÑ‰ΩøÁî®ÂíåÂÜÖÂ≠òÂàÜÈÖç„ÄÇ\n\n```cpp\n#include <iostream>\n#include <vector>\n#include <queue>\n#include <string>\n\nusing namespace std;\n\nclass TrieNode {\npublic:\n    TrieNode* fail;\n    TrieNode* children[26];\n    int end;\n\n    TrieNode() {\n        fail = nullptr;\n        end = -1;\n        for (int i = 0; i < 26; ++i) {\n            children[i] = nullptr;\n        }\n    }\n};\n\nTrieNode* root;\nvector<int> cnt;\n\nvoid build(const string& s, int i) {\n    TrieNode* cur = root;\n    for (char c : s) {\n        int nx = c - \'a\';\n        if (!cur->children[nx]) {\n            cur->children[nx] = new TrieNode();\n        }\n        cur = cur->children[nx];\n    }\n    cur->end = i;\n}\n\nvoid buildFail() {\n    queue<TrieNode*> q;\n    for (int i = 0; i < 26; ++i) {\n        if (root->children[i]) {\n            root->children[i]->fail = root;\n            q.push(root->children[i]);\n        }\n    }\n    while (!q.empty()) {\n        TrieNode* cur = q.front();\n        q.pop();\n        for (int i = 0; i < 26; ++i) {\n            if (cur->children[i]) {\n                TrieNode* failNode = cur->fail;\n                while (failNode != root && !failNode->children[i]) {\n                    failNode = failNode->fail;\n                }\n                cur->children[i]->fail = failNode->children[i] ? failNode->children[i] : root;\n                q.push(cur->children[i]);\n            } else {\n                cur->children[i] = cur->fail->children[i];\n            }\n        }\n    }\n}\n\nvoid acQuery(const string& s) {\n    TrieNode* cur = root;\n    for (char c : s) {\n        int nx = c - \'a\';\n        while (!cur->children[nx] && cur != root) {\n            cur = cur->fail;\n        }\n        cur = cur->children[nx];\n        if (!cur) {\n            cur = root;\n        }\n        TrieNode* temp = cur;\n        while (temp != root) {\n            if (temp->end != -1) {\n                cnt[temp->end]++;\n            }\n            temp = temp->fail;\n        }\n    }\n}\n\nint main() {\n    int n;\n    string line, query;\n    while (cin >> n && n) {\n        cnt.assign(n, 0);\n        root = new TrieNode();\n        vector<string> patterns(n);\n        for (int i = 0; i < n; ++i) {\n            cin >> patterns[i];\n            build(patterns[i], i);\n        }\n        buildFail();\n        cin >> query;\n        acQuery(query);\n        int mx = *max_element(cnt.begin(), cnt.end());\n        cout << mx << endl;\n        for (int i = 0; i < n; ++i) {\n            if (cnt[i] == mx) {\n                cout << patterns[i] << endl;\n            }\n        }\n        // Clean up memory (omitted for brevity, should be done in real code)\n    }\n    return 0;\n}\n```\n\nËØ∑Ê≥®ÊÑèÔºåËøô‰∏™C++‰ª£Á†ÅÁ§∫‰æãÁúÅÁï•‰∫ÜËØ¶ÁªÜÁöÑÂÜÖÂ≠òÊ∏ÖÁêÜÈÉ®ÂàÜ„ÄÇÂú®ÂÆûÈôÖÂ∫îÁî®‰∏≠Ôºå‰Ω†Â∫îÂΩìÁ°Æ‰øùÂú®‰∏çÂÜçÈúÄË¶ÅÊó∂ÈáäÊîæÊâÄÊúâÂä®ÊÄÅÂàÜÈÖçÁöÑÂÜÖÂ≠òÔºå‰ª•ÈÅøÂÖçÂÜÖÂ≠òÊ≥ÑÊºè„ÄÇËøôÈÄöÂ∏∏ÈÄöËøá‰ΩøÁî®Êô∫ËÉΩÊåáÈíàÔºàÂ¶Ç`std::unique_ptr`Êàñ`std::shared_ptr`ÔºâÊàñÊâãÂä®Âà†Èô§ÂàÜÈÖçÁöÑËäÇÁÇπÊù•ÂÆåÊàê„ÄÇ\n\nÊ≠§Â§ñÔºåÈîôËØØÂ§ÑÁêÜÂú®Ëøô‰∏™Á§∫‰æã‰∏≠‰πüÊ≤°ÊúâËØ¶ÁªÜÂ±ïÁ§∫„ÄÇÂú®Áîü‰∫ß‰ª£Á†Å‰∏≠Ôºå‰Ω†Â∫îËØ•Ê∑ªÂä†ÈÄÇÂΩìÁöÑÈîôËØØÊ£ÄÊü•ÂíåÂ§ÑÁêÜÈÄªËæëÔºå‰ª•Á°Æ‰øùÁ®ãÂ∫èÁöÑÂÅ•Â£ÆÊÄß„ÄÇ', 229, 'assistant', '2024-07-08 22:31:13');
