INSERT INTO `articles` VALUES (14, '<h2 style=\"text-align: start;\">ä»£ç </h2><p style=\"text-align: start;\">ä¸€ï¼šç­é•¿</p><pre><code class=\"language-java\">import java.util.*;\n\npublic class Main {\n\n    static int[] fa;\n\n    public static void main(String[] args) {\n        Scanner sc = new Scanner(System.in);\n        int n = sc.nextInt();\n        int m = sc.nextInt();\n        fa = new int[n + 1];\n        for (int i = 1; i &lt;= n; i++) {\n            fa[i] = i;\n        }\n        for (int i = 0; i &lt; m; i++) {\n            int type = sc.nextInt();\n            if (type == 1) {\n                merge(sc.nextInt(), sc.nextInt());\n            } else if (type == 2) {\n                int a = sc.nextInt();\n                int root = find(a);\n                fa[a] = a;\n                fa[root] = a;\n            } else {\n                System.out.println(find(sc.nextInt()));\n            }\n        }\n    }\n\n    public static void merge(int x, int y) {\n        fa[find(x)] = find(y);\n    }\n\n    public static int find(int x) {\n        if (fa[x] != x) {\n            fa[x] = find(fa[x]);\n        }\n        return fa[x];\n    }\n}</code></pre><p style=\"text-align: start;\">äºŒï¼šç´¢é“</p><pre><code class=\"language-java\">import java.util.*;\n\npublic class Main {\n\n    public static void main(String[] args) {\n        Scanner sc = new Scanner(System.in);\n        int n = sc.nextInt();\n        int k = sc.nextInt() - 1;\n        int[] arr = new int[n];\n        for (int i = 0; i &lt; n; i++) {\n            arr[i] = sc.nextInt();\n        }\n        int[] dp = new int[n];\n        int max1 = 0;\n        if (arr[0] &lt; arr[k]) {\n            dp[0] = 1;\n            max1 = 1;\n        }\n        for (int i = 1; i &lt; k; i++) {\n            if (arr[i] &lt; arr[k]) {\n                dp[i] = 1;\n                for (int j = 0; j &lt; i; j++) {\n                    if (arr[i] &gt; arr[j]) {\n                        dp[i] = Math.max(dp[i], dp[j] + 1);\n                    }\n                }\n            }\n            max1 = Math.max(max1, dp[i]);\n        }\n        int max2 = 0;\n        if (k + 1 &lt; n && arr[k + 1] &lt; arr[k]) {\n            dp[k + 1] = 1;\n            max2 = 1;\n        }\n        for (int i = k + 2; i &lt; n; i++) {\n            if (arr[i] &lt; arr[k]) {\n                dp[i] = 1;\n                for (int j = k + 1; j &lt; i; j++) {\n                    if (arr[j] &gt; arr[i]) {\n                        dp[i] = Math.max(dp[i], dp[j] + 1);\n                    }\n                }\n            }\n            max2 = Math.max(max2, dp[i]);\n        }\n        System.out.println(max1 + max2 + 1);\n    }\n\n}</code></pre><p><br></p>', 1, '2023-08-01 23:07:34', 'ç¬¬å››æ¬¡éšæœºèµ›', '', NULL, '', 'ä»£ç \nä¸€ï¼šç­é•¿\nimport java.util.*;\r\n\r\npublic class Main {\r\n\r\n    static int[] fa;\r\n\r\n    public static void main(String[] args) {\r\n        Scanner sc = new Scanner(System.in);\r\n        int n = sc.nextInt();\r\n        int m = sc.nextInt();\r\n        fa = new int[n + 1];\r\n        for (int i = 1; i <= n; i++) {\r\n            fa[i] = i;\r\n        }\r\n        for (int i = 0; i < m; i++) {\r\n            int type = sc.nextInt();\r\n            if (type == 1) {\r\n                merge(sc.nextInt(), sc.nextInt());\r\n            } else if (type == 2) {\r\n                int a = sc.nextInt();\r\n                int root = find(a);\r\n                fa[a] = a;\r\n                fa[root] = a;\r\n            } else {\r\n                System.out.println(find(sc.nextInt()));\r\n            }\r\n        }\r\n    }\r\n\r\n    public static void merge(int x, int y) {\r\n        fa[find(x)] = find(y);\r\n    }\r\n\r\n    public static int find(int x) {\r\n        if (fa[x] != x) {\r\n            fa[x] = find(fa[x]);\r\n        }\r\n        return fa[x];\r\n    }\r\n}\näºŒï¼šç´¢é“\nimport java.util.*;\r\n\r\npublic class Main {\r\n\r\n    public static void main(String[] args) {\r\n        Scanner sc = new Scanner(System.in);\r\n        int n = sc.nextInt();\r\n        int k = sc.nextInt() - 1;\r\n        int[] arr = new int[n];\r\n        for (int i = 0; i < n; i++) {\r\n            arr[i] = sc.nextInt();\r\n        }\r\n        int[] dp = new int[n];\r\n        int max1 = 0;\r\n        if (arr[0] < arr[k]) {\r\n            dp[0] = 1;\r\n            max1 = 1;\r\n        }\r\n        for (int i = 1; i < k; i++) {\r\n            if (arr[i] < arr[k]) {\r\n                dp[i] = 1;\r\n                for (int j = 0; j < i; j++) {\r\n                    if (arr[i] > arr[j]) {\r\n                        dp[i] = Math.max(dp[i], dp[j] + 1);\r\n                    }\r\n                }\r\n            }\r\n            max1 = Math.max(max1, dp[i]);\r\n        }\r\n        int max2 = 0;\r\n        if (k + 1 < n && arr[k + 1] < arr[k]) {\r\n            dp[k + 1] = 1;\r\n            max2 = 1;\r\n        }\r\n        for (int i = k + 2; i < n; i++) {\r\n            if (arr[i] < arr[k]) {\r\n                dp[i] = 1;\r\n                for (int j = k + 1; j < i; j++) {\r\n                    if (arr[j] > arr[i]) {\r\n                        dp[i] = Math.max(dp[i], dp[j] + 1);\r\n                    }\r\n                }\r\n            }\r\n            max2 = Math.max(max2, dp[i]);\r\n        }\r\n        System.out.println(max1 + max2 + 1);\r\n    }\r\n\r\n}\n', 'richtext', '0');
INSERT INTO `articles` VALUES (15, '<h1>è®°å½•ä¸€äº›MySQLå¸¸ç”¨æŸ¥è¯¢æ“ä½œ</h1><h2><strong>æŸ¥è¯¢éƒ¨é—¨è–ªèµ„å‰Næˆ–è€…å‰å‡ çš„å‘˜å·¥</strong></h2><h3>1ã€çª—å£å‡½æ•°è§£æ³•</h3><pre><code class=\"language-sql\">è¾“å…¥ï¼š\nEmployee è¡¨:\n+----+-------+--------+--------------+\n| id | name  | salary | departmentId |\n+----+-------+--------+--------------+\n| 1  | Joe   | 70000  | 1            |\n| 2  | Jim   | 90000  | 1            |\n| 3  | Henry | 80000  | 2            |\n| 4  | Sam   | 60000  | 2            |\n| 5  | Max   | 90000  | 1            |\n+----+-------+--------+--------------+\nDepartment è¡¨:\n+----+-------+\n| id | name  |\n+----+-------+\n| 1  | IT    |\n| 2  | Sales |\n+----+-------+\nè¾“å‡ºï¼š\n+------------+----------+--------+\n| Department | Employee | Salary |\n+------------+----------+--------+\n| IT         | Jim      | 90000  |\n| Sales      | Henry    | 80000  |\n| IT         | Max      | 90000  |\n+------------+----------+--------+\nè§£é‡Šï¼šMax å’Œ Jim åœ¨ IT éƒ¨é—¨çš„å·¥èµ„éƒ½æ˜¯æœ€é«˜çš„ï¼ŒHenry åœ¨é”€å”®éƒ¨çš„å·¥èµ„æœ€é«˜ã€‚\n\nselect Department,Employee,Salary\nfrom (\n    select d.name Department,e.name Employee,e.Salary,\n    dense_rank() over(\n        partition by e.departmentId \n        order by e.salary desc\n    ) r_rank\n    from Employee e,Department d\n    where e.departmentId = d.id\n) t\nwhere t.r_rank = 1</code></pre><h3>2ã€æ™®é€šè§£æ³•</h3><pre><code class=\"language-sql\">select d.name Department,e.name Employee,e.Salary\nfrom Employee e\nleft join Department d on e.departmentId = d.id\nwhere (departmentId,Salary) in (\n    select departmentId,max(salary) from Employee\n    group by departmentId\n)</code></pre><h2><strong>åˆ é™¤è¡¨ä¸­é‡å¤å…ƒç´ </strong></h2><h3>1ã€çª—å£å‡½æ•°è§£æ³•</h3><pre><code class=\"language-sql\">è¾“å…¥: \nPerson è¡¨:\n+----+------------------+\n| id | email            |\n+----+------------------+\n| 1  | john@example.com |\n| 2  | bob@example.com  |\n| 3  | john@example.com |\n+----+---------------