:string t;\n        while(head != nullptr){\n            t.push_back(head -> val + \'0\');\n            head = head -> next;\n        }\n        reverse(t.begin(), t.end());\n        auto mul = [&](std::string &a, int b) -> std::string {\n            int n = a.size();\n            int c = 0;\n            std::string ans;\n            for(int i = 0; i < n; i ++ ){\n                int d = (a[i] - \'0\') * 2 + c;\n                ans.push_back((d % 10) + \'0\');\n                c = d / 10;\n            }\n            if(c){\n                ans.push_back(c + \'0\');\n            }\n            return std::string(ans.rbegin(), ans.rend());\n        };\n        auto b = mul(t, 2);\n        //std::cout << b << \'\\n\';\n        ListNode *root = nullptr;\n        int n = b.size();\n        ListNode *cur = nullptr;\n        for(int i = 0; i < n; i ++ ){\n            ListNode *node = new ListNode(b[i] - \'0\');\n            if(root == nullptr){\n                root = node;\n                cur = root;\n            } else {\n                //std::cout << cur -> val << \'\\n\';\n                cur -> next = node;\n                cur = cur -> next;\n            }\n        }\n        return root;\n    }\n};\n```\n\n### T3ï¼š[é™åˆ¶æ¡ä»¶ä¸‹å…ƒç´ ä¹‹é—´çš„æœ€å°ç»å¯¹å·®](https://leetcode.cn/problems/minimum-absolute-difference-between-elements-with-constraint/)\n\næˆ‘ä»¬è¦æ±‚çš„æ˜¯å¯¹äºä»»æ„ä¸€ä¸ªä¸‹æ ‡$i$ï¼Œæ±‚ä¸€ä¸ª$j$ï¼Œæ»¡è¶³$abs(j - i) \\geq x $å¹¶ä¸” ä½¿å¾—$abs(a_j - a_i)æœ€å°$ã€‚\n\nåˆ†æï¼š\n\n#### æ–¹æ³•ä¸€ï¼š\n\næˆ‘ä»¬æŠŠçœ‹ä¸€ä¸‹æ‰€æœ‰çš„$j$æ»¡è¶³ä»€ä¹ˆæ¡ä»¶ã€‚å¦‚å›¾\n\næˆ‘ä»¬è¦æ‰¾çš„$j$åº”è¯¥éƒ½æ˜¯åœ¨$i + x$åˆ°$n$ä¹‹é—´çš„ï¼Œæ‰€ä»¥é¢˜ç›®å¯ä»¥å˜æˆï¼Œå¯¹äºä»»æ„ä¸€ä¸ª$i$ï¼Œæ±‚$i + x - n$ä¹‹é—´ä¸$a_i$å·®æœ€å°çš„ã€‚å¾ˆå®¹æ˜“æƒ³åˆ°æ‰¾ç¬¬ä¸€ä¸ª**å¤§äºç­‰äº**$a_i$çš„ä½ç½®å’Œç¬¬ä¸€ä¸ª**å°äº**$a_i$çš„ä½ç½®å³å¯ã€‚å–ä¸€ä¸ª$min$ã€‚è¿™ä¸ªæ“ä½œæˆ‘ä»¬ä¹Ÿå¯ä»¥åè¿‡æ¥ï¼Œå¯¹äºä¸€ä¸ª$j$ï¼Œæ‰¾$j - x$ä¹‹å‰çš„æ‰€æœ‰æ»¡è¶³æ¡ä»¶çš„æ•°å­—å–ä¸€ä¸ª$min$ï¼Œäºæ˜¯æˆ‘ä»¬å¯ä»¥åœ¨`set`é‡Œè¿›è¡ŒäºŒåˆ†ï¼Œ`set`é‡Œç»´æŠ¤çš„æ˜¯$i - x$ä¹‹å‰æ‰€æœ‰ç‚¹ï¼Œå› ä¸º$i$ä¸¥æ ¼é€’å¢ï¼Œå¦‚æœ$i - 1$æ»¡è¶³æ¡ä»¶åˆ™$i$ä¸€å®šæ»¡è¶³ã€‚\n`AC Code`\n\n```cpp\nclass Solution {\npublic:\n    int minAbsoluteDifference(vector<int>& nums, int x) {\n        int n = nums.size();\n        std::set<int> set;\n        int ans = INT_MAX;\n        for(int i = 0; i < n; i ++ ){\n            if(i - x >= 0){\n                set.insert(nums[i - x]);\n            }\n            auto it = set.lower_bound(nums[i]);\n            if(it != set.end()){\n                ans = std::min(ans, abs(nums[i] - *it));\n            }\n            if(it != set.begin()){\n                ans = std::min(ans, abs(nums[i] - *std::prev(it)));\n            }\n        }\n        return ans;\n    }\n};\n```\n\n#### æ–¹æ³•äºŒï¼š\n\næˆ‘ä»¬çŸ¥é“ï¼Œå¯¹äºä¸€ä¸ª$i$ä¸ç¬¦åˆæ¡ä»¶çš„èŒƒå›´æ˜¯$[i - x + 1, i + x - 1]$ã€‚è€ƒè™‘å¦‚æœä¸‹æ ‡ä»$i$å˜åˆ°äº†$i + 1$ï¼Œé‚£ä¹ˆ$i - x + 1$å°±ä¼šå˜æˆåˆæ³•çš„ï¼Œä½†æ˜¯$i + x$å°±ä¼šå˜å¾—ä¸åˆæ³•ã€‚å› ä¸ºå½“$i+1$åä¸åˆæ³•çš„åŒºé—´æ˜¯$[i + 1 + 1 - x, i + 1 + x - 1]$å³[i + 2 - x, i + x]ï¼Œæ‰€ä»¥è¦æŠŠ$i - x + 1$è¿™ä¸ªç‚¹åŠ å…¥è¿›æ¥ï¼Œåˆ æ‰$i + x$è¿™ä¸ªç‚¹ï¼ŒäºŒåˆ†ä¸€ä¸‹å³å¯ã€‚\n\næ—¶é—´å¤æ‚åº¦$O(nlogn)$\n\n`AC Code`\n\n```cpp\nclass Solution {\npublic:\n    int minAbsoluteDifference(vector<int>& nums, int x) {\n        if(x == 0){\n            return 0;\n        }\n        std::multiset<int> mulset;\n        int n = nums.size();\n        int ans = INT_MAX;\n        for (int i = x; i < n; i++) {\n            mulset.insert(nums[i]);\n        }\n        for(int i = 0; i < n; i ++ ){\n            auto it = mulset.lower_bound(nums[i]);\n            if(it != mulset.end()){\n                ans = std::min(ans, abs(nums[i] - *it));\n            }\n            if(it != mulset.begin()){\n                ans = std::min(ans, abs(nums[i] - *std::prev(it)));\n            }\n            if(i + x < n){\n                mulset.erase(mulset.find(nums[i + x]));\n            }\n            if(i + 1 - x >= 0){\n                mulset.insert(nums[i + 1 - x]);\n            }\n        }\n        return ans;\n    }\n};\n```\n\n### T4ï¼š[æ“ä½œä½¿å¾—åˆ†æœ€å¤§](https://leetcode.cn/problems/apply-operations-to-maximize-score/)\n\nåˆ†æé¢˜ç›®ï¼š\né¢˜ç›®è®©æˆ‘ä»¬æ±‚ï¼š\n\n+ ä¹‹å‰æ²¡æœ‰é€‰æ‹©è¿‡çš„å­æ•°ç»„$nums[l......r]$ã€‚\n+ é€‰åŒºé—´å†…é€‰æ‹©ä¸€ä¸ª**è´¨æ•°åˆ†æ•°**æœ€é«˜çš„ï¼Œå¦‚æœéƒ½ä¸ºæœ€é«˜çš„ï¼Œé€‰æ‹©ä¸‹æ ‡**æœ€å°**çš„ã€‚\n+ åˆ†æ•°åˆå§‹å€¼ä¸º$1$ï¼Œæ¯æ¬¡ä¹˜è´¨æ•°åˆ†æ•°æœ€é«˜çš„è¿™ä¸ªæ•°å­—$a_i$ã€‚\n+ æ±‚`k`æ¬¡æ“ä½œååˆ†æ•°çš„æœ€å¤§å€¼ã€‚\n\nè´¨æ•°åˆ†æ•°ï¼šä¸€ä¸ªæ•°çš„è´¨å› æ•°ä¸ªæ•°ã€‚\n\nç¬¬ä¸€æ­¥ï¼Œæˆ‘ä»¬å¯ä»¥é¢„å¤„ç†å‡ºæ¥$10^5$ä»¥å†…æ‰€æœ‰æ•°å­—çš„è´¨æ•°åˆ†æ•°ã€‚å¤æ‚åº¦$O(n\\sqrt{n})$\n\nç¬¬äºŒæ­¥ï¼Œæ€è€ƒï¼Œæ±‚æœ€å¤§å€¼ï¼Œè´ªå¿ƒè€ƒè™‘ï¼Œæˆ‘ä»¬æ¯ä¸€æ¬¡ä¸€å®šè¦é€‰æ‹©æ•°å­—æœ€å¤§çš„æ•°å­—ã€‚\n\nç¬¬ä¸‰æ­¥ï¼Œäºæ˜¯é—®é¢˜å°±å˜æˆäº†ï¼Œ$a_i$è¢«åŒºé—´åŒ…å«äº†å‡ æ¬¡ã€‚\n\nè€ƒè™‘ä¸€ä¸ªé—®é¢˜ï¼Œå‡å¦‚æˆ‘ä»¬æœ‰ä¸€ä¸ªè´¨æ•°åˆ†æ•°çš„æ•°ç»„ï¼š$1,1,1,2,1,1,1$è€ƒè™‘å¯¹äºç¬¬ä¸‰ä¸ª$1$ï¼Œæˆ‘ä»¬å¤„ç†å‡ºæ¥å·¦è¾¹ç¬¬ä¸€ä¸ª**å¤§äºç­‰äº**ä»–çš„å€¼è®°ä¸º$l_i$ï¼Œå¦‚æœæ²¡æœ‰å°±æ˜¯$-1$ï¼Œä¸ºä»€ä¹ˆå‘¢æ˜¯$-1$å‘¢ï¼Ÿæ–¹ä¾¿æˆ‘ä»¬å¯¹ç­”æ¡ˆè¿›è¡Œè®¡ç®—ï¼Œåç»­åœ¨è®²ã€‚è¿™æ ·ç”±äºæˆ‘ä»¬å½“å‰çš„$i$åº”è¯¥è¦æ˜¯å·¦ç«¯ç‚¹ï¼Œå› ä¸ºå·¦è¾¹å’Œä»–ç›¸ç­‰çš„æˆ‘ä»¬æ˜¯ä¸èƒ½é€‰çš„ï¼Œå¦‚æœé€‰äº†åˆ™ä¸æ»¡è¶³é¢˜ç›®è¦æ±‚çš„ä¸‹æ ‡æœ€å°ï¼Œæ‰€ä»¥å·¦è¾¹è´¡çŒ®åº”è¯¥æ˜¯$i - l_i$ï¼Œå½“$l_i = -1$çš„æ—¶å€™ï¼Œä¹Ÿå¯ä»¥ç®—å‡ºåŒºé—´é•¿åº¦å³$i + 1$ã€‚ä¸ºä»€ä¹ˆå–**ç­‰äº**ï¼Œå› ä¸ºæˆ‘ä»¬ä¸èƒ½åŒ…æ‹¬ç­‰å·çš„è¿™ä¸ªç‚¹ï¼Œè¿™æ ·ä¸é¢˜ç›®æœ€å°ä¸‹æ ‡å†²çªã€‚\n\nè€ƒè™‘å³è¾¹ï¼Œå³è¾¹åº”è¯¥æ‰¾ä¸¥æ ¼**å¤§äº**$i$çš„ç‚¹è®°ä¸º$r_i$ï¼Œå› ä¸ºç­‰äºä¹Ÿæ˜¯å¯ä»¥åŒ…æ‹¬$i$çš„ï¼Œå› ä¸º$i$æ˜¯æœ€å·¦è¾¹ï¼Œå³è¾¹å³ä¾¿æœ‰ä¸€æ ·çš„ä¹Ÿå¯ä»¥ã€‚\n\nè¿™ä¸ªè¿‡ç¨‹æˆ‘ä»¬æ€ä¹ˆå¤„ç†ï¼Ÿ\n\nå…ˆè®©æ˜¯ä¸€ä¸ªä¸¥æ ¼å•è°ƒçš„ï¼Œè€ƒè™‘ç”¨**å•è°ƒæ ˆ**ã€‚\n\nå°ç»†èŠ‚ï¼šå¤„ç†å·¦è¾¹çš„æ—¶å€™ï¼Œ`v[nums[s.top()]] < v[nums[i]]`çš„æ—¶å€™æˆ‘ä»¬å°±å¼¹å‡ºæ ˆå¤´ï¼Œä½†æ˜¯å¦‚æœæˆ‘ä»¬æŠŠ`s.top()`å½“æˆ`i`ï¼Œå³æ¡ä»¶ä¸­çš„è¿™ä¸ª`i`åˆšå¥½æ˜¯`s.top()`å³è¾¹ç¬¬ä¸€ä¸ª**å¤§äº**ä»–çš„å€¼ã€‚äºæ˜¯å¯ä»¥ç›´æ¥å¤„ç†å‡ºæ¥$l_iå’Œr_i$æ•°ç»„ã€‚\n\näºæ˜¯ç­”æ¡ˆè´¡çŒ®å°±æ˜¯ï¼š\n$cnt  = (i-l_i) \\times (r_i - i)$\nå¾ˆå¥½ç†è§£ï¼Œå·¦è¾¹æ¯ä¸ªç‚¹å’Œå³è¾¹æ¯ä¸ªç‚¹éƒ½èƒ½åŒ¹é…æˆä¸€ç»„åŒ…å«æœ€å¤§å€¼$i$çš„åŒºé—´ã€‚\n\nç¬¬ä¸‰æ­¥ï¼Œç”±äºè¦æ±‚çš„æ˜¯çš„æ—¶å€™æˆ‘ä»¬éœ€è¦çŸ¥é“$i$ï¼Œæ‰€ä»¥ä»¥ä¸‹æ ‡æ’åºï¼Œä¸‹æ ‡çš„æ’åºåº”è¯¥æŒ‰ç…§$nums_i \\gt nums_j$æ’åºï¼Œæ‰€ä»¥å¼€ä¸€ä¸ªæ•°ç»„$ids$ï¼Œä¸­å­˜$0-n - 1$ä¸‹æ ‡ï¼Œç„¶åæŒ‰ç…§$nums_i \\gt nums_j$æ’åºå³å¯ã€‚\n\næœ€åè€ƒè™‘ç­”æ¡ˆï¼Œå¦‚æœè¿™ä¸ªåŒºé—´å‡ºç°çš„æ¬¡æ•°$cnt \\geq k$ï¼Œæˆ‘ä»¬åªèƒ½å˜åŒ–$k$æ¬¡ï¼Œæ¯ä¸€æ¬¡éƒ½æ˜¯${nums_i}^{k}$ï¼Œåä¹‹å°±æ˜¯${nums_i}^{cnt}$åè¿˜å‰©ä¸‹$k - cnt$æ¬¡æ“ä½œã€‚è¿™ä¸ªæ“ä½œå¯ä»¥ç”¨å¿«é€Ÿå¹‚å®ç°ã€‚\n\næ—¶é—´å¤æ‚åº¦ï¼š$O(n \\sqrt{n})$ æ¥æºäºé¢„å¤„ç†ï¼Œå¿«é€Ÿå¹‚å¤æ‚åº¦æ˜¯$O(nlogn)$\n\n`AC Code`\n\n```cpp\nint v[100001];\nauto init = []() {\n    for(int i = 2; i <= 100000; i ++ ){\n        int cnt = 0;\n        int t = i;\n        for(int j = 2; j <= i / j; j ++ ){\n            if(t % j == 0){\n                cnt ++;\n                while(t % j == 0){\n                    t /= j;\n                }\n            }\n        }\n        if(t > 1){\n            cnt ++;\n        }\n        v[i] = cnt;\n    }\n    return 0;\n}();\n\nclass Solution {\n    using i64 = long long;\npublic:\n    static constexpr int MOD = 1e9 + 7;\n    int maximumScore(vector<int>& nums, int k) {\n        int n = nums.size();\n        std::vector<int> l(n, -1);\n        std::vector<int> r(n, n);\n        std::stack<int> s;\n        for(int i = 0; i < n; i ++ ){\n            while(!s.empty() && v[nums[s.top()]] < v[nums[i]]){\n                r[s.top()] = i;\n                s.pop();\n            }\n            if(!s.empty()){\n                l[i] = s.top();\n            }\n            s.push(i);\n        }\n        auto qmi = [&](i64 a, i64 b){\n            i64 ans = 1;\n            while(b){\n                if(b & 1){\n                    ans = (ans * a) % MOD;\n                }\n                a = (a * a) % MOD;\n                b >>= 1;\n            }\n            return ans;\n        };\n        std::vector<int> ids(n);\n        iota(ids.begin(), ids.end(), 0);\n        std::sort(ids.begin(), ids.end(), [&](int a, int b) -> bool {\n            return nums[a] > nums[b];\n        });\n        i64 ans = 1;\n        for(auto &i : ids){\n            i64 cnt = (i - l[i]) * (r[i] - i);\n            if(cnt >= k){\n                ans = (ans * qmi(nums[i], k)) % MOD;\n                break;\n            } else {\n                ans = (ans * qmi(nums[i], cnt)) % MOD;\n                k -= cnt;\n            }\n        }\n        return ans;\n    }\n};\n```\n', 13, '2023-08-14 00:26:33', '', '', NULL, '', 'test', 'markdown', '0');
