INSERT INTO `xz_dialogue` VALUES (310, '#include <bits/stdc++.h>\n#define all(s) s.begin(), s.end()\nusing namespace std;\nusing ll = long long;\nusing ull = unsigned long long;\n\nconst int _N = 1e5 + 5;\n\nint T;\n\nvoid solve() {\n	int n; cin >> n;\n	vector<ll> a(n); ll sum = 0;\n	for (int i = 0; i < n; i++) cin >> a[i], sum += a[i];\n	if (sum % n) return cout << \"No\" << \'\\n\', void();\n	sum /= n;\n	vector<int> bit(31, 0);\n	auto lowbit = [](int x) {\n	    return x & (-x);\n	};\n	for (int i = 0; i < n; i++) {\n		if (a[i] == sum) continue;\n		int d = abs(a[i] - sum);\n		int p = lowbit(d);\n		int e = d + p;\n		if (__builtin_popcount(e) == 1) {\n		    if (a[i] > sum) bit[__lg(e)]++, bit[__lg(p)]--;\n		    else bit[__lg(e)]--, bit[__lg(p)]++;\n		} else {\n		    cout << \"No\" << \'\\n\';\n		    return;\n		}\n	}\n	for (int i = 0; i < 31; i++) {\n		if (bit[i]) {\n			cout << \"No\" << \'\\n\';\n			return;\n		}\n	}\n	cout << \"Yes\" << \'\\n\';\n	return;\n}\n\nint main() {\n	ios::sync_with_stdio(false), cin.tie(0), cout.tie(0);\n	cin >> T;\n	while (T--) {\n		solve();\n	}\n}\nç¿»è¯‘æˆjava', 65, 'user', '2023-10-08 21:29:42');
