INSERT INTO `xz_dialogue` VALUES (315, 'ä»¥ä¸‹æ˜¯è¿™æ®µC++ä»£ç çš„Javaç‰ˆæœ¬ã€‚æˆ‘å·²ç»å°½å¯èƒ½åœ°ä¿ç•™äº†åŸå§‹C++ä»£ç çš„ç»“æ„å’Œé€»è¾‘ã€‚ä½†æ˜¯ï¼ŒJavaæœ‰ä¸€äº›ç‰¹æ€§ä¸C++ä¸åŒï¼Œå› æ­¤æœ‰äº›éƒ¨åˆ†è¿›è¡Œäº†é€‚å½“çš„ä¿®æ”¹ã€‚\n\n\n```java\nimport java.util.*;\n\npublic class Main {\n    static final int _N = 1e5 + 5;\n    static int T;\n\n    static void solve() {\n        int n; \n        Scanner in = new Scanner(System.in);\n        n = in.nextInt();\n        long[] a = new long[n];\n        long sum = 0;\n        for (int i = 0; i < n; i++) {\n            a[i] = in.nextLong();\n            sum += a[i];\n        }\n        if (sum % n != 0) {\n            System.out.println(\"No\");\n            return;\n        }\n        sum /= n;\n        int[] bit = new int[31];\n\n        static int lowbit(int x) {\n            return x & (-x);\n        }\n        for (int i = 0; i < n; i++) {\n            if (a[i] == sum) continue;\n            int d = (int)Math.abs(a[i] - sum);\n            int p = lowbit(d);\n            int e = d + p;\n            if ((e & -e) == e) { // instead of __builtin_popcount(e)\n                if (a[i] > sum) bit[Integer.numberOfTrailingZeros(e)]++, bit[Integer.numberOfTrailingZeros(p)]--;\n                else bit[Integer.numberOfTrailingZeros(e)]--, bit[Integer.numberOfTrailingZeros(p)]++;\n            } else {\n                System.out.println(\"No\");\n                return;\n            }\n        }\n        for (int i = 0; i < 31; i++) {\n            if (bit[i] != 0) {\n                System.out.println(\"No\");\n                return;\n            }\n        }\n        System.out.println(\"Yes\");\n    }\n\n    public static void main(String[] args) {\n        Scanner in = new Scanner(System.in);\n        T = in.nextInt();\n        while (T-- > 0) {\n            solve();\n        }\n        in.close();\n    }\n}\n```\næ³¨æ„ï¼šåœ¨Javaä¸­ï¼Œæ•°ç»„çš„å¤§å°åœ¨å£°æ˜æ—¶å¿…é¡»ç¡®å®šï¼Œå› æ­¤æˆ‘å°†`vector<ll>`å’Œ`vector<int>`æ›´æ”¹ä¸º`long[]`