INSERT INTO `xz_dialogue` VALUES (385, 'Dijkstraç®—æ³•æ˜¯ä¸€ç§ç”¨äºæŸ¥æ‰¾å›¾ä¸­æœ€çŸ­è·¯å¾„çš„ç®—æ³•ã€‚ä¸ºäº†å®ç°å †ä¼˜åŒ–ç‰ˆæœ¬çš„Dijkstraç®—æ³•ï¼Œæˆ‘ä»¬éœ€è¦ä½¿ç”¨Javaä¸­çš„ä¼˜å…ˆé˜Ÿåˆ—å’Œå †æ•°æ®ç»“æ„ã€‚ä¸‹é¢æ˜¯ä¸€ä¸ªç®€å•çš„å®ç°ï¼š\n\n\n```java\nimport java.util.*;\n\npublic class DijkstraHeapOptimized {\n    private static final int V = numVertices(graph);\n    private PriorityQueue<Node> pq;\n\n    public DijkstraHeapOptimized(Graph g) {\n        pq = new PriorityQueue<>((a, b) -> (a.cost - b.cost));\n        for (int v = 0; v < V; ++v) {\n            pq.offer(new Node(v, Double.POSITIVE_INFINITY));\n        }\n        for (int u : g.adjList[0]) {\n            update(u);\n        }\n    }\n\n    private void update(int node) {\n        Node u = pq.poll();\n        int v = u.node;\n        double oldDist = u.cost;\n        while (v != node) {\n            pq.offer(new Node(v, oldDist + graph.dist[v][node]));\n            v = getAdjNode(v);\n            oldDist += graph.dist[v][node];\n        }\n    }\n\n    public void dijkstra(int start) {\n        pq.offer(new Node(start, 0));\n        while (!pq.isEmpty()) {\n            Node u = pq.poll();\n            System.out.println(\"Vertex \" + u.node + \" with cost \" + u.cost);\n            for (Node v : pq) {\n                if (v.cost < graph.dist[u.node][v.node]) {\n                    graph.dist[u.node][v.node] = v.cost;\n                    graph.prev[u.node][v.node] = v.prev;\n                }\n            }\n        }\n    }\n}\n```\nåœ¨è¿™ä¸ªä»£ç ä¸­ï¼Œæˆ‘ä»¬ä½¿ç”¨äº†ä¼˜å…ˆé˜Ÿåˆ—ï¼ˆJavaä¸­çš„`PriorityQueue`ï¼‰æ¥ä¼˜åŒ–Dijkstraç®—æ³•ã€‚ä¼˜å…ˆé˜Ÿåˆ—çš„ç‰¹æ€§æ˜¯æ¯æ¬¡ä»é˜Ÿåˆ—ä¸­å–å‡ºçš„å…ƒç´ æ˜¯æœ€å°çš„ï¼Œå› æ­¤æˆ‘ä»¬å¯ä»¥åˆ©ç”¨è¿™ä¸ªç‰¹æ€§æ¥æ‰¾å‡ºæœ€çŸ­è·¯å¾„ã€‚è¿™ä¸ªç®—æ³•çš„æ ¸å¿ƒåœ¨äºç»´æŠ¤ä¸€ä¸ª`Node`å¯¹è±¡çš„å †ï¼Œæ¯æ¬¡æ‰¾åˆ°ä¸€ä¸ªæ–°çš„æœ€çŸ­è·¯å¾„åï¼Œå°†å…¶åŠ å…¥åˆ°å †ä¸­ï¼Œå¹¶åœ¨ä¹‹åå–å‡ºè¯¥è·¯å¾„è¿›è¡Œæ›´æ–°ã€‚å…·ä½“å®ç°å¦‚ä¸‹ï¼š\n\n1. åœ¨`DijkstraHeapOptimized`ç±»çš„æ„é€ å‡½æ•°ä¸­ï¼Œæˆ‘ä»¬å…ˆåˆå§‹å