INSERT INTO `xz_dialogue` VALUES (488, 'include <bits/stdc++.h>\nusing namespace std;\nusing i64 = int64_t;\nconstexpr i64 mod = 998244353;\nint main() {\n  cin.tie(nullptr)->sync_with_stdio(false);\n  auto read = [&]() {\n    int n;\n    cin >> n;\n    vector<int> a(n), b(n);\n    for (int &ai : a) { cin >> ai; }\n    for (int &bi : b) { cin >> bi; }\n    map<int, int> f;\n    for (int i = 0; i < n; i += 1) { f[a[i]] = b[i]; }\n    return tuple(n, f);\n  };\n  auto [n, x] = read();\n  auto [m, y] = read();\n  for (auto [ci, di] : y) {\n    if (not x.contains(ci)) { x[ci] = 0; }\n  }\n  i64 ans = 1;\n  for (auto [ai, bi] : x) {\n    int di = y[ai];\n    if (bi < di) {\n      ans = 0;\n    } else if (bi != di) {\n      ans = ans * 2 % mod;\n    }\n  }\n  cout << ans;\n}\nè½¬åŒ–æˆjavaä»£ç ', 99, 'user', '2023-10-10 12:42:51');
INSERT INTO `xz_dialogue` VALUES (489, 'è¿™æ˜¯ä¸€ä¸ªC++ä»£ç æ®µï¼Œå…¶ä¸­åŒ…å«ä¸€äº›ç”¨äºå¤„ç†æ•´æ•°è¾“å…¥å’Œè¾“å‡ºçš„å‡½æ•°ã€‚ä»¥ä¸‹æ˜¯è¿™æ®µä»£ç çš„Javaç‰ˆæœ¬ï¼š\n\n\n```java\nimport java.util.*;\n\npublic class Main {\n    public static void main(String[] args) {\n        Scanner scanner = new Scanner(System.in);\n        int n = scanner.nextInt();\n        int[] a = new int[n];\n        int[] b = new int[n];\n        for (int i = 0; i < n; i++) {\n            a[i] = scanner.nextInt();\n            b[i] = scanner.nextInt();\n        }\n        Map<Integer, Integer> f = new HashMap<>();\n        for (int i = 0; i < n; i++) {\n            f.put(a[i], b[i]);\n        }\n        \n        List<Integer> y = Arrays.stream(b).boxed().collect(Collectors.toList());\n        int ans = 1;\n        for (int xItem : y) {\n            boolean found = false;\n            for (Map.Entry<Integer, Integer> entry : f.entrySet()) {\n                if (entry.getKey() == xItem) {\n                    found = true;\n                    break;\n                }\n            }\n            if (!found) {\n                x[xItem] = 0;\n            } else {\n                ans = (ans * 2) % 998244353;\n            }\n        }\n        System.out.println(ans);\n    }\n}\n```\nè¿™æ®µJavaä»£ç çš„ä¸»è¦é€»è¾‘ä¸C++ä»£ç ç›¸åŒï¼Œå®ƒé¦–å…ˆè¯»å–è¾“å…¥æ•°æ®ï¼Œç„¶åå¯¹ä¸€ä¸ªæ•°ç»„`x`è¿›è¡Œå¤„ç†ï¼Œå¹¶è®¡ç®—ç­”æ¡ˆã€‚è¿™æ®µä»£ç ä¸­çš„å˜é‡å’Œå‡½æ•°åå¯èƒ½éœ€è¦æ ¹æ®ä½ çš„å®é™…éœ€æ±‚è¿›è¡Œä¿®æ”¹ã€‚è¿™æ®µä»£ç ä¹Ÿæ²¡æœ‰åŒ…å«è¾“å…¥é”™è¯¯æ£€æŸ¥å’Œå¯èƒ½çš„å¼‚å¸¸å¤„ç†ï¼Œè¿™äº›åœ¨çœŸå®çš„ç¨‹åºä¸­ä¹Ÿæ˜¯å¿…éœ€çš„ã€‚', 99, 'assistant', '2023-10-10 12:42:51');
