INSERT INTO `xz_dialogue` VALUES (594, 'import java.math.BigInteger;\nclass Solution {\n    public int[][] constructProductMatrix(int[][] grid) {\n        int n = grid.length;\n        int m = grid[0].length;\n        BigInteger multi = BigInteger.valueOf(1);\n        for (int i = 0; i < n; i++) {\n            for (int j = 0; j < m; j++) {\n                multi = multi.multiply(BigInteger.valueOf(grid[i][j]));\n            }\n        }\n        Map<Integer, Integer> map = new HashMap<>();\n        int[][] ans = new int[n][m];\n        for (int i = 0; i < n; i++) {\n            for (int j = 0; j < m; j++) {\n                if (map.containsKey(grid[i][j])) {\n                    ans[i][j] = map.get(grid[i][j]);\n                    continue;\n                }\n                ans[i][j] = multi.divide(BigInteger.valueOf(grid[i][j])).mod(BigInteger.valueOf(12345)).intValue();\n                map.put(grid[i][j], ans[i][j]);\n            }\n        }\n        return ans;\n    }\n}\næ€ä¹ˆä¼˜åŒ–', 112, 'user', '2023-10-15 11:42:27');
