INSERT INTO `xz_dialogue` VALUES (1426, 'import sys\n\nN = 5001\nf = [[False for _ in range(N)] for _ in range(N)]\ninput = sys.stdin.readline\nfor i in range(N):\n    for j in range(N):\n        if not f[i][j]:\n            k = 1\n            while k + i < N:\n                s = 0\n                while j + s * k < N:\n                    f[i + k][j + s * k] = True\n                    s += 1\n                k += 1\n\n            k = 1\n            while k + j < N:\n                s = 0\n                while i + s * k < N:\n                    f[i + s * k][j + k] = True\n                    s += 1\n                k += 1\n\nfor _ in range(int(input())):\n    n, m = list(map(int, input().split()))\n    if f[n][m]:\n        print(\"Alice\")\n    else:\n        print(\"Bob\")\nç¿»è¯‘æˆc++', 178, 'user', '2024-05-04 20:32:52');
INSERT INTO `xz_dialogue` VALUES (1427, 'å½“ç„¶å¯ä»¥ã€‚ä¸‹é¢æ˜¯å¯¹åº”çš„C++ä»£ç ã€‚è¿™ä¸ªä»£ç å®ç°äº†ä¸€ä¸ªç”¨äºå¤„ç†äºŒç»´ç½‘æ ¼çš„ç®—æ³•ï¼Œçœ‹èµ·æ¥åƒæ˜¯åœ¨é¢„å¤„ç†ä¸€ä¸ªå¯ä»¥ç”¨äºåˆ¤æ–­æŸä¸ªç‚¹æ˜¯å¦è¢«æŸç§æ¨¡å¼è¦†ç›–çš„ç½‘æ ¼ã€‚è¿™ä¸ªæ¨¡å¼çœ‹èµ·æ¥åƒæ˜¯ä»¥ç‰¹å®šçš„æ–œçº¿æ–¹å¼å¡«å……ç½‘æ ¼ã€‚ä¹‹åï¼Œå¯¹äºè¾“å…¥çš„æ¯ä¸€å¯¹åæ ‡ï¼Œå®ƒæ£€æŸ¥è¿™äº›åæ ‡åœ¨é¢„å¤„ç†çš„ç½‘æ ¼ä¸­çš„çŠ¶æ€ï¼Œå¹¶è¾“å‡º\"Alice\"æˆ–\"Bob\"ã€‚\n\n\n```cpp\n#include <iostream>\n#include <vector>\n#include <string>\n\nusing namespace std;\n\nint main() {\n    const int N = 5001;\n    vector<vector<bool>> f(N, vector<bool>(N, false));\n\n    for (int i = 0; i < N; ++i) {\n        for (int j = 0; j < N; ++j) {\n            if (!f[i][j]) {\n                int k = 1;\n                while (k + i < N) {\n                    int s = 0;\n                    while (j + s * k < N) {\n                        f[i + k][j + s * k] = true;\n                        s += 1;\n                    }\n                    k += 1;\n                }\n\n                k = 1;\n                while (k + j < N) {\n                    int s = 0;\n                    while (i + s * k < N) {\n                        f[i + s * k][j + k] = true;\n                        s += 1;\n                    }\n                    k += 1;\n                }\n            }\n        }\n    }\n\n    int t;\n    cin >> t;\n    while (t--) {\n        int n, m;\n        cin >> n >> m;\n        if (f[n][m]) {\n            cout << \"Alice\" << endl;\n        } else {\n            cout << \"Bob\" << endl;\n        }\n    }\n\n    return 0;\n}\n```\nè¯·æ³¨æ„ï¼Œç”±äºè¿™æ®µä»£ç æ¶‰åŠåˆ°å¤§é‡çš„æ•°ç»„è®¿é—®å’Œå¾ªç¯ï¼Œæ‰€ä»¥å®ƒå¯èƒ½éœ€è¦ä¸€äº›æ—¶é—´æ¥è¿è¡Œï¼Œç‰¹åˆ«æ˜¯åœ¨è¾ƒå¤§çš„è¾“å…¥ä¸Šã€‚ä½ å¯ä»¥å°è¯•ä¼˜åŒ–å®ƒä»¥æé«˜æ•ˆç‡ï¼Œä¾‹å¦‚é€šè¿‡æ”¹å˜æ•°æ®ç»“æ„æˆ–ç®—æ³•ã€‚ä½†æ˜¯ï¼Œå¯¹äºè¿™ä¸ªç‰¹å®šçš„ä»£ç ç‰‡æ®µï¼Œå®ƒåº”è¯¥èƒ½å¤Ÿåœ¨åˆç†çš„æ—¶é—´å†…å¤„ç†å¤§å°ä¸º5001çš„ç½‘æ ¼ã€‚', 178, 'assistant', '2024-05-04 20:32:52');
