INSERT INTO `xz_dialogue` VALUES (1463, 'ä¸ºäº†å®Œå–„`Date::AddDay(int days)`å‡½æ•°ï¼Œæˆ‘ä»¬éœ€è¦è€ƒè™‘å‡ ä¸ªå› ç´ ï¼ŒåŒ…æ‹¬æ¯æœˆçš„å¤©æ•°ä¸åŒã€é—°å¹´ä»¥åŠè·¨è¶Šå¤šä¸ªæœˆä»½æˆ–å¹´ä»½çš„æƒ…å†µã€‚ä¸‹é¢æ˜¯ä¸€ä¸ªç®€å•çš„å®ç°ï¼Œå®ƒé€æ­¥å¢åŠ å¤©æ•°ï¼ŒåŒæ—¶æ›´æ–°æ—¥ã€æœˆå’Œå¹´å±æ€§ã€‚\n\né¦–å…ˆï¼Œå‡è®¾ä½ çš„`Date`ç±»å¤§è‡´å®šä¹‰å¦‚ä¸‹ï¼š\n\n```cpp\nclass Date {\nprivate:\n    int day;\n    int month;\n    int year;\n    \n    // è¾…åŠ©å‡½æ•°ï¼Œç”¨äºåˆ¤æ–­æ˜¯å¦ä¸ºé—°å¹´\n    bool IsLeapYear(int y) const {\n        return (y % 4 == 0 && y % 100 != 0) || (y % 400 == 0);\n    }\n\n    // è¾…åŠ©å‡½æ•°ï¼Œè¿”å›ç»™å®šæœˆä»½çš„å¤©æ•°\n    int DaysInMonth(int m, int y) const {\n        switch (m) {\n            case 1: case 3: case 5: case 7: case 8: case 10: case 12:\n                return 31;\n            case 4: case 6: case 9: case 11:\n                return 30;\n            case 2:\n                return IsLeapYear(y) ? 29 : 28;\n            default:\n                return 0; // æ— æ•ˆæœˆä»½\n        }\n    }\n\npublic:\n    Date(int d, int m, int y) : day(d), month(m), year(y) {}\n\n    // ... å…¶ä»–æˆå‘˜å‡½æ•° ...\n\n    void AddDay(int days);\n    \n    // ... å¯èƒ½è¿˜æœ‰å…¶ä»–æˆå‘˜å‡½æ•°ï¼Œæ¯”å¦‚è·å–æ—¥æœŸçš„å‡½æ•°ç­‰ ...\n};\n```\n\nç°åœ¨ï¼Œæˆ‘ä»¬æ¥å®ç°`AddDay`å‡½æ•°ï¼š\n\n```cpp\nvoid Date::AddDay(int days) {\n    while (days > 0) { // å¯ä»¥å¤„ç†è´Ÿæ•°ï¼Œè¡¨ç¤ºå‘å‰å‡å°‘å¤©æ•°\n        int currentDaysInMonth = DaysInMonth(month, year);\n        \n        if (day + days <= currentDaysInMonth) {\n            // å¦‚æœå¢åŠ çš„å¤©æ•°ä¸ä¼šè¶…å‡ºå½“å‰æœˆçš„å¤©æ•°\n            day += days;\n            days = 0; // å®Œæˆå¢åŠ \n        } else {\n            // å¦‚æœå¢åŠ çš„å¤©æ•°ä¼šè¶…å‡ºå½“å‰æœˆçš„å¤©æ•°\n            days -= (currentDaysInMonth - day + 1);\n            day = 1; // è®¾ç½®ä¸ºä¸‹ä¸€ä¸ªæœˆçš„ç¬¬ä¸€å¤©\n            month++; // è¿›å…¥ä¸‹ä¸€ä¸ªæœˆ\n            \n            if (month > 12) {\n                // å¦‚æœæœˆä»½è¶…è¿‡12ï¼Œè¡¨ç¤ºè¿›å…¥äº†æ–°çš„ä¸€å¹´\n                month = 1; // é‡ç½®æœˆä»½ä¸º1\n                year++; // å¹´ä»½å¢åŠ \n            }\n        }\n    }\n\n    // å¦‚æœéœ€è¦å¤„ç†å‡å°‘å¤©æ•°ï¼ˆdays < 0ï¼‰ï¼Œå¯ä»¥æ·»åŠ ç›¸åº”çš„é€»è¾‘\n}\n```\n\nè¿™ä¸ª`AddDay`å‡½æ•°é€šè¿‡å¾ªç¯é€æ­¥å¢åŠ å¤©æ•°ï¼ŒåŒæ—¶æ£€æŸ¥æ˜¯å¦éœ€è¦è·¨è¶Šæœˆä»½æˆ–å¹´ä»½ã€‚æ³¨æ„ï¼Œè¿™ä¸ªå‡½æ•°ä¹Ÿå¯ä»¥å¤„ç†è´Ÿæ•°å‚æ•°ï¼Œå³å‘å‰å‡å°‘å¤©æ•°ï¼Œå°½ç®¡è¿™ä¸ªå…·ä½“å®ç°ä¸»è¦å…³æ³¨å¢åŠ å¤©æ•°çš„æƒ…å†µã€‚å¦‚æœä½ éœ€è¦å¤„ç†å‡å°‘å¤©æ•°çš„é€»è¾‘ï¼Œå¯ä»¥åœ¨`while`å¾ªç¯ä¸­æ·»åŠ ç›¸åº”çš„ä»£ç æ¥å¤„ç†`days < 0`çš„æƒ…å†µã€‚\n\nè¯·ç¡®ä¿åœ¨å®é™…ä½¿ç”¨å‰å¯¹ä½ çš„`Date`ç±»è¿›è¡Œå……åˆ†çš„æµ‹è¯•ï¼Œä»¥ç¡®ä¿å…¶æ­£ç¡®æ€§å’Œå¥å£®æ€§ã€‚', 186, 'assistant', '2024-05-11 11:16:33');
