INSERT INTO `xz_dialogue` VALUES (1486, '#include <iostream>\nusing namespace std;\n\nclass Complex {\n    double real, imag;\n\npublic:\n    Complex(double r = 0, double i = 0) : real(r), imag(i) {}\n\n    // é€šè¿‡åŠŸèƒ½å‡½æ•°é—´æ¥å¾—åˆ°æ•°æ®æˆå‘˜çš„å€¼ï¼ˆæ¥å£ï¼‰\n    double Real() {\n        return real;\n    }\n\n    double Imag() {\n        return imag;\n    }\n\n    Complex operator+(Complex c);\n    Complex operator+(double);\n    Complex operator=(Complex); // è°åœ¨+çš„å³è¾¹è°å°±æ˜¯å‚æ•°ï¼Œä¸¤ä¸ªå¤æ•°å¯¹è±¡ç›¸åŠ \n    void print() {\n        cout << real << \"+\" << imag << \"i\" << endl;\n    }\n};\n\nComplex Complex::operator+(Complex c) {\n    Complex temp; // å¤æ•°ç±»å¯¹è±¡\n    temp.real = this->real + c.real; // this è¡¨ç¤º+å·¦è¾¹å¯¹è±¡\n    temp.imag = this->imag + c.imag;\n\n    return temp;\n}\n\nint main() {\n    Complex c1(3, 4), c2(4, 9), c3;\n    int shi = c1.Real() + c2.Real();\n    int xu = c1.Imag() + c2.Imag();\n    c3 = c1 + c2;\n    c3.print();\n\n    cout << \"c1+c2 = \" << shi << \" +\" << xu << \"i\" << endl;\n    return 0;\n}\nå¼‚å¸¸è¿”å›1ï¼Œå“ªé‡Œå‡ºé”™äº†', 195, 'user', '2024-05-17 11:16:25');
