INSERT INTO `xz_dialogue` VALUES (1574, 'for _ in range(int(input())):\n    n, k = list(map(int, input().split()))\n    a = list(map(int, input().split()))\n    g = [[] for _ in range(n + 1)]\n    c = 0\n    for _ in range(n - 1):\n        f, t = list(map(int, input().split()))\n        g[f].append(t)\n        g[t].append(f)\n    v = 0\n    for i in range(n):\n        v ^= a[i]\n    if v == 0:\n        print(\"YES\")\n        continue\n    elif k == 2:\n        print(\"NO\")\n        continue\n\n\n    def dfs(i: int, fa: int) -> int:\n        global c\n        cur = a[i - 1]\n        if cur == v:\n            c += 1\n            cur = 0\n        for nx in g[i]:\n            if nx != fa:\n                cur ^= dfs(nx, i)\n                if cur == v:\n                    c += 1\n                    cur = 0\n        return cur\n\n\n    dfs(1, -1)\n    print(\"YES\" if c >= 3 else \"NO\")\næ”¹æˆC++ä»£ç ', 208, 'user', '2024-06-20 11:15:48');
INSERT INTO `xz_dialogue` VALUES (1575, 'ä¸‹é¢æ˜¯å°†æ‚¨ç»™å‡ºçš„Pythonä»£ç æ”¹å†™ä¸ºC++çš„ç‰ˆæœ¬ã€‚è¯·æ³¨æ„ï¼ŒC++ä»£ç çš„ç»“æ„å’Œè¯­æ³•ä¸Pythonæœ‰å¾ˆå¤§ä¸åŒï¼Œå› æ­¤æˆ‘è¿›è¡Œäº†å¿…è¦çš„è°ƒæ•´ä»¥ç¡®ä¿é€»è¾‘å’ŒåŠŸèƒ½çš„ä¸€è‡´æ€§ã€‚\n\n```cpp\n#include <iostream>\n#include <vector>\n#include <cstdlib>\nusing namespace std;\n\nvoid dfs(int i, int fa, const vector<vector<int>>& g, const vector<int>& a, int v, int& c, int& cur) {\n    cur = a[i];\n    if (cur == v) {\n        c++;\n        cur = 0;\n    }\n    for (int nx : g[i]) {\n        if (nx != fa) {\n            cur ^= dfs(nx, i, g, a, v, c, cur);\n            if (cur == v) {\n                c++;\n                cur = 0;\n            }\n        }\n    }\n}\n\nint main() {\n    int t, n, k, f, tt;\n    cin >> t;\n    while (t--) {\n        cin >> n >> k;\n        vector<int> a(n);\n        for (int i = 0; i < n; ++i) {\n            cin >> a[i];\n        }\n        vector<vector<int>> g(n + 1);\n        for (int i = 0; i < n - 1; ++i) {\n            cin >> f >> tt;\n            g[f].push_back(tt);\n            g[tt].push_back(f);\n        }\n\n        int v = 0;\n        for (int i = 0; i < n; ++i) {\n            v ^= a[i];\n        }\n        \n        int c = 0; // Number of times v is found as a subarray xor\n        int cur; // Current xor value during DFS\n        dfs(1, -1, g, a, v, c, cur);\n        \n        if (v == 0) {\n            cout << \"YES\" << endl;\n        } else if (k == 2) {\n            cout << \"NO\" << endl;\n        } else {\n            cout << (c >= 3 ? \"YES\" : \"NO\") << endl;\n        }\n    }\n    return 0;\n}\n```\n\nåœ¨è¿™æ®µC++ä»£ç ä¸­ï¼Œæˆ‘ä½¿ç”¨äº†`vector`æ¥å­˜å‚¨å›¾å’Œæ•°ç»„ï¼Œå› ä¸ºå®ƒä»¬åœ¨C++ä¸­æ˜¯åŠ¨æ€æ•°ç»„ï¼Œå¯ä»¥å¾ˆæ–¹ä¾¿åœ°è°ƒæ•´å¤§å°ã€‚æˆ‘è¿˜ä½¿ç”¨å¼•ç”¨ä¼ é€’æ¥ä¿®æ”¹`c`å’Œ`cur`çš„å€¼ï¼Œè¿™æ˜¯C++ä¸­å¸¸ç”¨çš„åšæ³•ï¼Œå› ä¸ºå®ƒå¯ä»¥é¿å…å¤åˆ¶å¤§å‹æ•°æ®ç»“æ„ï¼Œå¹¶å…è®¸å‡½æ•°ä¿®æ”¹å…¶è°ƒç”¨è€…ä¼ å…¥çš„æ•°æ®ã€‚\n\nè¯·æ³¨æ„ï¼Œæ‚¨éœ€è¦åœ¨ç¼–è¯‘ç¯å¢ƒä¸­åŒ…å«å¿…è¦çš„å¤´æ–‡ä»¶ï¼Œ